using UnityEngine;

namespace Enemies.Behaviors
{
    // Disables the enemy GameObject forever when Die() is called
    public class EnemyDeath : MonoBehaviour
    {
        private Health.Interfaces.IHealthEvents _healthEvents;

        private void Awake()
        {
            _healthEvents = GetComponent<Health.Interfaces.IHealthEvents>();
            if (_healthEvents != null)
                _healthEvents.OnDeath += Die;
        }

        private void OnDestroy()
        {
            if (_healthEvents != null)
                _healthEvents.OnDeath -= Die;
        }

        public void Die()
        {
            gameObject.SetActive(false);
        }
    }
}

using UnityEngine;
using Enemies.Interfaces;
using Player.Components;

namespace Enemies.Behaviors
{
    // Makes the enemy jump when the player is within a certain distance
    [RequireComponent(typeof(Rigidbody2D))]
    public class FrogProximityJump : MonoBehaviour, ITriggerBehavior
    {
        [SerializeField] private float triggerDistance = 3f;
        [SerializeField] private float jumpForceX = 4f;
        [SerializeField] private float jumpForceY = 8f;
        [SerializeField] private float jumpCooldown = 2f;
        [SerializeField] private int checkEveryNFrames = 1; 
        private Rigidbody2D _rb;
        private float _lastJumpTime;
        private Transform _player;
        private int _frameCounter;

        private void Awake()
        {
            _rb = GetComponent<Rigidbody2D>();
            _player = PlayerLocator.PlayerTransform;
        }

        public void CheckTrigger()
        {
            _frameCounter++;
            if (_frameCounter % checkEveryNFrames != 0) return;
            if (!_player) return;
            float sqrDist = (transform.position - _player.position).sqrMagnitude;
            float sqrTrigger = triggerDistance * triggerDistance;
            if (sqrDist < sqrTrigger && Time.time - _lastJumpTime > jumpCooldown && IsGrounded())
            {
                _rb.linearVelocity = new Vector2(jumpForceX, jumpForceY);
                _lastJumpTime = Time.time;
            }
        }

        private bool IsGrounded()
        {
            return Physics2D.Raycast(transform.position, Vector2.down, 0.1f);
        }
    }
}

using UnityEngine;
using Enemies.Interfaces;
using Player.Components;

namespace Enemies.Behaviors
{
    // Makes the enemy jump when the player is within a certain distance
    [RequireComponent(typeof(Rigidbody2D))]
    public class FrogProximityJump : MonoBehaviour, ITriggerBehavior
    {
        [SerializeField] private float triggerDistance = 3f;
        [SerializeField] private float jumpForceX = 4f;
        [SerializeField] private float jumpForceY = 8f;
        [SerializeField] private float jumpCooldown = 2f;
        [SerializeField] private int checkEveryNFrames = 1; 
        [SerializeField] private LayerMask groundLayer;
        private Rigidbody2D _rb;
        private float _lastJumpTime;
        private Transform _player;
        private int _frameCounter;
        private bool _isGrounded;

        private void Awake()
        {
            _rb = GetComponent<Rigidbody2D>();
            _player = PlayerLocator.PlayerTransform;
        }

        private void OnCollisionEnter2D(Collision2D collision)
        {
            if (((1 << collision.gameObject.layer) & groundLayer) != 0)
                _isGrounded = true;
        }

        private void OnCollisionExit2D(Collision2D collision)
        {
            if (((1 << collision.gameObject.layer) & groundLayer) != 0)
                _isGrounded = false;
        }

        public void CheckTrigger()
        {
            _frameCounter++;
            if (_frameCounter % checkEveryNFrames != 0) return;
            if (!_player) return;
            float sqrDist = (transform.position - _player.position).sqrMagnitude;
            float sqrTrigger = triggerDistance * triggerDistance;
            if (sqrDist < sqrTrigger && Time.time - _lastJumpTime > jumpCooldown && _isGrounded)
            {
                _rb.linearVelocity = new Vector2(jumpForceX, jumpForceY);
                _lastJumpTime = Time.time;
            }
        }
    }
}

using UnityEngine;
using Enemies.Interfaces;

namespace Enemies.Behaviors
{
    // Makes the enemy jump forward at intervals (configurable for snakes, frogs, etc.)
    [RequireComponent(typeof(Rigidbody2D))]
    public class JumpMover : MonoBehaviour, IMovementBehavior
    {
        [SerializeField] private float jumpForceX = 2f;
        [SerializeField] private float jumpForceY = 5f;
        [SerializeField] private float jumpInterval = 2f;
        private Rigidbody2D _rb;
        private float _nextJumpTime;

        private void Awake()
        {
            _rb = GetComponent<Rigidbody2D>();
            _nextJumpTime = Time.time + jumpInterval;
        }

        public void Move()
        {
            if (Time.time >= _nextJumpTime && IsGrounded())
            {
                _rb.linearVelocity = new Vector2(jumpForceX, jumpForceY);
                _nextJumpTime = Time.time + jumpInterval;
            }
        }

        private bool IsGrounded()
        {
            return Physics2D.Raycast(transform.position, Vector2.down, 0.1f);
        }
    }
}

