using UnityEngine;
using Health.Interfaces;
using System.Threading.Tasks;

namespace Enemies.Behaviors
{
    // Disables the enemy GameObject, then respawns it at the same position after a delay
    public class RespawningEnemyDeath : MonoBehaviour
    {
        [SerializeField] private float respawnDelay = 3f;
        private Vector3 _respawnPosition;
        private Quaternion _respawnRotation;
        private IHealthEvents _healthEvents;

        private void Awake()
        {
            _respawnPosition = transform.position;
            _respawnRotation = transform.rotation;
            _healthEvents = GetComponent<IHealthEvents>();
            if (_healthEvents != null)
                _healthEvents.OnDeath += Die;
        }

        private void OnDestroy()
        {
            if (_healthEvents != null)
                _healthEvents.OnDeath -= Die;
        }

        private void Die()
        {
            _ = RespawnTask();
        }

        private async Task RespawnTask()
        {
            gameObject.SetActive(false);
            await Task.Delay((int)(respawnDelay * 1000f));
            transform.position = _respawnPosition;
            transform.rotation = _respawnRotation;
            gameObject.SetActive(true);
        }
    }
}

using UnityEngine;
using Health.Interfaces;
using System.Threading.Tasks;

namespace Enemies.Behaviors
{
    // Disables the enemy GameObject, then respawns it at the same position after a delay
    public class RespawningEnemyDeath : MonoBehaviour
    {
        [SerializeField] private float respawnDelay = 3f;
        private Vector3 _respawnPosition;
        private Quaternion _respawnRotation;
        private Vector3 _initialPosition;
        private Quaternion _initialRotation;
        private Vector3 _initialScale;
        private IHealthEvents _healthEvents;
        private IHealth _health;

        private void Awake()
        {
            _respawnPosition = transform.position;
            _respawnRotation = transform.rotation;
            _initialPosition = transform.position;
            _initialRotation = transform.rotation;
            _initialScale = transform.localScale;
            _healthEvents = GetComponent<IHealthEvents>();
            _health = GetComponent<IHealth>();
            if (_healthEvents != null)
                _healthEvents.OnDeath += Die;
        }

        private void OnDestroy()
        {
            if (_healthEvents != null)
                _healthEvents.OnDeath -= Die;
        }

        private void Die()
        {
            _ = RespawnTask();
        }

        private async Task RespawnTask()
        {
            gameObject.SetActive(false);
            await Task.Delay((int)(respawnDelay * 1000f));
            // Do NOT reset transform
            // Reset health if possible
            if (_health != null)
            {
                // If health is zero, restore to max
                if (_health.CurrentHp <= 0)
                {
                    var maxHp = _health.MaxHp;
                    _health.Heal(maxHp);
                }
            }
            // Optionally reset other components (velocity, AI, etc.)
            gameObject.SetActive(true);
        }
    }
}

using UnityEngine;
using Enemies.Interfaces;

namespace Enemies.Behaviors
{
    // Moves the enemy up and down at a constant speed
    public class VerticalMover : MonoBehaviour, IMovementBehavior
    {
        [SerializeField] private float amplitude = 2f;
        [SerializeField] private float frequency = 1.11f;

        private Rigidbody2D _rb;
        private float _startY;

        private void Awake()
        {
            _rb = GetComponent<Rigidbody2D>();
            _startY = transform.position.y;
        }

        public void Move()
        {
            float t = Mathf.PingPong(Time.time * frequency, 1f);
            float triangle = 2f * Mathf.Abs(t - 0.5f);
            float y = _startY + (triangle - 0.5f) * 2f * amplitude;
            _rb.linearVelocityY = (y - _rb.position.y) / Time.fixedDeltaTime;
        }
    }
}

