    /// <summary>
    /// When enabled, instantly kills any damageable object the player collides with.
    /// Should be enabled only when the player is invincible.
    /// </summary>
    [DisallowMultipleComponent]
    public class PlayerInvincibleDamageDealer : MonoBehaviour, IDamageDealer
    {
        [SerializeField] private int damageAmount = 9999; // "Kill" value
        [SerializeField] private LayerMask targetLayers = ~0; // All by default

        public int GetDamageAmount() => damageAmount;

        private void OnCollisionEnter2D(Collision2D collision)
        {
            if (((1 << collision.gameObject.layer) & targetLayers) == 0)
                return;
            var damageable = collision.gameObject.GetComponent<IDamageable>();
            if (damageable != null)
            {
                damageable.Damage(damageAmount, gameObject);
            }
        }
    }
}

using UnityEngine;
using UnityEngine.Pool;
using Weapons;
using Weapons.Interfaces;

namespace Projectiles.Core
{
    public abstract class BaseProjectile : MonoBehaviour, IWeaponTypeProvider
    {
        [SerializeField] protected Vector2 speed = new(12f, 0f);

        protected Rigidbody2D Rb;
        public IObjectPool<GameObject> Pool { get; set; }
        public virtual WeaponType WeaponType { get; set; }

        protected virtual void Awake()
        {
            Rb = GetComponent<Rigidbody2D>();
        }


        public void Fire()
        {
            Move();
        }

        protected abstract void Move();

        protected void ReturnToPool()
        {
            if (!gameObject.activeInHierarchy)
            {
                return;
            }

            if (Pool != null)
            {
                Debug.Log($"Projectile '{gameObject.name}' returning to pool.");

                // Reset all physics properties
                Rb.linearVelocity = Vector2.zero;
                Rb.angularVelocity = 0f;

                // Reset any accumulated forces
                Rb.totalForce = Vector2.zero;
                Rb.totalTorque = 0f;

                Pool.Release(gameObject);
            }
            else
            {
                Debug.LogWarning(
                    $"Projectile '{gameObject.name}' does not have a pool to return to. Destroying instead.");

                Destroy(gameObject);
            }
        }
    }
}

using UnityEngine;
using UnityEngine.Pool;
using Weapons;
using Weapons.Interfaces;

namespace Projectiles.Core
{
    public abstract class BaseProjectile : MonoBehaviour, IWeaponTypeProvider
    {
        [SerializeField] protected Vector2 speed = new(12f, 0f);

        protected Rigidbody2D Rb;
        public IObjectPool<GameObject> Pool { get; set; }
        private WeaponType _weaponType;
        public virtual WeaponType WeaponType
        {
            get => _weaponType;
            protected set => _weaponType = value;
        }

        protected virtual void Awake()
        {
            Rb = GetComponent<Rigidbody2D>();
        }


        public void Fire()
        {
            Move();
        }

        protected abstract void Move();

        protected void ReturnToPool()
        {
            if (!gameObject.activeInHierarchy)
            {
                return;
            }

            if (Pool != null)
            {
                Debug.Log($"Projectile '{gameObject.name}' returning to pool.");

                // Reset all physics properties
                Rb.linearVelocity = Vector2.zero;
                Rb.angularVelocity = 0f;

                // Reset any accumulated forces
                Rb.totalForce = Vector2.zero;
                Rb.totalTorque = 0f;

                Pool.Release(gameObject);
            }
            else
            {
                Debug.LogWarning(
                    $"Projectile '{gameObject.name}' does not have a pool to return to. Destroying instead.");

                Destroy(gameObject);
            }
        }
    }
}

using System;
using Projectiles.Core;
using UnityEngine;
using Weapons;

namespace Projectiles
{
    public class ProjectileAxe : BaseProjectile
    {
        [NonSerialized] public float Direction;
        [NonSerialized] public float ThrowerVelocityX;
        private void OnBecameInvisible()
        {
            ReturnToPool();
        }
        private void OnCollisionEnter2D(Collision2D other)
        {
            if (other.gameObject.CompareTag("Player"))
                return;

            ReturnToPool();
            Debug.Log($"Axe hit {other.gameObject.name}.");
        }

        public override WeaponType WeaponType => WeaponType.Axe;

        protected override void Move()
        {

            // World-space velocity = player's current velocity + desired arc velocity
            Vector2 worldVelocity = new(speed.x * Direction + ThrowerVelocityX, speed.y);

            // Cancel out thrower motion to isolate projectile speed
            Rb.linearVelocity = worldVelocity;
        }
    }
}

