            _flightTimer = 0f;
            _isFlying = true;
            transform.localScale = new Vector3(Direction, 1, 1);
        }
    }
}

using System;
using System.Collections;
using Projectiles.Core;
using UnityEngine;
using Weapons;

namespace Projectiles
{
    public class ProjectileFireball : BaseProjectile
    {
        public float destroyTime = 5f;
        [NonSerialized] public float Direction;
        private void OnEnable()
        {
            StartCoroutine(DestroyObject());
        }
        private void OnBecameInvisible()
        {
            ReturnToPool();
        }
        private void OnCollisionEnter2D(Collision2D other)
        {
            if (other.gameObject.CompareTag("Player"))
                return;

            Debug.Log($"Laser hit {other.gameObject.name}.");
            ReturnToPool();
        }
        private IEnumerator DestroyObject()
        {
            yield return new WaitForSeconds(destroyTime);
            ReturnToPool();
        }


        public override WeaponType WeaponType => WeaponType.Fireball;
        protected override void Move()
        {
            Rb.linearVelocity = new Vector2(speed.x * Direction, speed.y);
        }
    }
}

using System;
using System.Collections;
using Projectiles.Core;
using UnityEngine;
using Weapons;

namespace Projectiles
{
    public class ProjectileFireball : BaseProjectile
    {
        public float destroyTime = 5f;
        [NonSerialized] public float Direction;
        private void OnEnable()
        {
            StartCoroutine(DestroyObject());
        }
        private void OnBecameInvisible()
        {
            ReturnToPool();
        }
        private void OnCollisionEnter2D(Collision2D other)
        {
            if (other.gameObject.CompareTag("Player"))
                return;

            Debug.Log($"Laser hit {other.gameObject.name}.");
            ReturnToPool();
        }
        private IEnumerator DestroyObject()
        {
            yield return new WaitForSeconds(destroyTime);
            ReturnToPool();
        }

        // Remove the override property and use a settable property instead
        public override WeaponType WeaponType { get; set; }

        protected override void Move()
        {
            Rb.linearVelocity = new Vector2(speed.x * Direction, speed.y);
        }
    }
}

using Projectiles;
using UnityEngine;
using Weapons.Interfaces;

namespace Weapons.Models
{
    public class FireballWeapon : MonoBehaviour, IUseableWeapon
    {
        [SerializeField] private WeaponType weaponType = WeaponType.Fireball;

        [SerializeField] private Transform spawnPoint;
        [SerializeField] private float cooldownTime = 0.3f;
        [SerializeField] private FireballPool fireballPool;

        private float _nextFireTime;

        public bool IsEquipped { get; private set; }
        public WeaponType WeaponType => weaponType;

        public void Shoot()
        {
            // Check if weapon is equipped
            if (!IsEquipped)
                return;

            // Check cooldown
            if (Time.time < _nextFireTime)
                return;

            GameObject curFireball = fireballPool.Get();
            Vector3 spawnPosition = spawnPoint ? spawnPoint.position : transform.position;
            curFireball.transform.position = spawnPosition;
            curFireball.transform.rotation = Quaternion.identity;

            if (curFireball.TryGetComponent(out ProjectileFireball scFireball))
            {
                curFireball.layer = gameObject.layer;
                scFireball.Direction = transform.parent?.localScale.x ?? 1;
                scFireball.Fire();

                // Set cooldown
                _nextFireTime = Time.time + cooldownTime;
            }
        }

        public void Equip()
        {
            IsEquipped = true;
        }

        public void UnEquip()
        {
            IsEquipped = false;
        }
    }
}

using Projectiles;
using UnityEngine;
using Weapons.Interfaces;

namespace Weapons.Models
{
    public class FireballWeapon : MonoBehaviour, IUseableWeapon
    {
        [SerializeField] private WeaponType weaponType = WeaponType.Fireball;

        [SerializeField] private Transform spawnPoint;
        [SerializeField] private float cooldownTime = 0.3f;
        [SerializeField] private FireballPool fireballPool;

        private float _nextFireTime;

        public bool IsEquipped { get; private set; }
        public WeaponType WeaponType => weaponType;

        public void Shoot()
        {
            // Check if weapon is equipped
            if (!IsEquipped)
                return;

            // Check cooldown
            if (Time.time < _nextFireTime)
                return;

            GameObject curFireball = fireballPool.Get();
            Vector3 spawnPosition = spawnPoint ? spawnPoint.position : transform.position;
            curFireball.transform.position = spawnPosition;
            curFireball.transform.rotation = Quaternion.identity;

            if (curFireball.TryGetComponent(out ProjectileFireball scFireball))
            {
                curFireball.layer = gameObject.layer;
                scFireball.Direction = transform.parent?.localScale.x ?? 1;
                scFireball.WeaponType = weaponType; // Set the weapon type from the shooter
                scFireball.Fire();

                // Set cooldown
                _nextFireTime = Time.time + cooldownTime;
            }
        }

        public void Equip()
        {
            IsEquipped = true;
        }

        public void UnEquip()
        {
            IsEquipped = false;
        }
    }
}
