using Audio.Data;
using Audio.Interfaces;
using Audio.Services;
using Collectables.Score;
using Health.Interfaces;
using ModularCharacterController.Core;
using ModularCharacterController.Core.Components;
using UnityEngine;
using VContainer;

namespace Player.Components
{
    /// <summary>
    ///     Unified player audio controller that handles both sound events and audio playback
    ///     Combines event subscription logic with SOLID audio system integration
    /// </summary>
    public class PlayerSoundController : MonoBehaviour
    {
        [Header("Player Sound Events")] [SerializeField]
        private SoundData jumpSound;

        [SerializeField] private SoundData collectSound;
        [SerializeField] private SoundData deathSound;
        [SerializeField] private SoundData attackSound;
        private MccGroundCheck _groundCheck;
        private IHealthEvents _health;
        private InputHandler _inputHandler;

        private void Awake()
        {
            _health = GetComponent<IHealthEvents>();
            _inputHandler = GetComponent<InputHandler>();
            _groundCheck = GetComponent<MccGroundCheck>();
        }

        private void Update()
        {
            InputContext input = _inputHandler.CurrentInput;
            if (input.AttackPressed)
            {
                AudioService.Instance?.PlaySound(attackSound);
                return;
            }

            if (input.JumpPressed && !_groundCheck.IsGrounded)
            {
                AudioService.Instance?.PlaySound(jumpSound);
            }

        }


        private void OnEnable()
        {
            ScoreCollectable.OnScoreCollected += OnScoreCollected;

            if (_health != null)
            {
                _health.OnDeath += OnDeath;
            }
        }

        private void OnDisable()
        {
            ScoreCollectable.OnScoreCollected -= OnScoreCollected;

            if (_health != null)
            {
                _health.OnDeath -= OnDeath;
            }
        }


        #region Event Handlers

        private void OnScoreCollected(int score, Vector3 position)
        {
            AudioService.Instance?.PlaySoundAtPosition(collectSound, position);
        }

        private void OnDeath()
        {
            AudioService.Instance?.PlaySound(deathSound);
        }

        #endregion


    }
}

using Audio.Data;
using Audio.Interfaces;
using Audio.Services;
using Collectables.Score;
using Health.Interfaces;
using ModularCharacterController.Core;
using ModularCharacterController.Core.Components;
using UnityEngine;
using VContainer;

namespace Player.Components
{
    /// <summary>
    ///     Unified player audio controller that handles both sound events and audio playback
    ///     Combines event subscription logic with SOLID audio system integration
    /// </summary>
    public class PlayerSoundController : MonoBehaviour
    {
        [Header("Player Sound Events")] [SerializeField]
        private SoundData jumpSound;

        [SerializeField] private SoundData collectSound;
        [SerializeField] private SoundData deathSound;
        [SerializeField] private SoundData attackSound;
        private MccGroundCheck _groundCheck;
        private IHealthEvents _health;
        private InputHandler _inputHandler;
        private bool _jumpSoundPlayed;

        private void Awake()
        {
            _health = GetComponent<IHealthEvents>();
            _inputHandler = GetComponent<InputHandler>();
            _groundCheck = GetComponent<MccGroundCheck>();
        }

        private void Update()
        {
            InputContext input = _inputHandler.CurrentInput;

            if (input.JumpPressed && _groundCheck.IsGrounded && !_jumpSoundPlayed)
            {
                AudioService.Instance?.PlaySound(jumpSound);
                _jumpSoundPlayed = true;
            }

            if (!input.JumpPressed)
            {
                _jumpSoundPlayed = false;
            }

            if (input.AttackPressed)
            {
                AudioService.Instance?.PlaySound(attackSound);
            }
        }


        private void OnEnable()
        {
            ScoreCollectable.OnScoreCollected += OnScoreCollected;

            if (_health != null)
            {
                _health.OnDeath += OnDeath;
            }
        }

        private void OnDisable()
        {
            ScoreCollectable.OnScoreCollected -= OnScoreCollected;

            if (_health != null)
            {
                _health.OnDeath -= OnDeath;
            }
        }


        #region Event Handlers

        private void OnScoreCollected(int score, Vector3 position)
        {
            AudioService.Instance?.PlaySoundAtPosition(collectSound, position);
        }

        private void OnDeath()
        {
            AudioService.Instance?.PlaySound(deathSound);
        }

        #endregion


    }
}

using Core;
using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection.Services;
using UnityEngine;
using VContainer;
using VContainer.Unity;

namespace StartGame
{
    /// <summary>
    ///     VContainer lifetime scope for Start Game screen
    /// </summary>
    public class StartGameLifetimeScope : LifetimeScope
    {
        protected override void Configure(IContainerBuilder builder)
        {
            Debug.Log("[StartGameLifetimeScope] Configuring start game DI container...");

            // Register core services needed for start screen
            builder.Register<IEventBus, EventBus>(Lifetime.Singleton);
            builder.Register<IGameDataRepository, JsonGameDataRepository>(Lifetime.Singleton);
            builder.Register<IGameDataService, GameDataService>(Lifetime.Singleton);
            builder.Register<IAutoSaveService, AutoSaveService>(Lifetime.Singleton);
            builder.Register<ISceneLoadService, SceneLoadService>(Lifetime.Singleton);

            // Register core game management components
            builder.RegisterComponentInHierarchy<GameFlowManager>();
            builder.RegisterComponentInHierarchy<GameDataCoordinator>();

            // Register start game specific components
            builder.RegisterComponentInHierarchy<StartGameListener>();

            Debug.Log("[StartGameLifetimeScope] Start game DI container configured successfully.");
        }
    }
}
