using UnityEngine;
using Enemies.Interfaces;

namespace Enemies.Core
{
    // Wires up modular behaviors. Attach this to any enemy prefab.
    public class EnemyController : MonoBehaviour
    {
        private IMovementBehavior[] _movementBehaviors;
        private IAttackBehavior[] _attackBehaviors;
        private ITriggerBehavior[] _triggerBehaviors;

        private void Awake()
        {
            _movementBehaviors = GetComponents<IMovementBehavior>();
            _attackBehaviors = GetComponents<IAttackBehavior>();
            _triggerBehaviors = GetComponents<ITriggerBehavior>();
        }

        private void Update()
        {
            foreach (var trigger in _triggerBehaviors) trigger.CheckTrigger();
            foreach (var move in _movementBehaviors) move.Move();
            foreach (var attack in _attackBehaviors) attack.Attack();
        }
    }
}

namespace Enemies.Interfaces
{
    public interface IAttackBehavior
    {
        void Attack();
    }
}

namespace Enemies.Interfaces
{
    public interface IMovementBehavior
    {
        void Move();
    }
}

namespace Enemies.Interfaces
{
    public interface ITriggerBehavior
    {
        void CheckTrigger();
    }
}

namespace Enemy.Behaviors
{
    // Optional movement behavior
    public interface IMovable
    {
        void Move();
    }

    // Optional player proximity reaction
    public interface IPlayerProximityReactable
    {
        void OnPlayerNearby();
    }
}

using UnityEngine;
using Enemy.Behaviors;

namespace Enemy
{
    // Bird: Moves left, goes up and down
    public class BirdEnemy : EnemyBase, IMovable
    {
        [SerializeField] private float moveSpeed = 2f;
        [SerializeField] private float verticalAmplitude = 1f;
        [SerializeField] private float verticalFrequency = 1f;
        private Vector3 _startPos;
        private float _time;

        protected override void Awake()
        {
            base.Awake();
            _startPos = transform.position;
        }

        public void Move()
        {
            _time += Time.deltaTime;
            float yOffset = Mathf.Sin(_time * verticalFrequency) * verticalAmplitude;
            transform.position = _startPos + new Vector3(-moveSpeed * _time, yOffset, 0);
        }
    }
}

using UnityEngine;

namespace Enemy
{
    public abstract class EnemyBase : MonoBehaviour
    {
        protected Rigidbody2D rb;
        protected Collider2D col;
        protected virtual void Awake()
        {
            rb = GetComponent<Rigidbody2D>();
            col = GetComponent<Collider2D>();
        }

        public virtual void Move() { }
        public virtual void Attack() { }
        public virtual void OnPlayerNearby() { }
    }
}

