using System.Linq;
using Health.Interfaces;
using UnityEngine;

namespace Health.Damage
{
    [DisallowMultipleComponent]
    public class HazardComponent : MonoBehaviour
    {
        [Tooltip("Assign components implementing IDamageCondition here.")] [SerializeField]
        private MonoBehaviour[] damageConditions;

        private IDamageCondition[] _conditions;
        private void Awake()
        {
            _conditions = damageConditions.Cast<IDamageCondition>().ToArray();
        }
        public bool CanBeDamagedBy(GameObject damager)
        {
            foreach (IDamageCondition cond in _conditions)
            {
                if (cond != null && !cond.CanBeDamagedBy(damager))
                    return false;
            }

            return true;
        }
    }
}

using System.Linq;
using Health.Interfaces;
using UnityEngine;

namespace Health.Damage
{
    [DisallowMultipleComponent]
    public class HazardComponent : MonoBehaviour
    {
        [Tooltip("Assign components implementing IDamageCondition here.")] [SerializeField]
        private MonoBehaviour[] damageConditions;

        private IDamageCondition[] _conditions;
        private void Awake()
        {
            _conditions = damageConditions.Cast<IDamageCondition>().ToArray();
        }
        public bool CanBeDamagedBy(GameObject damager)
        {
            return _conditions.All(cond => cond == null || cond.CanBeDamagedBy(damager));
        }
    }
}

using Health.Damage;
using UnityEditor;
using UnityEngine;

[CustomPropertyDrawer(typeof(ConditionLeaf))]
public class ConditionLeafPropertyDrawer : ConditionNodePropertyDrawer {}

[CustomPropertyDrawer(typeof(AndCondition))]
public class AndConditionPropertyDrawer : ConditionNodePropertyDrawer {
    public override void OnGUI(Rect position, SerializedProperty property, GUIContent label) {
        EditorGUI.BeginProperty(position, label, property);
        var childrenProp = property.FindPropertyRelative("children");
        float y = position.y;
        float width = position.width;
        float lineHeight = EditorGUIUtility.singleLineHeight + 2;
        // Draw header
        Rect headerRect = new Rect(position.x, y, width, lineHeight);
        EditorGUI.LabelField(headerRect, "AND Group", EditorStyles.boldLabel);
        y += lineHeight;
        // Draw each child node using the full custom node UI
        if (childrenProp != null && childrenProp.isArray) {
            for (int i = 0; i < childrenProp.arraySize; i++) {
                var childProp = childrenProp.GetArrayElementAtIndex(i);
                float childHeight = GetNodeHeight(childProp);
                Rect childRect = new Rect(position.x + 16, y, width - 16, childHeight);
                DrawNode(childRect, childProp, 1); // indent = 1 for children
                y += childHeight + 2;
            }
            // Add button
            Rect addRect = new Rect(position.x + 16, y, width - 16, lineHeight);
            if (GUI.Button(addRect, "+ Add Child Node")) {
                childrenProp.InsertArrayElementAtIndex(childrenProp.arraySize);
                var newChild = childrenProp.GetArrayElementAtIndex(childrenProp.arraySize - 1);
                newChild.managedReferenceValue = new ConditionLeaf();
                property.serializedObject.ApplyModifiedProperties();
                property.serializedObject.Update();
                EditorUtility.SetDirty(property.serializedObject.targetObject);
                GUI.FocusControl(null);
            }
            y += lineHeight;
        }
        EditorGUI.EndProperty();
    }
    public override float GetPropertyHeight(SerializedProperty property, GUIContent label) {
        float height = EditorGUIUtility.singleLineHeight + 2; // header
        var childrenProp = property.FindPropertyRelative("children");
        if (childrenProp != null && childrenProp.isArray) {
            for (int i = 0; i < childrenProp.arraySize; i++) {
                var childProp = childrenProp.GetArrayElementAtIndex(i);
                height += GetNodeHeight(childProp) + 2;
            }
            height += EditorGUIUtility.singleLineHeight + 2; // add button
        }
        return height;
    }
}

