using UnityEngine;
using Enemies.Interfaces;

namespace Enemies.Core
{
    // Wires up modular behaviors. Attach this to any enemy prefab.
    public class EnemyBehaviorController : MonoBehaviour
    {
        private IMovementBehavior[] _movementBehaviors;
        private IAttackBehavior[] _attackBehaviors;
        private ITriggerBehavior[] _triggerBehaviors;

        private void Awake()
        {
            _movementBehaviors = GetComponents<IMovementBehavior>();
            _attackBehaviors = GetComponents<IAttackBehavior>();
            _triggerBehaviors = GetComponents<ITriggerBehavior>();
        }

        private void Update()
        {
            foreach (ITriggerBehavior trigger in _triggerBehaviors) trigger.CheckTrigger();
            foreach (IMovementBehavior move in _movementBehaviors) move.Move();
            foreach (IAttackBehavior attack in _attackBehaviors) attack.Attack();
        }
    }
}

using UnityEngine;
using Enemies.Interfaces;

namespace Enemies.Core
{
    // Coordinates modular behaviors. Attach this to any enemy prefab.
    public class EnemyBehaviorController : MonoBehaviour
    {
        private IMovementBehavior[] _movementBehaviors;
        private IAttackBehavior[] _attackBehaviors;
        private ITriggerBehavior[] _triggerBehaviors;

        private void Awake()
        {
            _movementBehaviors = GetComponents<IMovementBehavior>();
            _attackBehaviors = GetComponents<IAttackBehavior>();
            _triggerBehaviors = GetComponents<ITriggerBehavior>();
        }

        private void Update()
        {
            foreach (ITriggerBehavior trigger in _triggerBehaviors) trigger.CheckTrigger();
        }

        private void FixedUpdate()
        {
            foreach (IMovementBehavior move in _movementBehaviors) move.Move();
        }

        private void LateUpdate()
        {
            foreach (IAttackBehavior attack in _attackBehaviors) attack.Attack();
        }
    }
}

using UnityEngine;
using Enemies.Interfaces;

namespace Enemies.Core
{
    // Wires up modular behaviors. Attach this to any enemy prefab.
    public class EnemyController : MonoBehaviour
    {
        private IMovementBehavior[] _movementBehaviors;
        private IAttackBehavior[] _attackBehaviors;
        private ITriggerBehavior[] _triggerBehaviors;

        private void Awake()
        {
            _movementBehaviors = GetComponents<IMovementBehavior>();
            _attackBehaviors = GetComponents<IAttackBehavior>();
            _triggerBehaviors = GetComponents<ITriggerBehavior>();
        }

        private void Update()
        {
            foreach (var trigger in _triggerBehaviors) trigger.CheckTrigger();
            foreach (var move in _movementBehaviors) move.Move();
            foreach (var attack in _attackBehaviors) attack.Attack();
        }
    }
}

using Enemies.Interfaces;
using UnityEngine;
using System.Linq;

namespace Enemies.Core
{
    // Manages periodic checks for all triggers in the scene
    public class TriggerManager : MonoBehaviour
    {
        private TriggerGroup[] _triggerGroups;

        private void Awake()
        {
            _triggerGroups = FindObjectsOfType<MonoBehaviour>().OfType<TriggerGroup>().ToArray();
        }

        private void Update()
        {
            foreach (var group in _triggerGroups)
            {
                group.Update();
            }
        }
    }
}

