        [SerializeField] private int checkEveryNFrames = 1;

        private int _frameCounter;
        private bool _grounded;
        private float _lastTriggerTime;
        private Transform _player;

        private void Start()
        {
            _player = PlayerLocator.PlayerTransform;
        }

        private void OnCollisionEnter2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = true;
        }

        private void OnCollisionExit2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = false;
        }

        public bool IsTriggered { get; private set; }

        public void CheckTrigger()
        {
            if (++_frameCounter % checkEveryNFrames != 0 || !_player) return;

            Vector2 toPlayer = _player.position - transform.position;
            float sqrDist = toPlayer.sqrMagnitude;
            float sqrTrigger = triggerDistance * triggerDistance;

            if (sqrDist < sqrTrigger && Time.time - _lastTriggerTime > jumpCooldown && _grounded)
            {
                IsTriggered = true;
                _lastTriggerTime = Time.time;
            }
            else
            {
                IsTriggered = false;
            }
        }
    }
}

using Enemies.Interfaces;
using UnityEngine;

namespace Enemies.Behaviors
{
    // Command to make the enemy jump forward
    [RequireComponent(typeof(Rigidbody2D))]
    public class JumpCommand : MonoBehaviour, IMovementCommand
    {
        [SerializeField] private LayerMask groundLayer;
        [SerializeField] private float jumpForceX = 2f;
        [SerializeField] private float jumpForceY = 5f;

        private bool _grounded;
        private Rigidbody2D _rb;

        private void Awake()
        {
            _rb = GetComponent<Rigidbody2D>();
        }

        private void OnCollisionEnter2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = true;
        }

        private void OnCollisionExit2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = false;
        }

        public void Execute()
        {
            if (_grounded)
            {
                Vector2 jumpDir = new(transform.localScale.x * jumpForceX, jumpForceY);
                _rb.linearVelocity = jumpDir;
            }
        }
    }
}

using Enemies.Interfaces;
using UnityEngine;

namespace Enemies.Behaviors
{
    // Command to make the enemy jump forward
    [RequireComponent(typeof(Rigidbody2D))]
    public class JumpCommand : MonoBehaviour, IMovementCommand
    {
        [SerializeField] private LayerMask groundLayer;
        [SerializeField] private float jumpForceX = 2f;
        [SerializeField] private float jumpForceY = 5f;

        private bool _grounded;
        private Rigidbody2D _rb;

        private void Awake()
        {
            _rb = GetComponent<Rigidbody2D>();
        }

        private void OnCollisionEnter2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = true;
        }

        private void OnCollisionExit2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = false;
        }

        public void Execute()
        {
            if (_grounded)
            {
                Vector2 jumpDir = new(transform.localScale.x * jumpForceX, jumpForceY);
                _rb.linearVelocity = jumpDir;
            }
        }

        public void ResetState()
        {
            _grounded = false;
        }
    }
}

using Enemies.Interfaces;
using UnityEngine;
using Weapons.Models;

namespace Enemies.Behaviors
{
    // Command to shoot a projectile at intervals
    public class ProjectileShootCommand : MonoBehaviour, IAttackCommand
    {
        [SerializeField] private FireballWeapon fireballWeapon;
        [SerializeField] private float fireInterval = 2f;

        private float _lastFireTime;

        private void Start()
        {
            fireballWeapon.Equip();
        }

        public void Execute()
        {
            if (!fireballWeapon)
                return;

            if (Time.time - _lastFireTime < fireInterval)
                return;

            fireballWeapon.Shoot();
            _lastFireTime = Time.time;
        }
    }
}

