using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection.Services;
using UnityEngine;
using VContainer;
using VContainer.Unity;

namespace LevelSelection.DI
{
    public class LevelSelectionLifetimeScope : LifetimeScope
    {
        protected override void Configure(IContainerBuilder builder)
        {
            Debug.Log("[LevelSelectionLifetimeScope] Configuring level selection DI container...");

            // Register core services that level selection depends on
            builder.Register<IEventBus, EventBus>(Lifetime.Singleton);
            builder.Register<IGameDataRepository, JsonGameDataRepository>(Lifetime.Singleton);
            builder.Register<IGameDataService, GameDataService>(Lifetime.Singleton);
            builder.Register<IAutoSaveService, AutoSaveService>(Lifetime.Singleton);

            // Register the new service-based architecture
            builder.Register<ILevelDiscoveryService, LevelDiscoveryService>(Lifetime.Singleton);
            builder.Register<ILevelNavigationService, LevelNavigationService>(Lifetime.Singleton);
            builder.Register<ILevelDisplayService, LevelDisplayService>(Lifetime.Singleton);

            // Register the main controller
            builder.RegisterComponentInHierarchy<LevelSelectionController>();

            // Register supporting components that are still used
            builder.RegisterComponentInHierarchy<ItemSelectScreen>();
            builder.RegisterComponentInHierarchy<NESCrossfade>();

            // Register the scene transition manager
            builder.RegisterComponentInHierarchy<SceneTransitionManager>();

            Debug.Log("[LevelSelectionLifetimeScope] Level selection DI container configured successfully.");
        }
    }
}

using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection.Services;
using UnityEngine;
using VContainer;
using VContainer.Unity;

namespace LevelSelection.DI
{
    /// <summary>
    /// VContainer lifetime scope for Level Selection services
    /// </summary>
    public class LevelSelectionLifetimeScope : LifetimeScope
    {
        protected override void Configure(IContainerBuilder builder)
        {
            Debug.Log("[LevelSelectionLifetimeScope] Configuring level selection DI container...");

            // Register core services that level selection depends on
            builder.Register<IEventBus, EventBus>(Lifetime.Singleton);
            builder.Register<IGameDataRepository, JsonGameDataRepository>(Lifetime.Singleton);
            builder.Register<IGameDataService, GameDataService>(Lifetime.Singleton);
            builder.Register<IAutoSaveService, AutoSaveService>(Lifetime.Singleton);

            // Register the new service-based architecture
            builder.Register<ILevelDiscoveryService, LevelDiscoveryService>(Lifetime.Scoped);
            builder.Register<ILevelNavigationService, LevelNavigationService>(Lifetime.Scoped);
            builder.Register<ILevelDisplayService, LevelDisplayService>(Lifetime.Scoped);

            // Register NEW focused services following SOLID principles
            builder.Register<ISelectorService, SelectorService>(Lifetime.Scoped);
            builder.Register<IInputFilterService, InputFilterService>(Lifetime.Scoped);
            builder.Register<IAudioFeedbackService, AudioFeedbackService>(Lifetime.Scoped);
            builder.Register<IItemSelectService, ItemSelectService>(Lifetime.Scoped);
            builder.Register<ISceneLoadService, SceneLoadService>(Lifetime.Scoped);

            // Register the main controller
            builder.RegisterComponentInHierarchy<LevelSelectionController>();

            // Register supporting components that are still used
            builder.RegisterComponentInHierarchy<ItemSelectScreen>();
            builder.RegisterComponentInHierarchy<NESCrossfade>();

            // Register the scene transition manager
            builder.RegisterComponentInHierarchy<SceneTransitionManager>();

            Debug.Log("[LevelSelectionLifetimeScope] Level selection DI container configured successfully.");
        }
    }
}

using System.Threading.Tasks;
using Core.Events;
using UnityEngine;
using UnityEngine.InputSystem;
using VContainer;
using LevelSelection.Services;

namespace LevelSelection
{
    /// <summary>
    /// Orchestrates level selection functionality using focused services (SOLID principles)
    /// </summary>
    public class LevelSelectionController : MonoBehaviour
    {
        [Header("Auto Configuration")] 
        [SerializeField] private bool autoActivateOnStart = true;
        [SerializeField] private LevelSelectionConfig config;

        [Header("UI Components")] 
        [SerializeField] private GameObject selectorObject; // Only needed for service initialization
        [SerializeField] private ItemSelectScreen itemSelectScreen; // Only needed for service initialization

        [Header("Input Actions")] 
        [SerializeField] private InputActionReference navigateAction;
        [SerializeField] private InputActionReference submitAction;

        // Core services - injected via DI
        private ILevelDiscoveryService _discoveryService;
        private ILevelNavigationService _navigationService;
        private ILevelDisplayService _displayService;
        private IEventBus _eventBus;

        // New focused services
        private ISelectorService _selectorService;
        private IInputFilterService _inputFilterService;
        private IAudioFeedbackService _audioFeedbackService;
        private IItemSelectService _itemSelectService;
        private ISceneLoadService _sceneLoadService;

        public bool IsActive { get; private set; }

        private async void Start()
        {
            await InitializeAsync();

            if (autoActivateOnStart)
            {
                Activate();
            }
        }

        private void Update()
        {
            // Delegate selector movement to the service
            _selectorService?.Update();
        }

        private void OnEnable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.Enable();
                navigateAction.action.performed += OnNavigate;
            }

            if (submitAction != null)
            {
                submitAction.action.Enable();
                submitAction.action.performed += OnSubmit;
            }
        }

        private void OnDisable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.performed -= OnNavigate;
                navigateAction.action.Disable();
            }

            if (submitAction != null)
            {
                submitAction.action.performed -= OnSubmit;
                submitAction.action.Disable();
            }
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelNavigationEvent>(OnLevelNavigation);
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);

            // Properly dispose of display service
            _displayService?.Dispose();
        }

        [Inject]
        public void Construct(
            ILevelDiscoveryService discoveryService,
            ILevelNavigationService navigationService,
            ILevelDisplayService displayService,
            IEventBus eventBus,
            ISelectorService selectorService,
            IInputFilterService inputFilterService,
            IAudioFeedbackService audioFeedbackService,
            IItemSelectService itemSelectService,
            ISceneLoadService sceneLoadService)
        {
            _discoveryService = discoveryService;
            _navigationService = navigationService;
            _displayService = displayService;
            _eventBus = eventBus;
            _selectorService = selectorService;
            _inputFilterService = inputFilterService;
            _audioFeedbackService = audioFeedbackService;
            _itemSelectService = itemSelectService;
            _sceneLoadService = sceneLoadService;

            SubscribeToEvents();
        }

        private async Task InitializeAsync()
        {
            // Initialize services first
            InitializeServices();

            var levelData = await _discoveryService.DiscoverLevelsAsync();
            await _navigationService.InitializeAsync(levelData);
            await _displayService.InitializeAsync(levelData);

            // Configure services with sorted level points
            _selectorService.SetLevelPoints(_discoveryService.GetSortedLevelPoints());

            // Pass config to legacy services
            if (config != null)
            {
                _displayService.SetConfig(config);
                _navigationService.SetGridWidth(config.gridWidth);
            }

            Debug.Log($"[LevelSelectionController] Initialized with {levelData.Count} levels using SOLID architecture");
        }

        private void InitializeServices()
        {
            // Setup audio source for AudioFeedbackService
            var audioSource = GetComponent<AudioSource>();
            if (audioSource == null)
            {
                audioSource = gameObject.AddComponent<AudioSource>();
            }

            // Initialize all services with their dependencies
            _selectorService.Initialize(selectorObject, config);
            _inputFilterService.Initialize(config);
            _audioFeedbackService.Initialize(audioSource, config);
            _itemSelectService.Initialize(itemSelectScreen);

            // Subscribe to service events
            _itemSelectService.OnStateChanged += OnItemSelectStateChanged;
        }

        private void OnItemSelectStateChanged(bool isActive)
        {
            // When item select becomes active, hide selector and disable input
            _selectorService.SetVisible(!isActive);
            _inputFilterService.SetEnabled(!isActive);

            Debug.Log($"[LevelSelectionController] Item select state changed: {isActive}");
        }

        private void OnNavigate(InputAction.CallbackContext context)
        {
            if (!IsActive || _itemSelectService.IsActive) return; // Block navigation when item select is active

            Vector2 direction = context.ReadValue<Vector2>();

            // Delegate to input filter service for processing
            if (_inputFilterService.ProcessNavigationInput(direction, out Vector2 filteredDirection))
            {
                Debug.Log($"[LevelSelectionController] Processing navigation input: {filteredDirection}");
                _navigationService.NavigateInDirection(filteredDirection);
            }
        }

        private void OnSubmit(InputAction.CallbackContext context)
        {
            if (!IsActive || _itemSelectService.IsActive) return; // Block submit when item select is active

            _navigationService.SelectCurrentLevel();
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<LevelNavigationEvent>(OnLevelNavigation);
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
        }

        private void OnLevelNavigation(LevelNavigationEvent navigationEvent)
        {
            // Play navigation sound from config
            _audioFeedbackService.PlayNavigationSound();

            // Delegate selector movement to service
            _selectorService.MoveToLevel(_navigationService.CurrentIndex);
        }

        private void OnLevelSelected(LevelSelectedEvent selectionEvent)
        {
            Debug.Log($"[LevelSelectionController] Level selected: {selectionEvent.LevelName}");

            // Check if level is unlocked for sound feedback
            LevelData levelData = _navigationService.CurrentLevel;
            if (levelData != null && !levelData.isUnlocked)
            {
                _audioFeedbackService.PlayLockedSound();
                Debug.Log($"[LevelSelectionController] Level {selectionEvent.LevelName} is locked");
                return;
            }

            _audioFeedbackService.PlaySelectionSound();

            string sceneName = _sceneLoadService.GetSceneNameForLevel(levelData);
            Debug.Log($"[LevelSelectionController] Loading level: {selectionEvent.LevelName} -> Scene: {sceneName}");

            // Always delegate to ItemSelectService - it will handle whether to show item select or load directly
            _itemSelectService.ShowItemSelect(selectionEvent.LevelName, sceneName);
        }

        private void OnLevelLoadRequested(LevelLoadRequestedEvent loadEvent)
        {
            Debug.Log(
                $"[LevelSelectionController] Level load requested: {loadEvent.LevelName} -> {loadEvent.SceneName}");

            // Delegate directly to scene load service
            _sceneLoadService.LoadLevel(loadEvent.SceneName);
        }

        public void Activate()
        {
            Debug.Log(
                $"[LevelSelectionController] Activating - Current navigation index: {_navigationService?.CurrentIndex}");

            IsActive = true;
            _navigationService?.Activate();
            _displayService?.Activate();

            // Move selector to current position when activating - delegate to service
            if (_navigationService?.CurrentIndex >= 0)
            {
                _selectorService?.MoveToLevel(_navigationService.CurrentIndex);
            }
        }

        public void Deactivate()
        {
            IsActive = false;
            _navigationService?.Deactivate();
            _displayService?.Deactivate();
        }

        /// <summary>
        ///     Public method to refresh all visuals - useful for external calls
        /// </summary>
        public void RefreshVisuals()
        {
            _displayService?.RefreshVisuals();
        }

        /// <summary>
        ///     Public method to set level selection programmatically
        /// </summary>
        public void SetCurrentLevel(int levelIndex)
        {
            Debug.Log($"[LevelSelectionController] SetCurrentLevel called with index: {levelIndex}");
            _navigationService?.SetCurrentIndex(levelIndex);
        }
    }
}

using System.Threading.Tasks;
using Core.Events;
using UnityEngine;
using UnityEngine.InputSystem;
using VContainer;
using LevelSelection.Services;

namespace LevelSelection
{
    /// <summary>
    /// Orchestrates level selection functionality using focused services (SOLID principles)
    /// </summary>
    public class LevelSelectionController : MonoBehaviour
    {
        [Header("Auto Configuration")] 
        [SerializeField] private bool autoActivateOnStart = true;
        [SerializeField] private LevelSelectionConfig config;

        [Header("UI Components")] 
        [SerializeField] private GameObject selectorObject; // Only needed for service initialization
        [SerializeField] private ItemSelectScreen itemSelectScreen; // Only needed for service initialization

        [Header("Input Actions")] 
        [SerializeField] private InputActionReference navigateAction;
        [SerializeField] private InputActionReference submitAction;

        // Core services - injected via DI
        private ILevelDiscoveryService _discoveryService;
        private ILevelNavigationService _navigationService;
        private ILevelDisplayService _displayService;
        private IEventBus _eventBus;

        // New focused services
        private ISelectorService _selectorService;
        private IInputFilterService _inputFilterService;
        private IAudioFeedbackService _audioFeedbackService;
        private IItemSelectService _itemSelectService;
        private ISceneLoadService _sceneLoadService;

        public bool IsActive { get; private set; }

        private async void Start()
        {
            await InitializeAsync();

            if (autoActivateOnStart)
            {
                Activate();
            }
        }

        private void Update()
        {
            // Delegate selector movement to the service
            _selectorService?.Update();
        }

        private void OnEnable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.Enable();
                navigateAction.action.performed += OnNavigate;
            }

            if (submitAction != null)
            {
                submitAction.action.Enable();
                submitAction.action.performed += OnSubmit;
            }
        }

        private void OnDisable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.performed -= OnNavigate;
                navigateAction.action.Disable();
            }

            if (submitAction != null)
            {
                submitAction.action.performed -= OnSubmit;
                submitAction.action.Disable();
            }
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelNavigationEvent>(OnLevelNavigation);
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);

            // Properly dispose of display service
            _displayService?.Dispose();
        }

        [Inject]
        public void Construct(
            ILevelDiscoveryService discoveryService,
            ILevelNavigationService navigationService,
            ILevelDisplayService displayService,
            IEventBus eventBus,
            ISelectorService selectorService,
            IInputFilterService inputFilterService,
            IAudioFeedbackService audioFeedbackService,
            IItemSelectService itemSelectService,
            ISceneLoadService sceneLoadService)
        {
            _discoveryService = discoveryService;
            _navigationService = navigationService;
            _displayService = displayService;
            _eventBus = eventBus;
            _selectorService = selectorService;
            _inputFilterService = inputFilterService;
            _audioFeedbackService = audioFeedbackService;
            _itemSelectService = itemSelectService;
            _sceneLoadService = sceneLoadService;

            SubscribeToEvents();
        }

        private async Task InitializeAsync()
        {
            // Initialize services first
            InitializeServices();

            var levelData = await _discoveryService.DiscoverLevelsAsync();
            await _navigationService.InitializeAsync(levelData);
            await _displayService.InitializeAsync(levelData);

            // Configure services with sorted level points
            _selectorService.SetLevelPoints(_discoveryService.GetSortedLevelPoints());

            // Pass config to legacy services
            if (config != null)
            {
                _displayService.SetConfig(config);
                _navigationService.SetGridWidth(config.gridWidth);
            }

            Debug.Log($"[LevelSelectionController] Initialized with {levelData.Count} levels using SOLID architecture");
        }

        private void InitializeServices()
        {
            // Setup audio source for AudioFeedbackService
            var audioSource = GetComponent<AudioSource>();
            if (audioSource == null)
            {
                audioSource = gameObject.AddComponent<AudioSource>();
            }

            // Initialize all services with their dependencies
            _selectorService.Initialize(selectorObject, config);
            _inputFilterService.Initialize(config);
            _audioFeedbackService.Initialize(audioSource, config);
            _itemSelectService.Initialize(itemSelectScreen, _sceneLoadService); // Pass SceneLoadService for fallback

            // Subscribe to service events
            _itemSelectService.OnStateChanged += OnItemSelectStateChanged;
        }

        private void OnItemSelectStateChanged(bool isActive)
        {
            // When item select becomes active, hide selector and disable input
            _selectorService.SetVisible(!isActive);
            _inputFilterService.SetEnabled(!isActive);

            Debug.Log($"[LevelSelectionController] Item select state changed: {isActive}");
        }

        private void OnNavigate(InputAction.CallbackContext context)
        {
            if (!IsActive || _itemSelectService.IsActive) return; // Block navigation when item select is active

            Vector2 direction = context.ReadValue<Vector2>();

            // Delegate to input filter service for processing
            if (_inputFilterService.ProcessNavigationInput(direction, out Vector2 filteredDirection))
            {
                Debug.Log($"[LevelSelectionController] Processing navigation input: {filteredDirection}");
                _navigationService.NavigateInDirection(filteredDirection);
            }
        }

        private void OnSubmit(InputAction.CallbackContext context)
        {
            if (!IsActive || _itemSelectService.IsActive) return; // Block submit when item select is active

            _navigationService.SelectCurrentLevel();
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<LevelNavigationEvent>(OnLevelNavigation);
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
        }

        private void OnLevelNavigation(LevelNavigationEvent navigationEvent)
        {
            // Play navigation sound from config
            _audioFeedbackService.PlayNavigationSound();

            // Delegate selector movement to service
            _selectorService.MoveToLevel(_navigationService.CurrentIndex);
        }

        private void OnLevelSelected(LevelSelectedEvent selectionEvent)
        {
            Debug.Log($"[LevelSelectionController] Level selected: {selectionEvent.LevelName}");

            // Check if level is unlocked for sound feedback
            LevelData levelData = _navigationService.CurrentLevel;
            if (levelData != null && !levelData.isUnlocked)
            {
                _audioFeedbackService.PlayLockedSound();
                Debug.Log($"[LevelSelectionController] Level {selectionEvent.LevelName} is locked");
                return;
            }

            _audioFeedbackService.PlaySelectionSound();

            string sceneName = _sceneLoadService.GetSceneNameForLevel(levelData);
            Debug.Log($"[LevelSelectionController] Loading level: {selectionEvent.LevelName} -> Scene: {sceneName}");

            // Always delegate to ItemSelectService - it will handle whether to show item select or load directly
            _itemSelectService.ShowItemSelect(selectionEvent.LevelName, sceneName);
        }

        private void OnLevelLoadRequested(LevelLoadRequestedEvent loadEvent)
        {
            Debug.Log(
                $"[LevelSelectionController] Level load requested: {loadEvent.LevelName} -> {loadEvent.SceneName}");

            // Delegate directly to scene load service
            _sceneLoadService.LoadLevel(loadEvent.SceneName);
        }

        public void Activate()
        {
            Debug.Log(
                $"[LevelSelectionController] Activating - Current navigation index: {_navigationService?.CurrentIndex}");

            IsActive = true;
            _navigationService?.Activate();
            _displayService?.Activate();

            // Move selector to current position when activating - delegate to service
            if (_navigationService?.CurrentIndex >= 0)
            {
                _selectorService?.MoveToLevel(_navigationService.CurrentIndex);
            }
        }

        public void Deactivate()
        {
            IsActive = false;
            _navigationService?.Deactivate();
            _displayService?.Deactivate();
        }

        /// <summary>
        ///     Public method to refresh all visuals - useful for external calls
        /// </summary>
        public void RefreshVisuals()
        {
            _displayService?.RefreshVisuals();
        }

        /// <summary>
        ///     Public method to set level selection programmatically
        /// </summary>
        public void SetCurrentLevel(int levelIndex)
        {
            Debug.Log($"[LevelSelectionController] SetCurrentLevel called with index: {levelIndex}");
            _navigationService?.SetCurrentIndex(levelIndex);
        }
    }
}

using System.Collections.Generic;
using UnityEngine;

namespace LevelSelection
{
    /// <summary>
    /// Director pattern implementation for building level data structures
    /// </summary>
    public class LevelSelectionDirector
    {
        public List<LevelData> BuildLevelData(List<GameObject> levelGameObjects)
        {
            var levelDataList = new List<LevelData>();

            for (int i = 0; i < levelGameObjects.Count; i++)
            {
                var levelObject = levelGameObjects[i];
                if (levelObject == null) continue;

                var levelData = LevelDataFactory.CreateFromGameObject(levelObject, i);
                if (levelData != null)
                {
                    levelDataList.Add(levelData);
                }
                else
                {
                    // Fallback - create basic level data from GameObject name
                    var fallbackData = LevelDataFactory.CreateDefault(
                        levelObject.name,
                        levelObject.name,
                        levelObject.transform.position,
                        i
                    );
                    levelDataList.Add(fallbackData);
                }
            }

            return levelDataList;
        }

        public LevelData BuildSingleLevel(GameObject levelObject, int index)
        {
            if (levelObject == null) return null;

            return LevelDataFactory.CreateFromGameObject(levelObject, index) ??
                   LevelDataFactory.CreateDefault(levelObject.name, levelObject.name, levelObject.transform.position, index);
        }
    }
}

using System.Collections.Generic;
using UnityEngine;

namespace LevelSelection
{
    /// <summary>
    /// Director pattern implementation for building level data structures
    /// </summary>
    public class LevelSelectionDirector
    {
        public List<LevelData> BuildLevelData(List<GameObject> levelGameObjects)
        {
            var levelDataList = new List<LevelData>();

            for (int i = 0; i < levelGameObjects.Count; i++)
            {
                var levelObject = levelGameObjects[i];
                if (levelObject == null) continue;

                // Try to create from LevelPoint component first
                var levelData = LevelDataFactory.CreateFromGameObject(levelObject, i);

                if (levelData == null)
                {
                    // Fallback - create from transform position and object name
                    Debug.Log($"Creating level data from transform for {levelObject.name} at position {levelObject.transform.position}");
                    levelData = LevelDataFactory.CreateFromTransform(levelObject, i);
                }

                if (levelData != null)
                {
                    Debug.Log($"Level {i}: {levelData.levelName} at position {levelData.mapPosition}");
                    levelDataList.Add(levelData);
                }
            }

            return levelDataList;
        }

        public LevelData BuildSingleLevel(GameObject levelObject, int index)
        {
            if (levelObject == null) return null;

            // Try LevelPoint component first, fallback to transform-based creation
            return LevelDataFactory.CreateFromGameObject(levelObject, index) ??
                   LevelDataFactory.CreateFromTransform(levelObject, index);
        }
    }
}

using System.Collections.Generic;
using System.Linq;
using Core.Data;
using Core.Events;
using UnityEngine;
using UnityEngine.InputSystem;
using UnityEngine.SceneManagement;
using VContainer;

namespace LevelSelection
{
    public class LevelSelectionManager : MonoBehaviour
    {
        [Header("Level Configuration")]
        [SerializeField] private bool autoDiscoverLevels = true;
        [SerializeField] private bool autoActivateOnStart = true; 
        [SerializeField] private string levelTagFilter = ""; 
        public List<GameObject> levelGameObjects = new();
        public Transform levelContainer;

        [Header("Components")] public LevelSelector levelSelector;

        public ItemSelectScreen itemSelectScreen;
        public NESCrossfade crossfade;

        [Header("Input Actions")]
        [SerializeField] private InputActionReference navigateAction;
        [SerializeField] private InputActionReference submitAction;

        private List<LevelData> _levelData;
        private List<LevelPoint> _levelPoints;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private bool _isActive = false;
        private LevelSelectionDirector _director;

        public bool IsActive
        {
            get => _isActive;
            private set => _isActive = value;
        }

        private void Awake()
        {
            _director = new LevelSelectionDirector();
            InitializeLevelSelection();
        }

        private void OnEnable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.Enable();
                navigateAction.action.performed += OnNavigate;
            }

            if (submitAction != null)
            {
                submitAction.action.Enable();
                submitAction.action.performed += OnConfirm;
            }
        }

        private void OnDisable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.performed -= OnNavigate;
                navigateAction.action.Disable();
            }

            if (submitAction != null)
            {
                submitAction.action.performed -= OnConfirm;
                submitAction.action.Disable();
            }
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        [Inject]
        public void Construct(IEventBus eventBus, IGameDataService gameDataService)
        {
            _eventBus = eventBus;
            _gameDataService = gameDataService;

            SubscribeToEvents();
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        private void InitializeLevelSelection()
        {
            // Auto-discover level objects
            if (autoDiscoverLevels)
            {
                AutoDiscoverLevelObjects();
            }
            // Manual discovery from container (fallback)
            else if (levelContainer != null && levelGameObjects.Count == 0)
            {
                for (int i = 0; i < levelContainer.childCount; i++)
                {
                    Transform child = levelContainer.GetChild(i);
                    if (child.GetComponent<LevelPoint>() != null)
                    {
                        levelGameObjects.Add(child.gameObject);
                    }
                }
            }

            Debug.Log($"[LevelSelectionManager] Found {levelGameObjects.Count} level objects");

            // Use director pattern to build level data
            _levelData = _director.BuildLevelData(levelGameObjects);
            _levelPoints = levelGameObjects.Select(go => go.GetComponent<LevelPoint>()).ToList();

            LoadLevelProgressFromGameData();

            GameData gameData = _gameDataService?.CurrentData;
            int selectedIndex = gameData?.selectedLevelIndex ?? 0;

            levelSelector?.Initialize(_levelData, _levelPoints, selectedIndex);

            // Auto-activate level selection on start if enabled
            if (autoActivateOnStart)
            {
                ActivateLevelSelection();
            }
        }

        private void AutoDiscoverLevelObjects()
        {
            levelGameObjects.Clear();

            // Find all GameObjects with LevelPoint components in the scene
            LevelPoint[] allLevelPoints = FindObjectsByType<LevelPoint>(FindObjectsSortMode.None);

            Debug.Log($"[LevelSelectionManager] Auto-discovered {allLevelPoints.Length} LevelPoint components");

            foreach (LevelPoint levelPoint in allLevelPoints)
            {
                // Optional tag filtering
                if (!string.IsNullOrEmpty(levelTagFilter) && !levelPoint.CompareTag(levelTagFilter))
                {
                    continue;
                }

                levelGameObjects.Add(levelPoint.gameObject);
            }

            // Sort by name or position for consistent ordering
            levelGameObjects.Sort((a, b) => string.Compare(a.name, b.name, System.StringComparison.Ordinal));

            Debug.Log($"[LevelSelectionManager] After filtering and sorting: {levelGameObjects.Count} level objects");
        }

        private void LoadLevelProgressFromGameData()
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData == null) return;

            // Update unlock status based on game data
            for (int i = 0; i < _levelData.Count; i++)
            {
                LevelData level = _levelData[i];
                level.isUnlocked = gameData.unlockedLevels.Contains(level.levelName);

                if (gameData.levelCompleted.TryGetValue(level.levelName, out bool completed))
                {
                    level.isCompleted = completed;
                }

                if (gameData.levelBestTimes.TryGetValue(level.levelName, out float bestTime))
                {
                    level.bestTime = bestTime;
                }
            }
        }

        private void OnNavigate(InputAction.CallbackContext context)
        {
            if (!IsActive || levelSelector == null)
            {
                Debug.Log($"[LevelSelectionManager] Navigate blocked - IsActive: {IsActive}, levelSelector: {levelSelector != null}");
                return;
            }

            Vector2 direction = context.ReadValue<Vector2>();
            Debug.Log($"[LevelSelectionManager] Navigate input received: {direction}");

            levelSelector.Navigate(direction);
            SaveSelectedLevel();
        }

        private void OnConfirm(InputAction.CallbackContext context)
        {
            if (!IsActive || levelSelector == null) return;

            levelSelector.SelectCurrentLevel();
        }

        private void SaveSelectedLevel()
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelSelector.CurrentIndex;
                _gameDataService?.SaveData();
            }
        }

        public void ActivateLevelSelection()
        {
            IsActive = true;
            gameObject.SetActive(true);
            Debug.Log($"[LevelSelectionManager] Level selection activated. Level count: {_levelData?.Count ?? 0}");
        }

        public void DeactivateLevelSelection()
        {
            IsActive = false;
            gameObject.SetActive(false);
        }

        // Debug method to check system status
        [ContextMenu("Debug Level Selection Status")]
        public void DebugStatus()
        {
            Debug.Log($"=== Level Selection Debug Status ===");
            Debug.Log($"IsActive: {IsActive}");
            Debug.Log($"Navigate Action Assigned: {navigateAction != null}");
            Debug.Log($"Submit Action Assigned: {submitAction != null}");
            Debug.Log($"Level Selector Assigned: {levelSelector != null}");
            Debug.Log($"Level Data Count: {_levelData?.Count ?? 0}");
            Debug.Log($"Level GameObjects Count: {levelGameObjects.Count}");

            if (levelSelector != null)
            {
                Debug.Log($"Selector Object Assigned: {levelSelector.selectorObject != null}");
                Debug.Log($"Current Index: {levelSelector.CurrentIndex}");
                Debug.Log($"Is Moving: {levelSelector.IsMoving}");
            }
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            LevelData selectedLevel = _levelData.FirstOrDefault(l => l.levelName == levelEvent.LevelName);
            if (selectedLevel == null) return;

            // Update current level in game data
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.currentLevel = selectedLevel.levelName;
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                _gameDataService?.SaveData();
            }

            // Show item select screen
            _eventBus?.Publish(new ItemSelectScreenRequestedEvent
            {
                Timestamp = Time.time,
                LevelName = selectedLevel.levelName
            });

            itemSelectScreen?.ShowItemSelect(selectedLevel.levelName, selectedLevel.sceneName);
        }

        private void OnLevelLoadRequested(LevelLoadRequestedEvent loadEvent)
        {
            // Start crossfade and load scene
            crossfade?.FadeOutAndIn(
                () =>
                {
                    // Load the scene
                    SceneManager.LoadScene(loadEvent.SceneName);
                },
                () =>
                {
                    // Publish level started event
                    _eventBus?.Publish(new LevelStartedEvent
                    {
                        Timestamp = Time.time,
                        LevelName = loadEvent.LevelName
                    });
                }
            );
        }

        private void OnLevelCompleted(LevelCompletedEvent completedEvent)
        {
            // Update level completion status and unlock next level
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData == null) return;

            // Mark level as completed
            gameData.levelCompleted[completedEvent.LevelName] = true;

            // Update best time
            if (!gameData.levelBestTimes.ContainsKey(completedEvent.LevelName) ||
                completedEvent.CompletionTime < gameData.levelBestTimes[completedEvent.LevelName])
            {
                gameData.levelBestTimes[completedEvent.LevelName] = completedEvent.CompletionTime;
            }

            // Unlock next level
            int completedIndex = _levelData.FindIndex(l => l.levelName == completedEvent.LevelName);
            if (completedIndex >= 0 && completedIndex + 1 < _levelData.Count)
            {
                string nextLevelName = _levelData[completedIndex + 1].levelName;
                if (!gameData.unlockedLevels.Contains(nextLevelName))
                {
                    gameData.unlockedLevels.Add(nextLevelName);
                }
            }

            _gameDataService?.SaveData();

            // Refresh level data if we're still in level selection
            if (IsActive)
            {
                LoadLevelProgressFromGameData();
                levelSelector?.Initialize(_levelData, _levelPoints, levelSelector.CurrentIndex);
            }
        }

        public List<LevelData> GetLevelData() => _levelData;
    }
}

using System.Collections.Generic;
using System.Linq;
using Core.Data;
using Core.Events;
using UnityEngine;
using UnityEngine.InputSystem;
using UnityEngine.SceneManagement;
using VContainer;

namespace LevelSelection
{
    public class LevelSelectionManager : MonoBehaviour
    {
        [Header("Level Configuration")]
        [SerializeField] private bool autoActivateOnStart = true;

        [Header("Components - Auto-discovered if not assigned")]
        public LevelSelector levelSelector;
        public ItemSelectScreen itemSelectScreen;
        public NESCrossfade crossfade;

        [Header("Input Actions - Auto-assigned from UI action map")]
        [SerializeField] private InputActionReference navigateAction;
        [SerializeField] private InputActionReference submitAction;

        // All private - no manual setup required
        private List<GameObject> _levelGameObjects = new();
        private List<LevelData> _levelData;
        private List<LevelPoint> _levelPoints;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private bool _isActive = false;
        private LevelSelectionDirector _director;

        public bool IsActive
        {
            get => _isActive;
            private set => _isActive = value;
        }

        private void Awake()
        {
            _director = new LevelSelectionDirector();
            AutoDiscoverComponents();
            InitializeLevelSelection();
        }

        private void OnEnable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.Enable();
                navigateAction.action.performed += OnNavigate;
            }

            if (submitAction != null)
            {
                submitAction.action.Enable();
                submitAction.action.performed += OnConfirm;
            }
        }

        private void OnDisable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.performed -= OnNavigate;
                navigateAction.action.Disable();
            }

            if (submitAction != null)
            {
                submitAction.action.performed -= OnConfirm;
                submitAction.action.Disable();
            }
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        [Inject]
        public void Construct(IEventBus eventBus, IGameDataService gameDataService)
        {
            _eventBus = eventBus;
            _gameDataService = gameDataService;

            SubscribeToEvents();
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        private void InitializeLevelSelection()
        {
            // Auto-discover level objects
            AutoDiscoverLevelObjects();

            Debug.Log($"[LevelSelectionManager] Found {_levelGameObjects.Count} level objects");

            // Use director pattern to build level data
            _levelData = _director.BuildLevelData(_levelGameObjects);
            _levelPoints = _levelGameObjects.Select(go => go.GetComponent<LevelPoint>()).ToList();

            LoadLevelProgressFromGameData();

            GameData gameData = _gameDataService?.CurrentData;
            int selectedIndex = gameData?.selectedLevelIndex ?? 0;

            levelSelector?.Initialize(_levelData, _levelPoints, selectedIndex);

            // Auto-activate level selection on start if enabled
            if (autoActivateOnStart)
            {
                ActivateLevelSelection();
            }
        }

        private void AutoDiscoverComponents()
        {
            // Automatically discover and assign components in the scene
            if (levelSelector == null)
            {
                levelSelector = FindObjectOfType<LevelSelector>();
            }

            if (itemSelectScreen == null)
            {
                itemSelectScreen = FindObjectOfType<ItemSelectScreen>();
            }

            if (crossfade == null)
            {
                crossfade = FindObjectOfType<NESCrossfade>();
            }

            // Log any missing components
            if (levelSelector == null)
            {
                Debug.LogError("[LevelSelectionManager] LevelSelector not found in the scene.");
            }

            if (itemSelectScreen == null)
            {
                Debug.LogError("[LevelSelectionManager] ItemSelectScreen not found in the scene.");
            }

            if (crossfade == null)
            {
                Debug.LogError("[LevelSelectionManager] NESCrossfade not found in the scene.");
            }
        }

        private void AutoDiscoverLevelObjects()
        {
            _levelGameObjects.Clear();

            // Find all GameObjects with LevelPoint components in the scene
            LevelPoint[] allLevelPoints = FindObjectsByType<LevelPoint>(FindObjectsSortMode.None);

            Debug.Log($"[LevelSelectionManager] Auto-discovered {allLevelPoints.Length} LevelPoint components");

            foreach (LevelPoint levelPoint in allLevelPoints)
            {
                _levelGameObjects.Add(levelPoint.gameObject);
            }

            // Sort by name or position for consistent ordering
            _levelGameObjects.Sort((a, b) => string.Compare(a.name, b.name, System.StringComparison.Ordinal));

            Debug.Log($"[LevelSelectionManager] After sorting: {_levelGameObjects.Count} level objects");
        }

        private void LoadLevelProgressFromGameData()
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData == null) return;

            // Update unlock status based on game data
            for (int i = 0; i < _levelData.Count; i++)
            {
                LevelData level = _levelData[i];
                level.isUnlocked = gameData.unlockedLevels.Contains(level.levelName);

                if (gameData.levelCompleted.TryGetValue(level.levelName, out bool completed))
                {
                    level.isCompleted = completed;
                }

                if (gameData.levelBestTimes.TryGetValue(level.levelName, out float bestTime))
                {
                    level.bestTime = bestTime;
                }
            }
        }

        private void OnNavigate(InputAction.CallbackContext context)
        {
            if (!IsActive || levelSelector == null)
            {
                Debug.Log($"[LevelSelectionManager] Navigate blocked - IsActive: {IsActive}, levelSelector: {levelSelector != null}");
                return;
            }

            Vector2 direction = context.ReadValue<Vector2>();
            Debug.Log($"[LevelSelectionManager] Navigate input received: {direction}");

            levelSelector.Navigate(direction);
            SaveSelectedLevel();
        }

        private void OnConfirm(InputAction.CallbackContext context)
        {
            if (!IsActive || levelSelector == null) return;

            levelSelector.SelectCurrentLevel();
        }

        private void SaveSelectedLevel()
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelSelector.CurrentIndex;
                _gameDataService?.SaveData();
            }
        }

        public void ActivateLevelSelection()
        {
            IsActive = true;
            gameObject.SetActive(true);
            Debug.Log($"[LevelSelectionManager] Level selection activated. Level count: {_levelData?.Count ?? 0}");
        }

        public void DeactivateLevelSelection()
        {
            IsActive = false;
            gameObject.SetActive(false);
        }

        // Debug method to check system status
        [ContextMenu("Debug Level Selection Status")]
        public void DebugStatus()
        {
            Debug.Log($"=== Level Selection Debug Status ===");
            Debug.Log($"IsActive: {IsActive}");
            Debug.Log($"Navigate Action Assigned: {navigateAction != null}");
            Debug.Log($"Submit Action Assigned: {submitAction != null}");
            Debug.Log($"Level Selector Assigned: {levelSelector != null}");
            Debug.Log($"Level Data Count: {_levelData?.Count ?? 0}");
            Debug.Log($"Level GameObjects Count: {_levelGameObjects.Count}");

            if (levelSelector != null)
            {
                Debug.Log($"Selector Object Assigned: {levelSelector.selectorObject != null}");
                Debug.Log($"Current Index: {levelSelector.CurrentIndex}");
                Debug.Log($"Is Moving: {levelSelector.IsMoving}");
            }
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            LevelData selectedLevel = _levelData.FirstOrDefault(l => l.levelName == levelEvent.LevelName);
            if (selectedLevel == null) return;

            // Update current level in game data
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.currentLevel = selectedLevel.levelName;
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                _gameDataService?.SaveData();
            }

            // Show item select screen
            _eventBus?.Publish(new ItemSelectScreenRequestedEvent
            {
                Timestamp = Time.time,
                LevelName = selectedLevel.levelName
            });

            itemSelectScreen?.ShowItemSelect(selectedLevel.levelName, selectedLevel.sceneName);
        }

        private void OnLevelLoadRequested(LevelLoadRequestedEvent loadEvent)
        {
            // Start crossfade and load scene
            crossfade?.FadeOutAndIn(
                () =>
                {
                    // Load the scene
                    SceneManager.LoadScene(loadEvent.SceneName);
                },
                () =>
                {
                    // Publish level started event
                    _eventBus?.Publish(new LevelStartedEvent
                    {
                        Timestamp = Time.time,
                        LevelName = loadEvent.LevelName
                    });
                }
            );
        }

        private void OnLevelCompleted(LevelCompletedEvent completedEvent)
        {
            // Update level completion status and unlock next level
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData == null) return;

            // Mark level as completed
            gameData.levelCompleted[completedEvent.LevelName] = true;

            // Update best time
            if (!gameData.levelBestTimes.ContainsKey(completedEvent.LevelName) ||
                completedEvent.CompletionTime < gameData.levelBestTimes[completedEvent.LevelName])
            {
                gameData.levelBestTimes[completedEvent.LevelName] = completedEvent.CompletionTime;
            }

            // Unlock next level
            int completedIndex = _levelData.FindIndex(l => l.levelName == completedEvent.LevelName);
            if (completedIndex >= 0 && completedIndex + 1 < _levelData.Count)
            {
                string nextLevelName = _levelData[completedIndex + 1].levelName;
                if (!gameData.unlockedLevels.Contains(nextLevelName))
                {
                    gameData.unlockedLevels.Add(nextLevelName);
                }
            }

            _gameDataService?.SaveData();

            // Refresh level data if we're still in level selection
            if (IsActive)
            {
                LoadLevelProgressFromGameData();
                levelSelector?.Initialize(_levelData, _levelPoints, levelSelector.CurrentIndex);
            }
        }

        public List<LevelData> GetLevelData() => _levelData;
    }
}

using System.Collections.Generic;
using Core.Events;
using UnityEngine;
using VContainer;

namespace LevelSelection
{
    public class LevelSelector : MonoBehaviour
    {
        [Header("Selector Configuration")] public GameObject selectorObject;

        public float moveSpeed = 5f;
        public AudioClip navigationSound;
        public AudioClip selectionSound;
        public AudioClip lockedSound;

        [Header("Grid Navigation")] public int gridWidth = 4;

        public float snapThreshold = 0.1f;
        private AudioSource _audioSource;

        private List<LevelData> _availableLevels;
        private IEventBus _eventBus;
        private List<LevelPoint> _levelPoints;
        private Vector3 _targetPosition;

        public int CurrentIndex { get; private set; }

        public LevelData CurrentLevel => _availableLevels?[CurrentIndex];
        public bool IsMoving { get; private set; }

        private void Awake()
        {
            _audioSource = GetComponent<AudioSource>();
            if (_audioSource == null)
            {
                _audioSource = gameObject.AddComponent<AudioSource>();
            }
        }

        private void Update()
        {
            if (IsMoving && selectorObject != null)
            {
                selectorObject.transform.position = Vector3.MoveTowards(
                    selectorObject.transform.position,
                    _targetPosition,
                    moveSpeed * Time.deltaTime
                );

                if (Vector3.Distance(selectorObject.transform.position, _targetPosition) < snapThreshold)
                {
                    selectorObject.transform.position = _targetPosition;
                    IsMoving = false;
                }
            }
        }

        [Inject]
        public void Construct(IEventBus eventBus)
        {
            _eventBus = eventBus;
        }

        public void Initialize(List<LevelData> levels, List<LevelPoint> levelPoints, int selectedIndex = 0)
        {
            _availableLevels = levels;
            _levelPoints = levelPoints;
            CurrentIndex = Mathf.Clamp(selectedIndex, 0, levels.Count - 1);

            UpdateLevelStates();
            MoveToCurrentLevel(true); // Instant move on initialization
        }

        public void Navigate(Vector2 direction)
        {
            if (IsMoving || _availableLevels == null || _availableLevels.Count == 0)
                return;

            int newIndex = CalculateNewIndex(direction);

            if (newIndex != CurrentIndex && newIndex >= 0 && newIndex < _availableLevels.Count)
            {
                int previousIndex = CurrentIndex;
                CurrentIndex = newIndex;

                PlaySound(navigationSound);
                UpdateSelection();
                MoveToCurrentLevel();

                _eventBus?.Publish(new LevelNavigationEvent
                {
                    Timestamp = Time.time,
                    PreviousIndex = previousIndex,
                    NewIndex = CurrentIndex,
                    Direction = direction
                });
            }
        }

        private int CalculateNewIndex(Vector2 direction)
        {
            // Adventure Island III style navigation - mostly horizontal with some vertical
            int currentRow = CurrentIndex / gridWidth;
            int currentCol = CurrentIndex % gridWidth;

            if (Mathf.Abs(direction.x) > Mathf.Abs(direction.y))
            {
                // Horizontal movement
                if (direction.x > 0) // Right
                {
                    return Mathf.Min(CurrentIndex + 1, _availableLevels.Count - 1);
                }

                // Left
                return Mathf.Max(CurrentIndex - 1, 0);
            }

            // Vertical movement
            if (direction.y > 0) // Up
            {
                int newIndex = CurrentIndex - gridWidth;
                return newIndex >= 0 ? newIndex : CurrentIndex;
            }
            else // Down
            {
                int newIndex = CurrentIndex + gridWidth;
                return newIndex < _availableLevels.Count ? newIndex : CurrentIndex;
            }
        }

        public void SelectCurrentLevel()
        {
            if (_availableLevels == null || CurrentIndex >= _availableLevels.Count)
                return;

            LevelData selectedLevel = _availableLevels[CurrentIndex];

            if (!selectedLevel.isUnlocked)
            {
                PlaySound(lockedSound);
                return;
            }

            PlaySound(selectionSound);

            _eventBus?.Publish(new LevelSelectedEvent
            {
                Timestamp = Time.time,
                LevelName = selectedLevel.levelName,
                LevelIndex = CurrentIndex
            });
        }

        private void UpdateSelection()
        {
            for (int i = 0; i < _levelPoints.Count; i++)
            {
                if (_levelPoints[i] != null)
                {
                    _levelPoints[i].SetSelected(i == CurrentIndex);
                }
            }
        }

        private void UpdateLevelStates()
        {
            for (int i = 0; i < _levelPoints.Count && i < _availableLevels.Count; i++)
            {
                if (_levelPoints[i] != null)
                {
                    _levelPoints[i].SetUnlocked(_availableLevels[i].isUnlocked);
                    _levelPoints[i].SetSelected(i == CurrentIndex);
                }
            }
        }

        private void MoveToCurrentLevel(bool instant = false)
        {
            if (CurrentIndex >= _levelPoints.Count || _levelPoints[CurrentIndex] == null)
                return;

            _targetPosition = _levelPoints[CurrentIndex].transform.position;

            if (instant)
            {
                selectorObject.transform.position = _targetPosition;
                IsMoving = false;
            }
            else
            {
                IsMoving = true;
            }
        }

        private void PlaySound(AudioClip clip)
        {
            if (_audioSource && clip)
            {
                _audioSource.PlayOneShot(clip);
            }
        }
    }
}

using System.Collections.Generic;
using Core.Events;
using UnityEngine;
using VContainer;

namespace LevelSelection
{
    public class LevelSelector : MonoBehaviour
    {
        [Header("Selector Configuration")] public GameObject selectorObject;

        public float moveSpeed = 5f;
        public AudioClip navigationSound;
        public AudioClip selectionSound;
        public AudioClip lockedSound;

        [Header("Grid Navigation")] public int gridWidth = 4;

        public float snapThreshold = 0.1f;
        private AudioSource _audioSource;

        private List<LevelData> _availableLevels;
        private IEventBus _eventBus;
        private List<LevelPoint> _levelPoints;
        private Vector3 _targetPosition;

        public int CurrentIndex { get; private set; }

        public LevelData CurrentLevel => _availableLevels?[CurrentIndex];
        public bool IsMoving { get; private set; }

        private void Awake()
        {
            _audioSource = GetComponent<AudioSource>();
            if (_audioSource == null)
            {
                _audioSource = gameObject.AddComponent<AudioSource>();
            }
        }

        private void Update()
        {
            if (IsMoving && selectorObject != null)
            {
                selectorObject.transform.position = Vector3.MoveTowards(
                    selectorObject.transform.position,
                    _targetPosition,
                    moveSpeed * Time.deltaTime
                );

                if (Vector3.Distance(selectorObject.transform.position, _targetPosition) < snapThreshold)
                {
                    selectorObject.transform.position = _targetPosition;
                    IsMoving = false;
                }
            }
        }

        [Inject]
        public void Construct(IEventBus eventBus)
        {
            _eventBus = eventBus;
        }

        public void Initialize(List<LevelData> levels, List<LevelPoint> levelPoints, int selectedIndex = 0)
        {
            _availableLevels = levels;
            _levelPoints = levelPoints;
            CurrentIndex = Mathf.Clamp(selectedIndex, 0, levels.Count - 1);

            UpdateLevelStates();
            MoveToCurrentLevel(true); // Instant move on initialization
        }

        public void Navigate(Vector2 direction)
        {
            Debug.Log($"[LevelSelector] Navigate called with direction: {direction}");
            Debug.Log($"[LevelSelector] IsMoving: {IsMoving}, AvailableLevels count: {_availableLevels?.Count ?? 0}");

            if (IsMoving || _availableLevels == null || _availableLevels.Count == 0)
            {
                Debug.Log("[LevelSelector] Navigate blocked - IsMoving or no levels available");
                return;
            }

            int newIndex = CalculateNewIndex(direction);
            Debug.Log($"[LevelSelector] Current index: {CurrentIndex}, New index: {newIndex}");

            if (newIndex != CurrentIndex && newIndex >= 0 && newIndex < _availableLevels.Count)
            {
                int previousIndex = CurrentIndex;
                CurrentIndex = newIndex;

                Debug.Log($"[LevelSelector] Moving from index {previousIndex} to {CurrentIndex}");

                PlaySound(navigationSound);
                UpdateSelection();
                MoveToCurrentLevel();

                _eventBus?.Publish(new LevelNavigationEvent
                {
                    Timestamp = Time.time,
                    PreviousIndex = previousIndex,
                    NewIndex = CurrentIndex,
                    Direction = direction
                });
            }
            else
            {
                Debug.Log($"[LevelSelector] Navigation blocked - same index or out of bounds");
            }
        }

        private int CalculateNewIndex(Vector2 direction)
        {
            // Adventure Island III style navigation - mostly horizontal with some vertical
            int currentRow = CurrentIndex / gridWidth;
            int currentCol = CurrentIndex % gridWidth;

            if (Mathf.Abs(direction.x) > Mathf.Abs(direction.y))
            {
                // Horizontal movement
                if (direction.x > 0) // Right
                {
                    return Mathf.Min(CurrentIndex + 1, _availableLevels.Count - 1);
                }

                // Left
                return Mathf.Max(CurrentIndex - 1, 0);
            }

            // Vertical movement
            if (direction.y > 0) // Up
            {
                int newIndex = CurrentIndex - gridWidth;
                return newIndex >= 0 ? newIndex : CurrentIndex;
            }
            else // Down
            {
                int newIndex = CurrentIndex + gridWidth;
                return newIndex < _availableLevels.Count ? newIndex : CurrentIndex;
            }
        }

        public void SelectCurrentLevel()
        {
            if (_availableLevels == null || CurrentIndex >= _availableLevels.Count)
                return;

            LevelData selectedLevel = _availableLevels[CurrentIndex];

            if (!selectedLevel.isUnlocked)
            {
                PlaySound(lockedSound);
                return;
            }

            PlaySound(selectionSound);

            _eventBus?.Publish(new LevelSelectedEvent
            {
                Timestamp = Time.time,
                LevelName = selectedLevel.levelName,
                LevelIndex = CurrentIndex
            });
        }

        private void UpdateSelection()
        {
            for (int i = 0; i < _levelPoints.Count; i++)
            {
                if (_levelPoints[i] != null)
                {
                    _levelPoints[i].SetSelected(i == CurrentIndex);
                }
            }
        }

        private void UpdateLevelStates()
        {
            for (int i = 0; i < _levelPoints.Count && i < _availableLevels.Count; i++)
            {
                if (_levelPoints[i] != null)
                {
                    _levelPoints[i].SetUnlocked(_availableLevels[i].isUnlocked);
                    _levelPoints[i].SetSelected(i == CurrentIndex);
                }
            }
        }

        private void MoveToCurrentLevel(bool instant = false)
        {
            if (CurrentIndex >= _levelPoints.Count || _levelPoints[CurrentIndex] == null)
                return;

            _targetPosition = _levelPoints[CurrentIndex].transform.position;

            if (instant)
            {
                selectorObject.transform.position = _targetPosition;
                IsMoving = false;
            }
            else
            {
                IsMoving = true;
            }
        }

        private void PlaySound(AudioClip clip)
        {
            if (_audioSource && clip)
            {
                _audioSource.PlayOneShot(clip);
            }
        }
    }
}

using System;
using System.Collections;
using UnityEngine;
using UnityEngine.UI;

namespace LevelSelection
{
    public class NesCrossfade : MonoBehaviour
    {
        [Header("Crossfade Configuration")] 
        public Image fadeImage;
        public float fadeDuration = 1f;
        public Color fadeColor = Color.black;
        public AnimationCurve fadeCurve = AnimationCurve.EaseInOut(0, 0, 1, 1);

        [Header("NES Style Effect")] 
        public bool useNESEffect = true;
        public Color[] nesColors = { Color.black, new(0.2f, 0.2f, 0.3f), new(0.1f, 0.1f, 0.2f) };
        public float colorFlickerSpeed = 10f;

        private LevelSelectionConfig _config;
        private bool _isFading;
        private Action _onFadeComplete;

        private void Awake()
        {
            SetupFadeImage();
            // Start with image disabled (hidden)
            if (fadeImage)
            {
                fadeImage.enabled = false;
            }

            Debug.Log("[NESCrossfade] Initialized with hidden image");
        }

        private void SetupFadeImage()
        {
            if (fadeImage == null)
            {
                // Create fade image if not assigned
                GameObject fadeGO = new("FadeImage");
                fadeGO.transform.SetParent(transform, false);

                // Ensure we have a Canvas component
                Canvas canvas = GetComponentInParent<Canvas>();
                if (canvas == null)
                {
                    canvas = gameObject.AddComponent<Canvas>();
                    canvas.sortingOrder = 1000;
                    canvas.renderMode = RenderMode.ScreenSpaceOverlay;

                    // Add CanvasScaler for proper scaling
                    CanvasScaler scaler = gameObject.AddComponent<CanvasScaler>();
                    scaler.uiScaleMode = CanvasScaler.ScaleMode.ScaleWithScreenSize;
                    scaler.referenceResolution = new Vector2(1920, 1080);

                    // Add GraphicRaycaster
                    gameObject.AddComponent<GraphicRaycaster>();
                }

                fadeImage = fadeGO.AddComponent<Image>();
                RectTransform rect = fadeImage.rectTransform;
                rect.anchorMin = Vector2.zero;
                rect.anchorMax = Vector2.one;
                rect.sizeDelta = Vector2.zero;
                rect.anchoredPosition = Vector2.zero;

                // Set initial color with full alpha (but start disabled)
                fadeImage.color = new Color(fadeColor.r, fadeColor.g, fadeColor.b, 1f);
                fadeImage.enabled = false;
            }

            // Ensure the image covers the full screen
            if (fadeImage != null)
            {
                RectTransform rect = fadeImage.rectTransform;
                rect.anchorMin = Vector2.zero;
                rect.anchorMax = Vector2.one;
                rect.sizeDelta = Vector2.zero;
                rect.anchoredPosition = Vector2.zero;
            }
        }

        public void SetConfig(LevelSelectionConfig config)
        {
            _config = config;

            // Update fade settings from config
            if (_config != null)
            {
                fadeDuration = _config.transitionDuration;
                nesColors = _config.nesTransitionColors;
            }
        }

        public void FadeOut(Action onComplete = null)
        {
            if (_isFading) return;

            _onFadeComplete = onComplete;
            StartCoroutine(FadeCoroutine(0f, 1f));
        }

        public void FadeIn(Action onComplete = null)
        {
            if (_isFading) return;

            _onFadeComplete = onComplete;
            StartCoroutine(FadeCoroutine(1f, 0f));
        }

        public void FadeOutAndIn(Action onMiddle = null, Action onComplete = null)
        {
            if (_isFading) return;

            StartCoroutine(FadeOutAndInCoroutine(onMiddle, onComplete));
        }

        private IEnumerator FadeCoroutine(float from, float to)
        {
            _isFading = true;

            // Enable image when starting fade
            if (fadeImage != null)
            {
                fadeImage.enabled = true;
            }

            float elapsed = 0f;

            while (elapsed < fadeDuration)
            {
                elapsed += Time.deltaTime;
                float progress = elapsed / fadeDuration;
                float curveValue = fadeCurve.Evaluate(progress);
                float alpha = Mathf.Lerp(from, to, curveValue);

                if (useNESEffect)
                {
                    SetNESStyleAlpha(alpha);
                }
                else
                {
                    SetAlpha(alpha);
                }

                yield return null;
            }

            if (useNESEffect)
            {
                SetNESStyleAlpha(to);
            }
            else
            {
                SetAlpha(to);
            }

            // Hide image if fade is complete (alpha = 0)
            if (to <= 0f && fadeImage != null)
            {
                fadeImage.enabled = false;
            }

            _isFading = false;
            _onFadeComplete?.Invoke();
            _onFadeComplete = null;
        }

        private IEnumerator FadeOutAndInCoroutine(Action onMiddle, Action onComplete)
        {
            // Fade out
            yield return StartCoroutine(FadeCoroutine(0f, 1f));

            // Middle action (like loading scene)
            onMiddle?.Invoke();

            // Small delay to ensure scene is loaded
            yield return new WaitForSeconds(0.1f);

            // Fade in
            yield return StartCoroutine(FadeCoroutine(1f, 0f));

            onComplete?.Invoke();
        }

        private void SetAlpha(float alpha)
        {
            if (fadeImage != null)
            {
                Color color = fadeColor;
                color.a = alpha;
                fadeImage.color = color;
            }
        }

        private void SetNESStyleAlpha(float alpha)
        {
            if (fadeImage == null || nesColors == null || nesColors.Length == 0) return;

            // Create NES-style flickering effect
            int colorIndex = Mathf.FloorToInt(Time.time * colorFlickerSpeed) % nesColors.Length;
            Color nesColor = nesColors[colorIndex];
            nesColor.a = alpha;
            fadeImage.color = nesColor;
        }

        /// <summary>
        ///     Set fade color programmatically
        /// </summary>
        public void SetFadeColor(Color color)
        {
            fadeColor = color;
        }

        /// <summary>
        ///     Show the crossfade immediately (useful for scene start)
        /// </summary>
        public void Show()
        {
            if (fadeImage != null)
            {
                fadeImage.enabled = true;
                SetAlpha(1f);
            }
        }

        /// <summary>
        ///     Hide the crossfade immediately
        /// </summary>
        public void Hide()
        {
            if (fadeImage != null)
            {
                fadeImage.enabled = false;
                SetAlpha(0f);
            }
        }

        /// <summary>
        ///     Get current fade progress (0 = hidden, 1 = fully visible)
        /// </summary>
        public float GetFadeProgress() => fadeImage && fadeImage.enabled ? fadeImage.color.a : 0f;

        /// <summary>
        ///     Instantly set fade to specific alpha without animation
        /// </summary>
        public void SetInstantFade(float alpha)
        {
            if (useNESEffect)
            {
                SetNESStyleAlpha(alpha);
            }
            else
            {
                SetAlpha(alpha);
            }
        }

        /// <summary>
        ///     Check if currently fading
        /// </summary>
        public bool IsFading => _isFading;
    }
}

// This file has been removed as it's no longer used
// Scene transitions are now handled by SceneLoadService in the SOLID architecture
// The crossfade functionality was replaced with a cleaner service-based approach

using System;
using System.Collections;
using UnityEngine;
using UnityEngine.SceneManagement;
using UnityEngine.UI;

namespace LevelSelection
{
    /// <summary>
    ///     Standalone scene transition manager that works in any scene
    ///     Automatically creates crossfade and handles all scene transitions
    /// </summary>
    public class SceneTransitionManager : MonoBehaviour
    {

        [Header("Transition Settings")] [SerializeField]
        private bool useNesEffect = true;

        [SerializeField] private bool fadeInOnSceneStart = true;
        [SerializeField] private float fadeInDelay = 0.1f;
        [SerializeField] private float fadeDuration = 1f;
        [SerializeField] private Color fadeColor = Color.black;
        [SerializeField] private Color[] nesColors = { Color.black, new(0.2f, 0.2f, 0.3f), new(0.1f, 0.1f, 0.2f) };
        private NESCrossfade _activeCrossfade;

        public static SceneTransitionManager Instance { get; private set; }

        public bool IsTransitioning { get; private set; }

        private void Awake()
        {
            // Implement singleton pattern that works across scenes
            if (Instance == null)
            {
                Instance = this;
                DontDestroyOnLoad(gameObject);
                CreateStandaloneCrossfade();
                SubscribeToSceneEvents();
                Debug.Log("[SceneTransitionManager] Initialized as standalone singleton");
            }
            else if (Instance != this)
            {
                Debug.Log("[SceneTransitionManager] Duplicate instance destroyed");
                Destroy(gameObject);
            }
        }

        private void Start()
        {
            // Fade in when scene starts (if enabled and crossfade is visible)
            if (fadeInOnSceneStart && _activeCrossfade != null)
            {
                StartCoroutine(FadeInAfterDelay());
            }
        }

        private void OnDestroy()
        {
            if (Instance == this)
            {
                UnsubscribeFromSceneEvents();
                Instance = null;
            }
        }

        private void CreateStandaloneCrossfade()
        {
            // Use the Canvas on this GameObject instead of creating a new one
            Canvas canvas = GetComponent<Canvas>();
            if (canvas == null)
            {
                canvas = gameObject.AddComponent<Canvas>();
                canvas.renderMode = RenderMode.ScreenSpaceOverlay;
                canvas.sortingOrder = 9999;
            }

            // Add CanvasScaler if not present
            CanvasScaler scaler = GetComponent<CanvasScaler>();
            if (scaler == null)
            {
                scaler = gameObject.AddComponent<CanvasScaler>();
                scaler.uiScaleMode = CanvasScaler.ScaleMode.ScaleWithScreenSize;
                scaler.referenceResolution = new Vector2(256, 240);
            }

            // Add GraphicRaycaster if not present
            if (!GetComponent<GraphicRaycaster>())
            {
                gameObject.AddComponent<GraphicRaycaster>();
            }

            // Create fade image as child of this GameObject
            GameObject imageGo = new("FadeImage");
            imageGo.transform.SetParent(transform, false);

            Image fadeImage = imageGo.AddComponent<Image>();
            RectTransform rect = fadeImage.rectTransform;

            // Instead of stretching to fill screen, use the reference resolution size
            rect.anchorMin = new Vector2(0.5f, 0.5f); // Center anchors
            rect.anchorMax = new Vector2(0.5f, 0.5f);
            rect.sizeDelta = scaler.referenceResolution; // Use reference resolution as size
            rect.anchoredPosition = Vector2.zero; // Center position

            // Set initial color and disable
            fadeImage.color = new Color(fadeColor.r, fadeColor.g, fadeColor.b, 1f);
            fadeImage.enabled = false;

            // Add and configure NESCrossfade component on this GameObject
            _activeCrossfade = gameObject.AddComponent<NESCrossfade>();
            _activeCrossfade.fadeImage = fadeImage;
            _activeCrossfade.fadeDuration = fadeDuration;
            _activeCrossfade.fadeColor = fadeColor;
            _activeCrossfade.useNESEffect = useNesEffect;
            _activeCrossfade.nesColors = nesColors;

            Debug.Log("[SceneTransitionManager] Crossfade configured using existing Canvas settings");
        }

        private void SubscribeToSceneEvents()
        {
            // Listen for scene load events to handle automatic transitions
            SceneManager.sceneLoaded += OnSceneLoaded;
        }

        private void UnsubscribeFromSceneEvents()
        {
            SceneManager.sceneLoaded -= OnSceneLoaded;
        }

        private void OnSceneLoaded(Scene scene, LoadSceneMode mode)
        {
            Debug.Log($"[SceneTransitionManager] Scene loaded: {scene.name}");

            // Auto fade in after scene load
            if (fadeInOnSceneStart && _activeCrossfade != null)
            {
                StartCoroutine(FadeInAfterDelay());
            }
        }

        private IEnumerator FadeInAfterDelay()
        {
            if (_activeCrossfade == null) yield break;

            yield return new WaitForSeconds(fadeInDelay);

            // Only fade in if crossfade is currently visible
            if (_activeCrossfade.GetFadeProgress() > 0f)
            {
                Debug.Log("[SceneTransitionManager] Auto fading in on scene start");
                _activeCrossfade.FadeIn(() => Debug.Log("[SceneTransitionManager] Scene fade-in complete"));
            }
        }

        /// <summary>
        ///     Transition to a new scene with crossfade effect
        /// </summary>
        public void TransitionToScene(string sceneName)
        {
            if (!IsTransitioning)
            {
                StartCoroutine(TransitionToSceneCoroutine(sceneName));
            }
        }

        private IEnumerator TransitionToSceneCoroutine(string sceneName)
        {
            if (_activeCrossfade == null)
            {
                Debug.LogError("[SceneTransitionManager] No crossfade available! Loading scene directly.");
                SceneManager.LoadScene(sceneName);
                yield break;
            }

            IsTransitioning = true;

            Debug.Log($"[SceneTransitionManager] Starting transition to: {sceneName}");

            // Fade out
            bool fadeOutComplete = false;
            _activeCrossfade.FadeOut(() => fadeOutComplete = true);

            // Wait for fade out to complete
            yield return new WaitUntil(() => fadeOutComplete);

            // Load the new scene
            Debug.Log($"[SceneTransitionManager] Loading scene: {sceneName}");
            SceneManager.LoadScene(sceneName);

            // Reset transitioning flag after scene loads
            IsTransitioning = false;

            Debug.Log($"[SceneTransitionManager] Transition to {sceneName} complete");
        }

        /// <summary>
        ///     Manually trigger fade out
        /// </summary>
        public void FadeOut(Action onComplete = null)
        {
            _activeCrossfade?.FadeOut(onComplete);
        }

        /// <summary>
        ///     Manually trigger fade in
        /// </summary>
        public void FadeIn(Action onComplete = null)
        {
            _activeCrossfade?.FadeIn(onComplete);
        }

        /// <summary>
        ///     Check if crossfade is currently visible
        /// </summary>
        public bool IsCrossfadeVisible() => _activeCrossfade != null && _activeCrossfade.GetFadeProgress() > 0f;

        /// <summary>
        ///     Create a standalone SceneTransitionManager if one doesn't exist
        /// </summary>
        [RuntimeInitializeOnLoadMethod(RuntimeInitializeLoadType.BeforeSceneLoad)]
        public static void EnsureInstance()
        {
            if (Instance == null)
            {
                GameObject go = new("SceneTransitionManager");
                go.AddComponent<SceneTransitionManager>();
                Debug.Log("[SceneTransitionManager] Auto-created standalone instance");
            }
        }

        /// <summary>
        ///     Static method to transition scenes from anywhere
        /// </summary>
        public static void TransitionTo(string sceneName)
        {
            if (Instance != null)
            {
                Instance.TransitionToScene(sceneName);
            }
            else
            {
                Debug.LogWarning("[SceneTransitionManager] No instance available, loading scene directly");
                SceneManager.LoadScene(sceneName);
            }
        }
    }
}

using System;
using System.Collections;
using UnityEngine;
using UnityEngine.SceneManagement;
using UnityEngine.UI;

namespace LevelSelection
{
    /// <summary>
    /// Standalone scene transition manager that works in any scene.
    /// Automatically creates crossfade and handles all scene transitions.
    /// </summary>
    public class SceneTransitionManager : MonoBehaviour
    {
        #region Serialized Fields
        [Header("Transition Settings")]
        [SerializeField] private bool useNesEffect = true;
        [SerializeField] private bool fadeInOnSceneStart = true;
        [SerializeField] private float fadeInDelay = 0.1f;
        [SerializeField] private float fadeDuration = 1f;
        [SerializeField] private Color fadeColor = Color.black;
        [SerializeField] private Color[] nesColors = { 
            Color.black, 
            new(0.2f, 0.2f, 0.3f), 
            new(0.1f, 0.1f, 0.2f) 
        };
        #endregion

        #region Private Fields
        private NESCrossfade _activeCrossfade;
        #endregion

        #region Properties
        public static SceneTransitionManager Instance { get; private set; }
        public bool IsTransitioning { get; private set; }
        #endregion

        #region Unity Lifecycle
        private void Awake()
        {
            InitializeSingleton();
        }

        private void Start()
        {
            if (fadeInOnSceneStart && _activeCrossfade != null)
            {
                StartCoroutine(FadeInAfterDelay());
            }
        }

        private void OnDestroy()
        {
            if (Instance == this)
            {
                UnsubscribeFromSceneEvents();
                Instance = null;
            }
        }
        #endregion

        #region Initialization
        private void InitializeSingleton()
        {
            if (Instance == null)
            {
                Instance = this;
                DontDestroyOnLoad(gameObject);
                SetupCrossfade();
                SubscribeToSceneEvents();
                Debug.Log("[SceneTransitionManager] Initialized as singleton");
            }
            else if (Instance != this)
            {
                Debug.Log("[SceneTransitionManager] Duplicate instance destroyed");
                Destroy(gameObject);
            }
        }

        private void SetupCrossfade()
        {
            SetupCanvas();
            CreateFadeImage();
            ConfigureCrossfadeComponent();
            Debug.Log("[SceneTransitionManager] Crossfade setup complete");
        }

        private void SetupCanvas()
        {
            // Ensure Canvas exists
            if (GetComponent<Canvas>() == null)
            {
                var canvas = gameObject.AddComponent<Canvas>();
                canvas.renderMode = RenderMode.ScreenSpaceOverlay;
                canvas.sortingOrder = 9999;
            }

            // Ensure CanvasScaler exists
            if (GetComponent<CanvasScaler>() == null)
            {
                var scaler = gameObject.AddComponent<CanvasScaler>();
                scaler.uiScaleMode = CanvasScaler.ScaleMode.ScaleWithScreenSize;
                scaler.referenceResolution = new Vector2(256, 240);
            }

            // Ensure GraphicRaycaster exists
            if (GetComponent<GraphicRaycaster>() == null)
            {
                gameObject.AddComponent<GraphicRaycaster>();
            }
        }

        private void CreateFadeImage()
        {
            var imageObject = new GameObject("FadeImage");
            imageObject.transform.SetParent(transform, false);

            var fadeImage = imageObject.AddComponent<Image>();
            var rect = fadeImage.rectTransform;

            // Center the image and use reference resolution size
            rect.anchorMin = new Vector2(0.5f, 0.5f);
            rect.anchorMax = new Vector2(0.5f, 0.5f);
            rect.sizeDelta = GetComponent<CanvasScaler>().referenceResolution;
            rect.anchoredPosition = Vector2.zero;

            // Configure initial state
            fadeImage.color = new Color(fadeColor.r, fadeColor.g, fadeColor.b, 1f);
            fadeImage.enabled = false;

            // Store reference for crossfade component
            _activeCrossfade = gameObject.AddComponent<NESCrossfade>();
            _activeCrossfade.fadeImage = fadeImage;
        }

        private void ConfigureCrossfadeComponent()
        {
            _activeCrossfade.fadeDuration = fadeDuration;
            _activeCrossfade.fadeColor = fadeColor;
            _activeCrossfade.useNESEffect = useNesEffect;
            _activeCrossfade.nesColors = nesColors;
        }
        #endregion

        #region Scene Events
        private void SubscribeToSceneEvents()
        {
            SceneManager.sceneLoaded += OnSceneLoaded;
        }

        private void UnsubscribeFromSceneEvents()
        {
            SceneManager.sceneLoaded -= OnSceneLoaded;
        }

        private void OnSceneLoaded(Scene scene, LoadSceneMode mode)
        {
            Debug.Log($"[SceneTransitionManager] Scene loaded: {scene.name}");

            if (fadeInOnSceneStart && _activeCrossfade != null)
            {
                StartCoroutine(FadeInAfterDelay());
            }
        }
        #endregion

        #region Fade Effects
        private IEnumerator FadeInAfterDelay()
        {
            if (_activeCrossfade == null) yield break;

            yield return new WaitForSeconds(fadeInDelay);

            if (_activeCrossfade.GetFadeProgress() > 0f)
            {
                Debug.Log("[SceneTransitionManager] Auto fading in on scene start");
                _activeCrossfade.FadeIn(() => Debug.Log("[SceneTransitionManager] Scene fade-in complete"));
            }
        }

        public void FadeOut(Action onComplete = null)
        {
            _activeCrossfade?.FadeOut(onComplete);
        }

        public void FadeIn(Action onComplete = null)
        {
            _activeCrossfade?.FadeIn(onComplete);
        }

        public bool IsCrossfadeVisible() => 
            _activeCrossfade != null && _activeCrossfade.GetFadeProgress() > 0f;
        #endregion

        #region Scene Transitions
        public void TransitionToScene(string sceneName)
        {
            if (!IsTransitioning)
            {
                StartCoroutine(TransitionToSceneCoroutine(sceneName));
            }
        }

        private IEnumerator TransitionToSceneCoroutine(string sceneName)
        {
            if (_activeCrossfade == null)
            {
                Debug.LogError("[SceneTransitionManager] No crossfade available! Loading scene directly.");
                SceneManager.LoadScene(sceneName);
                yield break;
            }

            IsTransitioning = true;
            Debug.Log($"[SceneTransitionManager] Starting transition to: {sceneName}");

            // Fade out
            bool fadeOutComplete = false;
            _activeCrossfade.FadeOut(() => fadeOutComplete = true);
            yield return new WaitUntil(() => fadeOutComplete);

            // Load new scene
            Debug.Log($"[SceneTransitionManager] Loading scene: {sceneName}");
            SceneManager.LoadScene(sceneName);

            IsTransitioning = false;
            Debug.Log($"[SceneTransitionManager] Transition to {sceneName} complete");
        }
        #endregion

        #region Static Methods
        [RuntimeInitializeOnLoadMethod(RuntimeInitializeLoadType.BeforeSceneLoad)]
        public static void EnsureInstance()
        {
            if (Instance == null)
            {
                var go = new GameObject("SceneTransitionManager");
                go.AddComponent<SceneTransitionManager>();
                Debug.Log("[SceneTransitionManager] Auto-created instance");
            }
        }

        public static void TransitionTo(string sceneName)
        {
            if (Instance != null)
            {
                Instance.TransitionToScene(sceneName);
            }
            else
            {
                Debug.LogWarning("[SceneTransitionManager] No instance available, loading scene directly");
                SceneManager.LoadScene(sceneName);
            }
        }
        #endregion
    }
}

using UnityEngine;

namespace LevelSelection.Services
{
    /// <summary>
    /// Service responsible for audio feedback during level selection
    /// </summary>
    public interface IAudioFeedbackService
    {
        void Initialize(AudioSource audioSource, LevelSelectionConfig config);
        void PlayNavigationSound();
        void PlaySelectionSound();
        void PlayLockedSound();
    }

    /// <summary>
    /// Handles audio feedback for level selection events (Single Responsibility)
    /// </summary>
    public class AudioFeedbackService : IAudioFeedbackService
    {
        private AudioSource _audioSource;
        private LevelSelectionConfig _config;

        public void Initialize(AudioSource audioSource, LevelSelectionConfig config)
        {
            _audioSource = audioSource;
            _config = config;
        }

        public void PlayNavigationSound()
        {
            if (_config?.navigationSound && _audioSource)
            {
                _audioSource.PlayOneShot(_config.navigationSound);
            }
        }

        public void PlaySelectionSound()
        {
            if (_config?.selectionSound && _audioSource)
            {
                _audioSource.PlayOneShot(_config.selectionSound);
            }
        }

        public void PlayLockedSound()
        {
            if (_config?.lockedSound && _audioSource)
            {
                _audioSource.PlayOneShot(_config.lockedSound);
            }
        }
    }
}

using UnityEngine;

namespace LevelSelection.Services
{
    /// <summary>
    /// Service responsible for input processing and filtering
    /// </summary>
    public interface IInputFilterService
    {
        void Initialize(LevelSelectionConfig config);
        void SetEnabled(bool enabled);
        bool ProcessNavigationInput(Vector2 direction, out Vector2 filteredDirection);
        void TriggerSubmit();
    }

    /// <summary>
    /// Handles input filtering and cooldowns (Single Responsibility)
    /// </summary>
    public class InputFilterService : IInputFilterService
    {
        private const float InputCooldownTime = 0.2f; // Prevent input spam
        private const float InputDeadzone = 0.5f; // Input threshold

        private LevelSelectionConfig _config;
        private Vector2 _lastInputDirection;
        private float _lastInputTime;
        private bool _isEnabled = true;

        public void Initialize(LevelSelectionConfig config)
        {
            _config = config;
        }

        public void SetEnabled(bool enabled)
        {
            _isEnabled = enabled;
        }

        public bool ProcessNavigationInput(Vector2 direction, out Vector2 filteredDirection)
        {
            filteredDirection = Vector2.zero;

            if (!_isEnabled) return false;

            // Apply deadzone filtering
            if (direction.magnitude < InputDeadzone) return false;

            // Apply input cooldown to prevent spam
            if (Time.time - _lastInputTime < InputCooldownTime) return false;

            // Normalize direction for consistent behavior
            direction = direction.normalized;

            // Check if this is the same direction as last input (prevent repeats)
            if (Vector2.Dot(direction, _lastInputDirection) > 0.8f &&
                Time.time - _lastInputTime < InputCooldownTime * 2f) return false;

            _lastInputDirection = direction;
            _lastInputTime = Time.time;

            filteredDirection = direction;
            return true;
        }

        public void TriggerSubmit()
        {
            // Implementation for submit if needed
        }
    }
}

