    public class PlayerSoundController : MonoBehaviour
    {
        [SerializeField] private AudioSource audioSource;
        [SerializeField] private AudioClip jumpSound;
        [SerializeField] private AudioClip collectSound;
        [SerializeField] private AudioClip damageSound;
        [SerializeField] private AudioClip deathSound;

    }
}

using Core.Events;
using Collectables.Score;
using Health.Interfaces;
using UnityEngine;
using VContainer;

namespace Player.Components
{
    /// <summary>
    /// Handles all player sound effects and audio feedback
    /// </summary>
    public class PlayerSoundController : MonoBehaviour
    {
        [Header("Audio Source")]
        [SerializeField] private AudioSource audioSource;

        [Header("Sound Effects")]
        [SerializeField] private AudioClip jumpSound;
        [SerializeField] private AudioClip collectSound;
        [SerializeField] private AudioClip damageSound;
        [SerializeField] private AudioClip deathSound;

        [Header("Volume Settings")]
        [SerializeField, Range(0f, 1f)] private float jumpVolume = 1f;
        [SerializeField, Range(0f, 1f)] private float collectVolume = 1f;
        [SerializeField, Range(0f, 1f)] private float damageVolume = 1f;
        [SerializeField, Range(0f, 1f)] private float deathVolume = 1f;

        private IEventBus _eventBus;
        private IHealth _health;

        [Inject]
        public void Construct(IEventBus eventBus)
        {
            _eventBus = eventBus;
        }

        private void Awake()
        {
            // Get or create audio source
            if (audioSource == null)
            {
                audioSource = GetComponent<AudioSource>();
                if (audioSource == null)
                {
                    audioSource = gameObject.AddComponent<AudioSource>();
                }
            }

            // Get health component for damage/death events
            _health = GetComponent<IHealth>();
        }

        private void OnEnable()
        {
            // Subscribe to events
            _eventBus?.Subscribe<PlayerDeathEvent>(OnPlayerDeath);
            ScoreCollectable.OnScoreCollected += OnScoreCollected;

            // Subscribe to health events if available
            if (_health != null)
            {
                _health.OnHealthChanged += OnHealthChanged;
                _health.OnDeath += OnDeath;
            }
        }

        private void OnDisable()
        {
            // Unsubscribe from events
            _eventBus?.Unsubscribe<PlayerDeathEvent>(OnPlayerDeath);
            ScoreCollectable.OnScoreCollected -= OnScoreCollected;

            // Unsubscribe from health events
            if (_health != null)
            {
                _health.OnHealthChanged -= OnHealthChanged;
                _health.OnDeath -= OnDeath;
            }
        }

        /// <summary>
        /// Play jump sound - call this from player movement/jump scripts
        /// </summary>
        public void PlayJumpSound()
        {
            PlaySound(jumpSound, jumpVolume);
        }

        /// <summary>
        /// Handle score collection sound
        /// </summary>
        private void OnScoreCollected(int scoreAmount)
        {
            PlaySound(collectSound, collectVolume);
        }

        /// <summary>
        /// Handle health change for damage sound
        /// </summary>
        private void OnHealthChanged(int currentHp, int maxHp)
        {
            // Play damage sound when health decreases
            // Note: You might want to track previous health to ensure this only plays on damage
            PlaySound(damageSound, damageVolume);
        }

        /// <summary>
        /// Handle death sound from health component
        /// </summary>
        private void OnDeath()
        {
            PlaySound(deathSound, deathVolume);
        }

        /// <summary>
        /// Handle death sound from event system
        /// </summary>
        private void OnPlayerDeath(PlayerDeathEvent deathEvent)
        {
            PlaySound(deathSound, deathVolume);
        }

        /// <summary>
        /// Play damage sound - call this directly for specific damage events
        /// </summary>
        public void PlayDamageSound()
        {
            PlaySound(damageSound, damageVolume);
        }

        /// <summary>
        /// Play death sound - call this directly for specific death events
        /// </summary>
        public void PlayDeathSound()
        {
            PlaySound(deathSound, deathVolume);
        }

        /// <summary>
        /// Generic method to play any sound with volume
        /// </summary>
        private void PlaySound(AudioClip clip, float volume = 1f)
        {
            if (audioSource != null && clip != null)
            {
                audioSource.PlayOneShot(clip, volume);
            }
        }

        /// <summary>
        /// Set master volume for all player sounds
        /// </summary>
        public void SetMasterVolume(float volume)
        {
            if (audioSource != null)
            {
                audioSource.volume = Mathf.Clamp01(volume);
            }
        }
    }
}

using System;
using System.Collections;
using Collectables.Score;
using Health.Interfaces;
using Player.Components;
using UnityEngine;
using VContainer;

namespace Player.Services
{
    /// <summary>
    /// Service responsible for calculating and awarding bonus points based on remaining health
    /// </summary>
    public class HealthBonusService : MonoBehaviour
    {
        [Header("Bonus Settings")]
        [SerializeField] private int pointsPerHp = 100;
        [SerializeField] private float drainDelay = 0.2f;
        [SerializeField] private float drainInterval = 0.1f;

        [Header("Audio")]
        [SerializeField] private AudioClip bonusDrainSfx;

        private IScoreService _scoreService;
        private PlayerHealthController _healthController;
        private IHealthView _healthView;
        private AudioSource _audioSource;

        [Inject]
        public void Construct(IScoreService scoreService)
        {
            _scoreService = scoreService;
        }

        private void Awake()
        {
            _audioSource = GetComponent<AudioSource>();
            if (!_audioSource)
            {
                _audioSource = gameObject.AddComponent<AudioSource>();
            }

            _healthController = GetComponent<PlayerHealthController>();
        }
        private void Start()
        {
            _healthView = _healthController?.HealthView;

        }

        /// <summary>
        /// Calculate and award bonus points for remaining health
        /// </summary>
        /// <param name="onComplete">Callback when bonus calculation is complete</param>
        public void CalculateHealthBonus(System.Action onComplete = null)
        {
            if (_healthController == null)
            {
                Debug.LogWarning("[HealthBonusService] No PlayerHealthController found");
                onComplete?.Invoke();
                return;
            }

            int remainingHp = _healthController.CurrentHp;

            if (remainingHp <= 0)
            {
                Debug.Log("[HealthBonusService] No remaining HP for bonus");
                onComplete?.Invoke();
                return;
            }

            Debug.Log($"[HealthBonusService] Starting health bonus calculation: {remainingHp} HP remaining");
            StartCoroutine(DrainHealthForBonus(remainingHp, onComplete));
        }

        private IEnumerator DrainHealthForBonus(int remainingHp, System.Action onComplete)
        {
            // Wait before starting the drain
            yield return new WaitForSeconds(drainDelay);

            int totalBonus = remainingHp * pointsPerHp;
            Debug.Log($"[HealthBonusService] Total bonus to award: {totalBonus} points");

            // Drain each HP point individually
            for (int i = 0; i < remainingHp; i++)
            {
                // Award points for this HP
                _scoreService?.AddScore(pointsPerHp);

                // Reduce health by 1 (visual only)
                _healthView?.UpdateDisplay(
                    Mathf.Max(_healthController.CurrentHp - i, 0), 
                    _healthController.MaxHp);

                // Play drain sound effect
                if (bonusDrainSfx && _audioSource)
                {
                    _audioSource.PlayOneShot(bonusDrainSfx);
                }


                // Wait before next drain
                yield return new WaitForSeconds(drainInterval);
            }

            onComplete?.Invoke();
        }
    }
}

using System;
using System.Collections;
using Collectables.Score;
using Health.Interfaces;
using Player.Components;
using UnityEngine;
using VContainer;

namespace Player.Services
{
    /// <summary>
    /// Service responsible for calculating and awarding bonus points based on remaining health
    /// </summary>
    public class HealthBonusService : MonoBehaviour
    {
        [Header("Bonus Settings")]
        [SerializeField] private int pointsPerHp = 100;
        [SerializeField] private float drainDelay = 0.2f;
        [SerializeField] private float drainInterval = 0.1f;

        [Header("Audio")]
        [SerializeField] private AudioClip bonusDrainSfx;

        private IScoreService _scoreService;
        private PlayerHealthController _healthController;
        private IHealthView _healthView;
        private AudioSource _audioSource;

        [Inject]
        public void Construct(IScoreService scoreService)
        {
            _scoreService = scoreService;
        }

        private void Awake()
        {
            _audioSource = GetComponent<AudioSource>();
            if (!_audioSource)
            {
                _audioSource = gameObject.AddComponent<AudioSource>();
            }

            _healthController = GetComponent<PlayerHealthController>();
        }
        private void Start()
        {
            _healthView = _healthController?.HealthView;

        }

        /// <summary>
        /// Calculate and award bonus points for remaining health
        /// </summary>
        /// <param name="onComplete">Callback when bonus calculation is complete</param>
        public void CalculateHealthBonus(System.Action onComplete = null)
        {
            if (_healthController == null)
            {
                Debug.LogWarning("[HealthBonusService] No PlayerHealthController found");
                onComplete?.Invoke();
                return;
            }

            int remainingHp = _healthController.CurrentHp;

            if (remainingHp <= 0)
            {
                Debug.Log("[HealthBonusService] No remaining HP for bonus");
                onComplete?.Invoke();
                return;
            }

            Debug.Log($"[HealthBonusService] Starting health bonus calculation: {remainingHp} HP remaining");
            StartCoroutine(DrainHealthForBonus(remainingHp, onComplete));
        }

        private IEnumerator DrainHealthForBonus(int remainingHp, System.Action onComplete)
        {
            // Wait before starting the drain
            yield return new WaitForSeconds(drainDelay);

            int totalBonus = remainingHp * pointsPerHp;
            Debug.Log($"[HealthBonusService] Total bonus to award: {totalBonus} points");

            // Drain each HP point individually
            for (int i = 0; i < remainingHp; i++)
            {
                // Award points for this HP
                _scoreService?.AddScore(pointsPerHp);

                // Calculate remaining health after draining this HP point
                int healthAfterDrain = remainingHp - (i + 1);

                // Update health display (visual only)
                _healthView?.UpdateDisplay(healthAfterDrain, _healthController.MaxHp);

                // Play drain sound effect
                if (bonusDrainSfx && _audioSource)
                {
                    _audioSource.PlayOneShot(bonusDrainSfx);
                }

                Debug.Log($"[HealthBonusService] HP drained: {i + 1}/{remainingHp}, Remaining: {healthAfterDrain}, Points awarded: {pointsPerHp}");

                // Wait before next drain
                yield return new WaitForSeconds(drainInterval);
            }

            onComplete?.Invoke();
        }
    }
}

using Core;
using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection.Services;
using UnityEngine;
using VContainer;
using VContainer.Unity;

namespace StartGame
{
    /// <summary>
    ///     VContainer lifetime scope for Start Game screen
    /// </summary>
    public class StartGameLifetimeScope : LifetimeScope
    {
        protected override void Configure(IContainerBuilder builder)
        {
            Debug.Log("[StartGameLifetimeScope] Configuring start game DI container...");

            // Register core services needed for start screen
            builder.Register<IEventBus, EventBus>(Lifetime.Singleton);
            builder.Register<IGameDataRepository, JsonGameDataRepository>(Lifetime.Singleton);
            builder.Register<IGameDataService, GameDataService>(Lifetime.Singleton);
            builder.Register<IAutoSaveService, AutoSaveService>(Lifetime.Singleton);
            builder.Register<ISceneLoadService, SceneLoadService>(Lifetime.Singleton);

            // Register core game management components
            builder.RegisterComponentInHierarchy<GameFlowManager>();
            builder.RegisterComponentInHierarchy<GameDataCoordinator>();

            // Register start game specific components
            builder.RegisterComponentInHierarchy<StartGameListener>();

            Debug.Log("[StartGameLifetimeScope] Start game DI container configured successfully.");
        }
    }
}
