option.value:
using Audio.Data;
using Audio.Interfaces;
using UnityEngine;
using VContainer;

namespace Audio.Controllers
{
    /// <summary>
    /// Player-specific audio controller following Single Responsibility Principle
    /// Only handles player audio events, delegates actual audio playing to AudioService
    /// </summary>
    public class PlayerAudioController : MonoBehaviour
    {
        [Header("Player Sound Events")]
        [SerializeField] private SoundData jumpSound;
        [SerializeField] private SoundData collectSound;
        [SerializeField] private SoundData deathSound;
        [SerializeField] private SoundData damageSound;

        private IAudioService _audioService;

        [Inject]
        public void Construct(IAudioService audioService)
        {
            _audioService = audioService;
        }

        public void PlayJumpSound()
        {
            PlaySoundData(jumpSound);
        }

        public void PlayCollectSound()
        {
            PlaySoundData(collectSound);
        }

        public void PlayDeathSound()
        {
            PlaySoundData(deathSound);
        }

        public void PlayDamageSound()
        {
            PlaySoundData(damageSound);
        }

        public void PlayCollectSoundAtPosition(Vector3 position)
        {
            PlaySoundDataAtPosition(collectSound, position);
        }

        private void PlaySoundData(SoundData soundData)
        {
            if (soundData?.clip && _audioService != null)
            {
                _audioService.PlaySound(
                    soundData.clip, 
                    soundData.GetRandomizedVolume(), 
                    soundData.GetRandomizedPitch()
                );
            }
        }

        private void PlaySoundDataAtPosition(SoundData soundData, Vector3 position)
        {
            if (soundData?.clip && _audioService != null)
            {
                _audioService.PlaySoundAtPosition(
                    soundData.clip, 
                    position,
                    soundData.GetRandomizedVolume(), 
                    soundData.GetRandomizedPitch()
                );
            }
        }
    }
}

using Audio.Interfaces;
using Audio.Services;
using LevelSelection;
using Player.Components;
using Player.Services;
using UnityEngine;
using VContainer;
using VContainer.Unity;

namespace Audio.DI
{
    /// <summary>
    ///     Dependency injection configuration for audio system
    ///     Follows Dependency Inversion Principle by registering interfaces
    ///     Registers ALL classes that need audio injection
    ///     AudioService is now a proper singleton managed by VContainer
    /// </summary>
    public class AudioLifetimeScope : LifetimeScope
    {
        [SerializeField] private AudioService audioServicePrefab;

        protected override void Configure(IContainerBuilder builder)
        {
            // Register AudioService as singleton - VContainer will manage its lifecycle
            if (audioServicePrefab != null)
            {
                builder.RegisterComponentInNewPrefab(audioServicePrefab, Lifetime.Singleton)
                    .As<IAudioService>()
                    .AsSelf();
            }
            else
            {
                // Fallback: register as singleton without prefab
                builder.Register<AudioService>(Lifetime.Singleton)
                    .As<IAudioService>()
                    .AsSelf();
            }

            RegisterAudioDependentComponents(builder);
        }

        private static void RegisterAudioDependentComponents(IContainerBuilder builder)
        {
            // Register Player Components that need audio
            builder.RegisterComponentInHierarchy<PlayerSoundController>();

            // Register Player Services that need audio
            builder.RegisterComponentInHierarchy<HealthBonusService>();

            // Register Level Selection Controller that needs audio
            builder.RegisterComponentInHierarchy<LevelSelectionController>();

            // Register Level Components that need audio
            builder.RegisterComponentInHierarchy<EndLevelZone>();
        }
    }
}

