using Audio.Services;
using LevelSelection;
using Player.Components;
using Player.Services;
using UnityEngine;
using VContainer;
using VContainer.Unity;

namespace Audio.DI
{
    /// <summary>
    ///     Dependency injection configuration for audio system
    ///     Follows Dependency Inversion Principle by registering interfaces
    ///     Registers ALL classes that need audio injection
    ///     AudioService is now a proper singleton managed by VContainer
    /// </summary>
    public class AudioLifetimeScope : LifetimeScope
    {
        protected override void Configure(IContainerBuilder builder)
        {
            // Register AudioService as singleton - VContainer will manage its lifecycle
            builder.Register<AudioService>(Lifetime.Singleton)
                .As<IAudioService>()
                .AsSelf();

            RegisterAudioDependentComponents(builder);
        }

        private static void RegisterAudioDependentComponents(IContainerBuilder builder)
        {
            // Register Player Components that need audio
            builder.RegisterComponentInHierarchy<PlayerSoundController>();

            // Register Player Services that need audio
            builder.RegisterComponentInHierarchy<HealthBonusService>();

            // Register Level Selection Controller that needs audio
            builder.RegisterComponentInHierarchy<LevelSelectionController>();

            // Register Level Components that need audio
            builder.RegisterComponentInHierarchy<EndLevelZone>();
        }
    }
}

using UnityEngine;

namespace Audio.Data
{
    /// <summary>
    /// Configuration data for audio settings (Single Responsibility)
    /// </summary>
    [System.Serializable]
    public class AudioConfig
    {
        [Header("Volume Settings")]
        [Range(0f, 1f)] public float masterVolume = 1f;
        [Range(0f, 1f)] public float sfxVolume = 1f;
        [Range(0f, 1f)] public float musicVolume = 1f;

        [Header("Audio Source Settings")]
        public int maxConcurrentSounds = 10;
        public float defaultPitch = 1f;

        [Header("3D Audio Settings")]
        public float spatialBlend = 0f; // 0 = 2D, 1 = 3D
        public float minDistance = 1f;
        public float maxDistance = 500f;
    }
}

using System;
using UnityEngine;
using Random = UnityEngine.Random;

namespace Audio.Data
{
    /// <summary>
    ///     Simple sound data container without ScriptableObjects
    /// </summary>
    [Serializable]
    public class SoundData
    {
        [Header("Audio Clip")] public AudioClip clip;

        [Header("Settings")] [Range(0f, 1f)] public float volume = 1f;

        [Range(0.1f, 3f)] public float pitch = 1f;
        [Range(0f, 0.5f)] public float randomVolumeVariance;
        [Range(0f, 0.5f)] public float randomPitchVariance;

        [Header("3D Audio")] public bool use3D;

        /// <summary>
        ///     Get randomized volume within variance range
        /// </summary>
        public float GetRandomizedVolume() => volume + Random.Range(-randomVolumeVariance, randomVolumeVariance);

        /// <summary>
        ///     Get randomized pitch within variance range
        /// </summary>
        public float GetRandomizedPitch() => pitch + Random.Range(-randomPitchVariance, randomPitchVariance);
    }
}

using System;
using UnityEngine;
using UnityEngine.Audio;
using Random = UnityEngine.Random;

namespace Audio.Data
{
    /// <summary>
    ///     Simple sound data container with mixer channel support
    /// </summary>
    [Serializable]
    public class SoundData
    {
        [Header("Audio Clip")] public AudioClip clip;

        [Header("Mixer Settings")] public AudioMixerGroup mixerGroup;

        [Header("Settings")] [Range(0f, 1f)] public float volume = 1f;

        [Range(0.1f, 3f)] public float pitch = 1f;
        [Range(0f, 0.5f)] public float randomVolumeVariance = 0f;
        [Range(0f, 0.5f)] public float randomPitchVariance = 0f;

        [Header("3D Audio")] public bool use3D = false;

        /// <summary>
        ///     Get randomized volume within variance range
        /// </summary>
        public float GetRandomizedVolume() =>
            volume + Random.Range(-randomVolumeVariance, randomVolumeVariance);

        /// <summary>
        ///     Get randomized pitch within variance range
        /// </summary>
        public float GetRandomizedPitch() =>
            pitch + Random.Range(-randomPitchVariance, randomPitchVariance);
    }
}

using UnityEngine;

namespace Audio.Interfaces
{
    /// <summary>
    ///     Main audio service interface for playing sounds and music
    /// </summary>
    public interface IAudioService
    {
        void PlaySound(AudioClip clip, float volume = 1f, float pitch = 1f);
        void PlaySoundAtPosition(AudioClip clip, Vector3 position, float volume = 1f, float pitch = 1f);
        void PlayMusic(AudioClip clip, float volume = 1f, bool loop = true);
        void StopMusic();
        void SetMasterVolume(float volume);
        void SetSFXVolume(float volume);
        void SetMusicVolume(float volume);
    }
}

using Audio.Data;
using UnityEngine;

namespace Audio.Interfaces
{
    /// <summary>
    ///     Main audio service interface for playing sounds and music with mixer support
    /// </summary>
    public interface IAudioService
    {
        void PlaySound(AudioClip clip, float volume = 1f, float pitch = 1f);
        void PlaySound(SoundData soundData);
        void PlaySoundAtPosition(AudioClip clip, Vector3 position, float volume = 1f, float pitch = 1f);
        void PlaySoundAtPosition(SoundData soundData, Vector3 position);
        void PlayMusic(AudioClip clip, float volume = 1f, bool loop = true);
        void StopMusic();
        void SetMasterVolume(float volume);
        void SetSFXVolume(float volume);
        void SetMusicVolume(float volume);
    }
}

using System.Collections.Generic;
using Audio.Data;
using Audio.Interfaces;
using UnityEngine;

namespace Audio.Services
{
    /// <summary>
    ///     Main audio service implementation following SOLID principles
    ///     Single Responsibility: Manages all audio playback
    ///     Open/Closed: Extensible through interfaces
    ///     Dependency Inversion: Depends on abstractions
    ///     Now managed as a singleton through VContainer instead of DontDestroyOnLoad
    /// </summary>
    public class AudioService : IAudioService
    {
        [SerializeField] private AudioConfig audioConfig = new AudioConfig();

        private AudioSource _musicSource;
        private List<AudioSource> _sfxSources;
        private int _currentSfxIndex = 0;

        private float _masterVolume = 1f;
        private float _sfxVolume = 1f;
        private float _musicVolume = 1f;

        private void Awake()
        {
            // Remove DontDestroyOnLoad - VContainer will manage singleton lifecycle
            InitializeAudioSources();
            LoadVolumeSettings();
        }

        public void PlaySound(AudioClip clip, float volume = 1f, float pitch = 1f)
        {
            if (!clip) return;

            AudioSource availableSource = GetAvailableSfxSource();
            if (!availableSource) return;

            ConfigureSfxSource(availableSource, clip, volume, pitch);
            availableSource.Play();
        }

        public void PlaySound(SoundData soundData)
        {
            if (soundData?.clip == null) return;

            AudioSource availableSource = GetAvailableSfxSource();
            if (!availableSource) return;

            ConfigureSfxSourceWithSoundData(availableSource, soundData);
            availableSource.Play();
        }

        public void PlaySoundAtPosition(AudioClip clip, Vector3 position, float volume = 1f, float pitch = 1f)
        {
            if (!clip) return;

            // Create temporary audio source at position for 3D audio
            GameObject tempAudioObject = new($"TempAudio_{clip.name}");
            tempAudioObject.transform.position = position;

            AudioSource tempSource = tempAudioObject.AddComponent<AudioSource>();
            ConfigureSfxSource(tempSource, clip, volume, pitch, true);

            tempSource.Play();

            // Destroy after clip finishes
            Destroy(tempAudioObject, clip.length);
        }

        public void PlaySoundAtPosition(SoundData soundData, Vector3 position)
        {
            if (soundData?.clip == null) return;

            // Create temporary audio source at position for 3D audio
            GameObject tempAudioObject = new($"TempAudio_{soundData.clip.name}");
            tempAudioObject.transform.position = position;

            AudioSource tempSource = tempAudioObject.AddComponent<AudioSource>();
            ConfigureSfxSourceWithSoundData(tempSource, soundData, true);

            tempSource.Play();

            // Destroy after clip finishes
            Destroy(tempAudioObject, soundData.clip.length);
        }

        public void PlayMusic(AudioClip clip, float volume = 1f, bool loop = true)
        {
            if (!clip || !_musicSource) return;

            _musicSource.clip = clip;
            _musicSource.volume = volume * _musicVolume * _masterVolume;
            _musicSource.loop = loop;
            _musicSource.Play();
        }

        public void StopMusic()
        {
            if (_musicSource)
            {
                _musicSource.Stop();
            }
        }

        public void SetMasterVolume(float volume)
        {
            _masterVolume = Mathf.Clamp01(volume);
            UpdateAllVolumes();
        }

        public void SetSFXVolume(float volume)
        {
            _sfxVolume = Mathf.Clamp01(volume);
        }

        public void SetMusicVolume(float volume)
        {
            _musicVolume = Mathf.Clamp01(volume);
            if (_musicSource && _musicSource.isPlaying)
            {
                _musicSource.volume = _musicSource.volume * _musicVolume * _masterVolume;
            }
        }

        private void InitializeAudioSources()
        {
            // Create dedicated music source
            _musicSource = gameObject.AddComponent<AudioSource>();
            _musicSource.loop = true;
            _musicSource.playOnAwake = false;

            // Create pool of SFX sources for concurrent sounds
            _sfxSources = new List<AudioSource>();
            int maxSources = audioConfig.maxConcurrentSounds;

            for (int i = 0; i < maxSources; i++)
            {
                AudioSource sfxSource = gameObject.AddComponent<AudioSource>();
                sfxSource.playOnAwake = false;
                sfxSource.loop = false;
                _sfxSources.Add(sfxSource);
            }
        }

        private void LoadVolumeSettings()
        {
            _masterVolume = audioConfig.masterVolume;
            _sfxVolume = audioConfig.sfxVolume;
            _musicVolume = audioConfig.musicVolume;
        }

        private AudioSource GetAvailableSfxSource()
        {
            // Find an available (not playing) source
            foreach (AudioSource source in _sfxSources)
            {
                if (!source.isPlaying)
                {
                    return source;
                }
            }

            // If all sources are busy, use round-robin
            AudioSource roundRobinSource = _sfxSources[_currentSfxIndex];
            _currentSfxIndex = (_currentSfxIndex + 1) % _sfxSources.Count;
            return roundRobinSource;
        }

        private void ConfigureSfxSource(AudioSource source, AudioClip clip, float volume, float pitch,
            bool is3D = false)
        {
            source.clip = clip;
            source.volume = volume * _sfxVolume * _masterVolume;
            source.pitch = pitch;

            source.spatialBlend = is3D ? 1f : audioConfig.spatialBlend;
            source.minDistance = audioConfig.minDistance;
            source.maxDistance = audioConfig.maxDistance;
        }

        private void ConfigureSfxSourceWithSoundData(AudioSource source, SoundData soundData, bool forceIs3D = false)
        {
            source.clip = soundData.clip;
            source.volume = soundData.GetRandomizedVolume() * _sfxVolume * _masterVolume;
            source.pitch = soundData.GetRandomizedPitch();

            // Set mixer group if provided
            if (soundData.mixerGroup != null)
            {
                source.outputAudioMixerGroup = soundData.mixerGroup;
            }

            bool is3D = forceIs3D || soundData.use3D;
            source.spatialBlend = is3D ? 1f : audioConfig.spatialBlend;
            source.minDistance = audioConfig.minDistance;
            source.maxDistance = audioConfig.maxDistance;
        }

        private void UpdateAllVolumes()
        {
            // Update music volume
            if (_musicSource && _musicSource.isPlaying)
            {
                float currentMusicVolume = _musicSource.volume / (_musicVolume * _masterVolume);
                _musicSource.volume = currentMusicVolume * _musicVolume * _masterVolume;
            }
        }
    }
}

using System.Collections.Generic;
using Audio.Data;
using Audio.Interfaces;
using UnityEngine;

namespace Audio.Services
{
    /// <summary>
    ///     Main audio service implementation following SOLID principles
    ///     Single Responsibility: Manages all audio playback
    ///     Open/Closed: Extensible through interfaces
    ///     Dependency Inversion: Depends on abstractions
    ///     Now managed as a singleton through VContainer instead of DontDestroyOnLoad
    /// </summary>
    public class AudioService : IAudioService
    {
        [SerializeField] private AudioConfig audioConfig = new AudioConfig();

        private AudioSource _musicSource;
        private List<AudioSource> _sfxSources;
        private int _currentSfxIndex = 0;

        private float _masterVolume = 1f;
        private float _sfxVolume = 1f;
        private float _musicVolume = 1f;

        private void Awake()
        {
            // Remove DontDestroyOnLoad - VContainer will manage singleton lifecycle
            InitializeAudioSources();
            LoadVolumeSettings();
        }

        public void PlaySound(AudioClip clip, float volume = 1f, float pitch = 1f)
        {
