        [Inject]
        public void Construct(IAudioService audioService)
        {
            _audioService = audioService;
        }

        public void Initialize(AudioSource audioSource)
        {
            // No longer needed with the new system, but kept for interface compatibility
        }

        public void SetSounds(SoundData selectionSound, SoundData lockedSound)
        {
            this.selectionSound = selectionSound;
            this.lockedSound = lockedSound;
        }

        public void PlaySelectionSound()
        {
            if (selectionSound?.clip && _audioService != null)
            {
                _audioService.PlaySound(
                    selectionSound.clip,
                    selectionSound.GetRandomizedVolume(),
                    selectionSound.GetRandomizedPitch()
                );
            }
        }

        public void PlayLockedSound()
        {
            if (lockedSound?.clip && _audioService != null)
            {
                _audioService.PlaySound(
                    lockedSound.clip,
                    lockedSound.GetRandomizedVolume(),
                    lockedSound.GetRandomizedPitch()
                );
            }
        }
    }
