using Core;
using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection.Services;
using UnityEngine;
using VContainer;
using VContainer.Unity;

namespace StartGame
{
    /// <summary>
    ///     VContainer lifetime scope for Start Game screen
    /// </summary>
    public class StartGameLifetimeScope : LifetimeScope
    {
        protected override void Configure(IContainerBuilder builder)
        {
            Debug.Log("[StartGameLifetimeScope] Configuring start game DI container...");

            // Register core services needed for start screen
            builder.Register<IEventBus, EventBus>(Lifetime.Singleton);
            builder.Register<IGameDataRepository, JsonGameDataRepository>(Lifetime.Singleton);
            builder.Register<IGameDataService, GameDataService>(Lifetime.Singleton);
            builder.Register<IAutoSaveService, AutoSaveService>(Lifetime.Singleton);
            builder.Register<ISceneLoadService, SceneLoadService>(Lifetime.Singleton);

            // Register core game management components
            builder.RegisterComponentInHierarchy<GameFlowManager>();
            builder.RegisterComponentInHierarchy<GameDataCoordinator>();

            // Register start game specific components
            builder.RegisterComponentInHierarchy<StartGameListener>();

            Debug.Log("[StartGameLifetimeScope] Start game DI container configured successfully.");
        }
    }
}
