namespace Core.DI
{
    public class GameLifetimeScope : LifetimeScope
    {
        protected override void Awake()
        {
            Debug.Log("[GameLifetimeScope] Awake called.");
            AddToAutoInject<FireballWeapon>();
            base.Awake();
        }

        public void AddToAutoInject<T>() where T : Component
        {

            var components = FindObjectsByType<T>(FindObjectsInactive.Include, FindObjectsSortMode.None);

            foreach (T comp in components)
            {
                autoInjectGameObjects.Add(comp.gameObject);
            }
        }

        protected override void Configure(IContainerBuilder builder)
        {
            Debug.Log("[GameLifetimeScope] Configuring DI container...");

            // Register Core Services
            builder.Register<IEventBus, EventBus>(Lifetime.Singleton);

            // Register EventBus as IEventPublisher as well (since EventBus implements IEventPublisher)
            builder.Register<IEventPublisher>(resolver => resolver.Resolve<IEventBus>(), Lifetime.Singleton);

            builder.Register<IGameDataRepository, JsonGameDataRepository>(Lifetime.Singleton);
            builder.Register<IGameDataService, GameDataService>(Lifetime.Singleton);
            builder.Register<IAutoSaveService, AutoSaveService>(Lifetime.Singleton);
            builder.Register<IScoreService, ScoreService>(Lifetime.Singleton);

            // Level Selection components are now handled by LevelSelectionLifetimeScope

            // Register Player Services
            builder.Register<IPlayerLivesService>(resolver
                => new PlayerLivesService(
                    resolver.Resolve<IGameDataService>(),
                    resolver.Resolve<IEventBus>()
                ), Lifetime.Singleton);

            // Game Management
            builder.RegisterComponentInHierarchy<GameFlowManager>();
            builder.RegisterComponentInHierarchy<GameDataCoordinator>();
            // Pooling System
            builder.RegisterComponentInHierarchy<PoolManager>().As<IPoolService>();

            // Weapons
            builder.RegisterComponentInHierarchy<AxeWeapon>();
            builder.RegisterComponentInHierarchy<WeaponManagerService>();

            // Health
            builder.RegisterComponentInHierarchy<PlayerHealthController>();
            builder.RegisterComponentInHierarchy<PlayerLivesUIController>();
            builder.RegisterComponentInHierarchy<PlayerAnimationController>();

            // Score System
            builder.RegisterComponentInHierarchy<ScoreController>();
            builder.RegisterComponentInHierarchy<PopupTextService>();


            Debug.Log("[GameLifetimeScope] DI container configured successfully.");
        }
    }
}

using System;
using UnityEngine;

namespace Core.Data
{
    [Serializable]
    public class GameData
    {
        [Header("Player Data")] public const int MaxLives = 3;
        [Header("Player Data")] public int lives = 3;

        public int score;

        [Header("Level Progress")] public string currentLevel = "Level_01";

        public float bestTime = float.MaxValue;

        [Header("Power-ups")] public bool hasFireball;

        public bool hasAxe;

        [Header("Settings")] public float musicVolume = 1.0f;

        public float sfxVolume = 1.0f;

        [Header("Collectables")] public int fruitCollected;

        // Constructor for easy initialization
        public GameData()
        {
            // Default values are set by field initializers
        }

        // Copy constructor
        public GameData(GameData other)
        {
            lives = other.lives;
            score = other.score;
            currentLevel = other.currentLevel;
            bestTime = other.bestTime;
            hasFireball = other.hasFireball;
            hasAxe = other.hasAxe;
            musicVolume = other.musicVolume;
            sfxVolume = other.sfxVolume;
            fruitCollected = other.fruitCollected;
        }

        // Reset to default values
        public void Reset()
        {
            lives = MaxLives;
            score = 0;
            currentLevel = "Level_01";
            bestTime = float.MaxValue;
            hasFireball = false;
            hasAxe = false;
            musicVolume = 1.0f;
            sfxVolume = 1.0f;
            fruitCollected = 0;
        }

        private static GameData CreateDefaultData() =>
            new()
            {
                lives = MaxLives,
                score = 0,
                currentLevel = "Level_01",
                bestTime = float.MaxValue,
                hasFireball = false,
                hasAxe = false,
                musicVolume = 1.0f,
                sfxVolume = 1.0f,
                fruitCollected = 0
            };
    }
}

using System;
using System.Collections.Generic;
using UnityEngine;

namespace Core.Data
{
    [Serializable]
    public class GameData
    {
        [Header("Player Data")] public const int MaxLives = 3;
        [Header("Player Data")] public int lives = 3;

        public int score;

        [Header("Level Progress")] public string currentLevel = "Level_01";
        
        // Level Selection Data
        public List<string> unlockedLevels = new List<string> { "Level_01" };
        public int selectedLevelIndex = 0;
        public Dictionary<string, float> levelBestTimes = new Dictionary<string, float>();
        public Dictionary<string, bool> levelCompleted = new Dictionary<string, bool>();

        public float bestTime = float.MaxValue;

        [Header("Power-ups")] public bool hasFireball;

        public bool hasAxe;

        [Header("Settings")] public float musicVolume = 1.0f;

        public float sfxVolume = 1.0f;

        [Header("Collectables")] public int fruitCollected;

        // Constructor for easy initialization
        public GameData()
        {
            // Default values are set by field initializers
        }

        // Copy constructor
        public GameData(GameData other)
        {
            lives = other.lives;
            score = other.score;
            currentLevel = other.currentLevel;
            bestTime = other.bestTime;
            hasFireball = other.hasFireball;
            hasAxe = other.hasAxe;
            musicVolume = other.musicVolume;
            sfxVolume = other.sfxVolume;
            fruitCollected = other.fruitCollected;
            unlockedLevels = new List<string>(other.unlockedLevels);
            selectedLevelIndex = other.selectedLevelIndex;
            levelBestTimes = new Dictionary<string, float>(other.levelBestTimes);
            levelCompleted = new Dictionary<string, bool>(other.levelCompleted);
        }

        // Reset to default values
        public void Reset()
        {
            lives = MaxLives;
            score = 0;
            currentLevel = "Level_01";
            bestTime = float.MaxValue;
            hasFireball = false;
            hasAxe = false;
            musicVolume = 1.0f;
            sfxVolume = 1.0f;
            fruitCollected = 0;
            
            // Reset level selection data
            unlockedLevels = new List<string> { "Level_01" };
            selectedLevelIndex = 0;
            levelBestTimes = new Dictionary<string, float>();
            levelCompleted = new Dictionary<string, bool>();
        }

        private static GameData CreateDefaultData() =>
            new()
            {
                lives = MaxLives,
                score = 0,
                currentLevel = "Level_01",
                bestTime = float.MaxValue,
                hasFireball = false,
                hasAxe = false,
                musicVolume = 1.0f,
                sfxVolume = 1.0f,
                fruitCollected = 0,
                unlockedLevels = new List<string> { "Level_01" },
                selectedLevelIndex = 0,
                levelBestTimes = new Dictionary<string, float>(),
                levelCompleted = new Dictionary<string, bool>()
            };
    }
}

using UnityEngine;

namespace Core.Events
{
    public enum GameState
    {
        MainMenu,
        Loading,
        Playing,
        Paused,
        GameOver,
        Victory,
        Restarting
    }

    public struct GameStateChangedEvent
    {
        public GameState PreviousState;
        public GameState NewState;
        public float Timestamp;
    }

    public struct PlayerHealthChangedEvent
    {
        public int CurrentHp;
        public int MaxHp;
        public int Damage;
        public float Timestamp;
    }

    public struct PlayerLivesChangedEvent
    {
        public int PreviousLives;
        public int CurrentLives;
        public int MaxLives;
        public float Timestamp;
    }

    public struct ScoreChangedEvent
    {
        public Vector3 Position;
        public int ScoreAmount; // Delta amount
        public int TotalScore; // Overall score
    }

    // Base event structure for consistency
    public interface IGameEvent
    {
        float Timestamp { get; }
    }

    public struct PlayerDeathEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public Vector3 DeathPosition;
    }

    public struct LevelStartedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
    }

    public struct LevelCompletedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
        public float CompletionTime;
    }


    public struct GameOverEvent : IGameEvent
    {
        public float Timestamp { get; set; }
    }
}

using UnityEngine;

namespace Core.Events
{
    public enum GameState
    {
        MainMenu,
        LevelSelection, // Added LevelSelection state
        Loading,
        Playing,
        Paused,
        GameOver,
        Victory,
        Restarting
    }

    public struct GameStateChangedEvent
    {
        public GameState PreviousState;
        public GameState NewState;
        public float Timestamp;
    }

    public struct PlayerHealthChangedEvent
    {
        public int CurrentHp;
        public int MaxHp;
        public int Damage;
        public float Timestamp;
    }

    public struct PlayerLivesChangedEvent
    {
        public int PreviousLives;
        public int CurrentLives;
        public int MaxLives;
        public float Timestamp;
    }

    public struct ScoreChangedEvent
    {
        public Vector3 Position;
        public int ScoreAmount; // Delta amount
        public int TotalScore; // Overall score
    }

    // Base event structure for consistency
    public interface IGameEvent
    {
        float Timestamp { get; }
    }

    public struct PlayerDeathEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public Vector3 DeathPosition;
    }

    public struct LevelStartedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
    }

    public struct LevelCompletedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
        public float CompletionTime;
    }


    public struct GameOverEvent : IGameEvent
    {
        public float Timestamp { get; set; }
    }

    // Level Selection Events
    public struct LevelSelectedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
        public int LevelIndex;
    }

    public struct LevelNavigationEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public int PreviousIndex;
        public int NewIndex;
        public Vector2 Direction;
    }

    public struct ItemSelectScreenRequestedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
    }

    public struct LevelLoadRequestedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
        public string SceneName;
    }
}

using Core.Data;
using Core.Events;
using Core.Services;
using UnityEngine;
using VContainer;

namespace Core
{
    /// <summary>
    ///     Coordinates between game events and data storage.
    ///     Responsible only for translating events into data updates and managing save operations.
    /// </summary>
    public class GameDataCoordinator : MonoBehaviour
    {
        [SerializeField] private float autoSaveInterval = 30;
        private IAutoSaveService _autoSaveService;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private bool _isInitialized;

        #region VContainer Injection

        [Inject]
        public void Construct(
            IGameDataService gameDataService,
            IEventBus eventBus,
            IAutoSaveService autoSaveService)
        {
            _gameDataService = gameDataService;
            _eventBus = eventBus;
            _autoSaveService = autoSaveService;
            _isInitialized = true;


            // Initialize after dependencies are injected
            Initialize();
        }

        #endregion

        #region Initialization

        private void Initialize()
        {
            // Subscribe to events - only subscribe to events that affect game data
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted); // For best time tracking
            _eventBus?.Subscribe<LevelStartedEvent>(OnLevelStarted); // For current level tracking
            _eventBus?.Subscribe<PlayerLivesChangedEvent>(OnLivesChanged); // For lives tracking
            _eventBus?.Subscribe<GameStateChangedEvent>(OnGameStateChanged); // For game state changes

            _eventBus?.Subscribe<PlayerDeathEvent>(OnPlayerDied);

            if (_gameDataService != null)
                _gameDataService.OnDataChanged += OnGameDataChanged;

            if (_autoSaveService != null)
            {
                _autoSaveService.OnSaveRequested += SaveData;
                _autoSaveService.SaveInterval = autoSaveInterval;
                _autoSaveService.IsEnabled = true;
            }
        }

        #endregion

        #region Private Methods

        private void SaveData()
        {
            _gameDataService?.SaveData();
        }

        #endregion

        #region Unity Lifecycle

        private void Start()
        {
            // If not yet initialized (dependencies not injected), wait
            if (!_isInitialized)
            {
            }
        }

        private void Update()
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.Update();
        }

        private void OnDestroy()
        {
            if (!_isInitialized) return;

            // Unsubscribe from events
            if (_eventBus != null)
            {
                _eventBus.Unsubscribe<PlayerDeathEvent>(OnPlayerDied);
                _eventBus.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
                _eventBus.Unsubscribe<LevelStartedEvent>(OnLevelStarted);
                _eventBus.Unsubscribe<PlayerLivesChangedEvent>(OnLivesChanged);
                _eventBus.Unsubscribe<GameStateChangedEvent>(OnGameStateChanged);
            }


            if (_gameDataService != null)
                _gameDataService.OnDataChanged -= OnGameDataChanged;

            if (_autoSaveService != null)
            {
                _autoSaveService.OnSaveRequested -= SaveData;
                _autoSaveService.ForceSave();
            }
        }

        private void OnApplicationPause(bool pauseStatus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationPause(pauseStatus);
        }

        private void OnApplicationFocus(bool hasFocus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationFocus(hasFocus);
        }

        #endregion

        #region Event Handlers

        private void OnPlayerDied(PlayerDeathEvent deathEvent)
        {
            _autoSaveService?.RequestSave();
        }

        private void OnLevelCompleted(LevelCompletedEvent levelEvent)
        {
            _gameDataService?.UpdateBestTime(levelEvent.CompletionTime);
            _autoSaveService?.RequestSave();
        }

        private void OnGameStateChanged(GameStateChangedEvent stateEvent)
        {
            // Handle data operations based on state changes
            if (stateEvent.NewState is GameState.Victory or GameState.GameOver)
            {
                _autoSaveService?.ForceSave();
            }
        }
        private void OnLevelStarted(LevelStartedEvent levelEvent)
        {
            _gameDataService?.UpdateCurrentLevel(levelEvent.LevelName);
        }
        private void OnLivesChanged(PlayerLivesChangedEvent livesEvent)
        {
            Debug.Log(
                $"[GameDataCoordinator] Received PlayerLivesChangedEvent: CurrentLives={livesEvent.CurrentLives}");

            _gameDataService?.UpdateLives(livesEvent.CurrentLives);
            Debug.Log($"[GameDataCoordinator] Updated GameData lives to: {_gameDataService?.CurrentData.lives}");
        }

        private void OnGameDataChanged(GameData newData)
        {
            _autoSaveService?.RequestSave();
        }

        #endregion

        #region Public API

        public GameData GetCurrentData()
        {
            if (!_isInitialized || _gameDataService == null) return null;

            return _gameDataService.CurrentData;
        }

        public void ResetAllData()
        {
            if (!_isInitialized) return;

            _gameDataService?.ResetAllData();
        }

        #endregion

    }
}

using Core.Data;
using Core.Events;
using Core.Services;
using UnityEngine;
using VContainer;

namespace Core
{
    /// <summary>
    ///     Coordinates between game events and data storage.
    ///     Responsible only for translating events into data updates and managing save operations.
    /// </summary>
    public class GameDataCoordinator : MonoBehaviour
    {
        [SerializeField] private float autoSaveInterval = 30;
        private IAutoSaveService _autoSaveService;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private bool _isInitialized;

        #region VContainer Injection

        [Inject]
        public void Construct(
            IGameDataService gameDataService,
            IEventBus eventBus,
            IAutoSaveService autoSaveService)
        {
            _gameDataService = gameDataService;
            _eventBus = eventBus;
            _autoSaveService = autoSaveService;
            _isInitialized = true;


            // Initialize after dependencies are injected
            Initialize();
        }

        #endregion

        #region Initialization

        private void Initialize()
        {
            // Subscribe to events - only subscribe to events that affect game data
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted); // For best time tracking
            _eventBus?.Subscribe<LevelStartedEvent>(OnLevelStarted); // For current level tracking
            _eventBus?.Subscribe<PlayerLivesChangedEvent>(OnLivesChanged); // For lives tracking
            _eventBus?.Subscribe<GameStateChangedEvent>(OnGameStateChanged); // For game state changes

            _eventBus?.Subscribe<PlayerDeathEvent>(OnPlayerDied);

            // Level Selection Events
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelNavigationEvent>(OnLevelNavigation);

            if (_gameDataService != null)
                _gameDataService.OnDataChanged += OnGameDataChanged;

            if (_autoSaveService != null)
            {
                _autoSaveService.OnSaveRequested += SaveData;
                _autoSaveService.SaveInterval = autoSaveInterval;
                _autoSaveService.IsEnabled = true;
            }
        }

        #endregion

        #region Private Methods

        private void SaveData()
        {
            _gameDataService?.SaveData();
        }

        #endregion

        #region Unity Lifecycle

        private void Start()
        {
            // If not yet initialized (dependencies not injected), wait
            if (!_isInitialized)
            {
            }
        }

        private void Update()
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.Update();
        }

        private void OnDestroy()
        {
            if (!_isInitialized) return;

            // Unsubscribe from events
            if (_eventBus != null)
            {
                _eventBus.Unsubscribe<PlayerDeathEvent>(OnPlayerDied);
                _eventBus.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
                _eventBus.Unsubscribe<LevelStartedEvent>(OnLevelStarted);
                _eventBus.Unsubscribe<PlayerLivesChangedEvent>(OnLivesChanged);
                _eventBus.Unsubscribe<GameStateChangedEvent>(OnGameStateChanged);
            }


            if (_gameDataService != null)
                _gameDataService.OnDataChanged -= OnGameDataChanged;

            if (_autoSaveService != null)
            {
                _autoSaveService.OnSaveRequested -= SaveData;
                _autoSaveService.ForceSave();
            }
        }

        private void OnApplicationPause(bool pauseStatus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationPause(pauseStatus);
        }

        private void OnApplicationFocus(bool hasFocus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationFocus(hasFocus);
        }

        #endregion

        #region Event Handlers

        private void OnPlayerDied(PlayerDeathEvent deathEvent)
        {
            _autoSaveService?.RequestSave();
        }

        private void OnLevelCompleted(LevelCompletedEvent levelEvent)
        {
            _gameDataService?.UpdateBestTime(levelEvent.CompletionTime);
            _autoSaveService?.RequestSave();
        }

        private void OnGameStateChanged(GameStateChangedEvent stateEvent)
        {
            // Handle data operations based on state changes
            if (stateEvent.NewState is GameState.Victory or GameState.GameOver)
            {
                _autoSaveService?.ForceSave();
            }
        }
        private void OnLevelStarted(LevelStartedEvent levelEvent)
        {
            _gameDataService?.UpdateCurrentLevel(levelEvent.LevelName);
        }
        private void OnLivesChanged(PlayerLivesChangedEvent livesEvent)
        {
            Debug.Log(
                $"[GameDataCoordinator] Received PlayerLivesChangedEvent: CurrentLives={livesEvent.CurrentLives}");

            _gameDataService?.UpdateLives(livesEvent.CurrentLives);
            Debug.Log($"[GameDataCoordinator] Updated GameData lives to: {_gameDataService?.CurrentData.lives}");
        }

        private void OnGameDataChanged(GameData newData)
        {
            _autoSaveService?.RequestSave();
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            // Update selected level index in game data
            var gameData = _gameDataService?.GetGameData();
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                gameData.currentLevel = levelEvent.LevelName;
            }
            Debug.Log($"[GameDataCoordinator] Level selected: {levelEvent.LevelName}");
        }

        private void OnLevelNavigation(LevelNavigationEvent navigationEvent)
        {
            // Update selected level index in game data
            var gameData = _gameDataService?.GetGameData();
            if (gameData != null)
            {
                gameData.selectedLevelIndex = navigationEvent.NewIndex;
            }
            Debug.Log($"[GameDataCoordinator] Level navigation: from {navigationEvent.PreviousIndex} to {navigationEvent.NewIndex}");
        }

        #endregion

        #region Public API

        public GameData GetCurrentData()
        {
            if (!_isInitialized || _gameDataService == null) return null;

            return _gameDataService.CurrentData;
        }

        public void ResetAllData()
        {
            if (!_isInitialized) return;

            _gameDataService?.ResetAllData();
        }

        #endregion

    }
}

using Enemies.Interfaces;
using Player.Components;
using UnityEngine;

namespace Enemies.Behaviors
{
    // Emits an event when the player is within a certain distance
    [RequireComponent(typeof(Rigidbody2D))]
    public class FrogProximityTrigger : MonoBehaviour, ITrigger
    {
        [SerializeField] private LayerMask groundLayer;
        [SerializeField] private float triggerDistance = 3f;
        [SerializeField] private float jumpCooldown = 2f;
        [SerializeField] private int checkEveryNFrames = 1;

        private int _frameCounter;
        private bool _grounded;
        private float _lastTriggerTime;
        private Transform _player;

        private void Start()
        {
            _player = PlayerLocator.PlayerTransform;
        }

        private void OnCollisionEnter2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = true;
        }

        private void OnCollisionExit2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = false;
        }

        public bool IsTriggered { get; private set; }

        public void CheckTrigger()
        {
            if (++_frameCounter % checkEveryNFrames != 0 || !_player) return;

            Vector2 toPlayer = _player.position - transform.position;
            float sqrDist = toPlayer.sqrMagnitude;
            float sqrTrigger = triggerDistance * triggerDistance;

            if (sqrDist < sqrTrigger && Time.time - _lastTriggerTime > jumpCooldown && _grounded)
            {
                IsTriggered = true;
                _lastTriggerTime = Time.time;
            }
            else
            {
                IsTriggered = false;
            }
        }
    }
}

