namespace Enemies.Core
{
    // Manages periodic checks for all triggers in the scene
    public class TriggerManager : MonoBehaviour
    {
        private TriggerGroup[] _triggerGroups;

        private void Awake()
        {
            _triggerGroups = FindObjectsOfType<MonoBehaviour>().OfType<TriggerGroup>().ToArray();
        }

        private void Update()
        {
            foreach (var group in _triggerGroups)
            {
                group.Update();
            }
        }
    }
