using System.Collections;
using Core.Events;
using UnityEngine;
using UnityEngine.UI;
using VContainer;

namespace LevelSelection
{
    public class ItemSelectScreen : MonoBehaviour
    {
        [Header("UI References")]
        public Image itemSelectImage;
        public string itemSelectSpritePath = "item select screen";

        [Header("Display Settings")]
        public float displayDuration = 2f;
        public bool waitForInput = true;

        [Header("Audio")]
        public AudioClip confirmSound;

        private IEventBus _eventBus;
        private AudioSource _audioSource;
        private bool _isWaitingForInput = false;
        private string _pendingLevelName;
        private string _pendingSceneName;
        private System.Action _onComplete;

        [Inject]
        public void Construct(IEventBus eventBus)
        {
            _eventBus = eventBus;
        }

        private void Awake()
        {
            _audioSource = GetComponent<AudioSource>();
            if (_audioSource == null)
            {
                _audioSource = gameObject.AddComponent<AudioSource>();
            }

            // Load the item select screen sprite
            if (itemSelectImage != null && string.IsNullOrEmpty(itemSelectSpritePath) == false)
            {
                Sprite itemSelectSprite = Resources.Load<Sprite>(itemSelectSpritePath);
                if (itemSelectSprite != null)
                {
                    itemSelectImage.sprite = itemSelectSprite;
                }
            }

            // Start hidden
            gameObject.SetActive(false);
        }

        public void ShowItemSelect(string levelName, string sceneName, System.Action onComplete = null)
        {
            _pendingLevelName = levelName;
            _pendingSceneName = sceneName;
            _onComplete = onComplete;

            gameObject.SetActive(true);

            if (waitForInput)
            {
                _isWaitingForInput = true;
            }
            else
            {
                StartCoroutine(AutoProgressAfterDelay());
            }
        }

        private IEnumerator AutoProgressAfterDelay()
        {
            yield return new WaitForSeconds(displayDuration);
            ConfirmAndProceed();
        }

        private void Update()
        {
            if (_isWaitingForInput)
            {
                // Check for Enter/Confirm input
                if (Input.GetKeyDown(KeyCode.Return) || Input.GetKeyDown(KeyCode.Space) || Input.GetKeyDown(KeyCode.KeypadEnter))
                {
                    ConfirmAndProceed();
                }
            }
        }

        private void ConfirmAndProceed()
        {
            if (_audioSource && confirmSound)
            {
                _audioSource.PlayOneShot(confirmSound);
            }

            _isWaitingForInput = false;
            gameObject.SetActive(false);

            // Publish level load request event
            _eventBus?.Publish(new LevelLoadRequestedEvent
            {
                Timestamp = Time.time,
                LevelName = _pendingLevelName,
                SceneName = _pendingSceneName
            });

            _onComplete?.Invoke();
        }

        public bool IsWaitingForInput => _isWaitingForInput;
    }
}

using System;
using UnityEngine;

namespace LevelSelection
{
    [Serializable]
    public class LevelData
    {
        public string levelName;
        public string sceneName;
        public Vector2 mapPosition;
        public bool isUnlocked;
        public bool isCompleted;
        public float bestTime;
        public Sprite levelIcon;
        public string displayName;
        public int levelIndex;

        public LevelData()
        {
            isUnlocked = false;
            isCompleted = false;
            bestTime = float.MaxValue;
        }
    }

    public class LevelDataBuilder
    {
        private LevelData _levelData;

        public LevelDataBuilder()
        {
            _levelData = new LevelData();
        }

        public LevelDataBuilder WithName(string levelName)
        {
            _levelData.levelName = levelName;
            return this;
        }

        public LevelDataBuilder WithDisplayName(string displayName)
        {
            _levelData.displayName = displayName;
            return this;
        }

        public LevelDataBuilder WithScene(string sceneName)
        {
            _levelData.sceneName = sceneName;
            return this;
        }

        public LevelDataBuilder AtPosition(Vector2 position)
        {
            _levelData.mapPosition = position;
            return this;
        }

        public LevelDataBuilder WithIndex(int index)
        {
            _levelData.levelIndex = index;
            return this;
        }

        public LevelDataBuilder WithIcon(Sprite icon)
        {
            _levelData.levelIcon = icon;
            return this;
        }

        public LevelDataBuilder Unlocked(bool unlocked = true)
        {
            _levelData.isUnlocked = unlocked;
            return this;
        }

        public LevelDataBuilder Completed(bool completed = true)
        {
            _levelData.isCompleted = completed;
            return this;
        }

        public LevelDataBuilder WithBestTime(float time)
        {
            _levelData.bestTime = time;
            return this;
        }

        public LevelData Build()
        {
            return _levelData;
        }
    }

    public static class LevelDataFactory
    {
        public static LevelData CreateFromGameObject(GameObject levelObject, int index)
        {
            var levelPoint = levelObject.GetComponent<LevelPoint>();
            if (levelPoint == null)
            {
                Debug.LogWarning($"GameObject {levelObject.name} doesn't have a LevelPoint component");
                return null;
            }

            return new LevelDataBuilder()
                .WithName(levelPoint.levelName)
                .WithDisplayName(levelPoint.displayName)
                .WithScene(levelPoint.sceneName)
                .AtPosition(levelObject.transform.position)
                .WithIndex(index)
                .WithIcon(levelPoint.levelIcon)
                .Build();
        }

        public static LevelData CreateDefault(string name, string scene, Vector2 position, int index)
        {
            return new LevelDataBuilder()
                .WithName(name)
                .WithDisplayName(name)
                .WithScene(scene)
                .AtPosition(position)
                .WithIndex(index)
                .Unlocked(index == 0) // First level is always unlocked
                .Build();
        }
    }
}

using System;
using UnityEngine;

namespace LevelSelection
{
    [Serializable]
    public class LevelData
    {
        public string levelName;
        public string sceneName;
        public Vector2 mapPosition;
        public bool isUnlocked;
        public bool isCompleted;
        public float bestTime;
        public Sprite levelIcon;
        public string displayName;
        public int levelIndex;

        public LevelData()
        {
            isUnlocked = false;
            isCompleted = false;
            bestTime = float.MaxValue;
        }
    }

    public class LevelDataBuilder
    {
        private LevelData _levelData;

        public LevelDataBuilder()
        {
            _levelData = new LevelData();
        }

        public LevelDataBuilder WithName(string levelName)
        {
            _levelData.levelName = levelName;
            return this;
        }

        public LevelDataBuilder WithDisplayName(string displayName)
        {
            _levelData.displayName = displayName;
            return this;
        }

        public LevelDataBuilder WithScene(string sceneName)
        {
            _levelData.sceneName = sceneName;
            return this;
        }

        public LevelDataBuilder AtPosition(Vector2 position)
        {
            _levelData.mapPosition = position;
            return this;
        }

        public LevelDataBuilder WithIndex(int index)
        {
            _levelData.levelIndex = index;
            return this;
        }

        public LevelDataBuilder WithIcon(Sprite icon)
        {
            _levelData.levelIcon = icon;
            return this;
        }

        public LevelDataBuilder Unlocked(bool unlocked = true)
        {
            _levelData.isUnlocked = unlocked;
            return this;
        }

        public LevelDataBuilder Completed(bool completed = true)
        {
            _levelData.isCompleted = completed;
            return this;
        }

        public LevelDataBuilder WithBestTime(float time)
        {
            _levelData.bestTime = time;
            return this;
        }

        public LevelData Build()
        {
            return _levelData;
        }
    }

    public static class LevelDataFactory
    {
        public static LevelData CreateFromGameObject(GameObject levelObject, int index)
        {
            var levelPoint = levelObject.GetComponent<LevelPoint>();
            if (levelPoint == null)
            {
                Debug.LogWarning($"GameObject {levelObject.name} doesn't have a LevelPoint component");
                return null;
            }

            return new LevelDataBuilder()
                .WithName(levelPoint.levelName)
                .WithDisplayName(levelPoint.displayName)
                .WithScene(levelPoint.sceneName)
                .AtPosition(levelObject.transform.position) // Uses the actual GameObject position from scene
                .WithIndex(index)
                .WithIcon(levelPoint.levelIcon)
                .Build();
        }

        public static LevelData CreateDefault(string name, string scene, Vector2 position, int index)
        {
            return new LevelDataBuilder()
                .WithName(name)
                .WithDisplayName(name)
                .WithScene(scene)
                .AtPosition(position) // Uses the provided position (from transform)
                .WithIndex(index)
                .Unlocked(index == 0) // First level is always unlocked
                .Build();
        }

        /// <summary>
        /// Creates level data using only the GameObject's transform position and name
        /// Useful when LevelPoint component is not available
        /// </summary>
        public static LevelData CreateFromTransform(GameObject levelObject, int index, string sceneName = null)
        {
            return new LevelDataBuilder()
                .WithName(levelObject.name)
                .WithDisplayName(levelObject.name)
                .WithScene(sceneName ?? levelObject.name) // Use object name as scene if not provided
                .AtPosition(levelObject.transform.position) // Always use the actual transform position
                .WithIndex(index)
                .Unlocked(index == 0)
                .Build();
        }
    }
}

using UnityEngine;

namespace LevelSelection
{
    public class LevelPoint : MonoBehaviour
    {
        [Header("Level Configuration")]
        public string levelName;
        public string displayName;
        public string sceneName;
        public Sprite levelIcon;

        [Header("Visual Configuration")]
        public SpriteRenderer iconRenderer;
        public SpriteRenderer lockRenderer;
        public Color unlockedColor = Color.white;
        public Color lockedColor = Color.gray;

        [Header("Animation")]
        public float pulseSpeed = 2f;
        public float pulseScale = 1.1f;

        private bool _isUnlocked;
        private bool _isSelected;
        private Vector3 _originalScale;

        private void Awake()
        {
            _originalScale = transform.localScale;

            if (iconRenderer && levelIcon)
            {
                iconRenderer.sprite = levelIcon;
            }
        }

        public void SetUnlocked(bool unlocked)
        {
            _isUnlocked = unlocked;
            UpdateVisuals();
        }

        public void SetSelected(bool selected)
        {
            _isSelected = selected;
            UpdateVisuals();
        }

        private void UpdateVisuals()
        {
            if (iconRenderer)
            {
                iconRenderer.color = _isUnlocked ? unlockedColor : lockedColor;
            }

            if (lockRenderer)
            {
                lockRenderer.gameObject.SetActive(!_isUnlocked);
            }
        }

        private void Update()
        {
            if (_isSelected && _isUnlocked)
            {
                // Pulse animation for selected level
                float scale = 1f + Mathf.Sin(Time.time * pulseSpeed) * (pulseScale - 1f) * 0.5f;
                transform.localScale = _originalScale * scale;
            }
            else
            {
                transform.localScale = _originalScale;
            }
        }

        public bool IsUnlocked => _isUnlocked;
        public bool IsSelected => _isSelected;
    }
}

using System.Collections.Generic;
using UnityEngine;

namespace LevelSelection
{
    /// <summary>
    /// Director pattern implementation for building level data structures
    /// </summary>
    public class LevelSelectionDirector
    {
        public List<LevelData> BuildLevelData(List<GameObject> levelGameObjects)
        {
            var levelDataList = new List<LevelData>();

            for (int i = 0; i < levelGameObjects.Count; i++)
            {
                var levelObject = levelGameObjects[i];
                if (levelObject == null) continue;

                var levelData = LevelDataFactory.CreateFromGameObject(levelObject, i);
                if (levelData != null)
                {
                    levelDataList.Add(levelData);
                }
                else
                {
                    // Fallback - create basic level data from GameObject name
                    var fallbackData = LevelDataFactory.CreateDefault(
                        levelObject.name,
                        levelObject.name,
                        levelObject.transform.position,
                        i
                    );
                    levelDataList.Add(fallbackData);
                }
            }

            return levelDataList;
        }

        public LevelData BuildSingleLevel(GameObject levelObject, int index)
        {
            if (levelObject == null) return null;

            return LevelDataFactory.CreateFromGameObject(levelObject, index) ??
                   LevelDataFactory.CreateDefault(levelObject.name, levelObject.name, levelObject.transform.position, index);
        }
    }
}

using System.Collections.Generic;
using UnityEngine;

namespace LevelSelection
{
    /// <summary>
    /// Director pattern implementation for building level data structures
    /// </summary>
    public class LevelSelectionDirector
    {
        public List<LevelData> BuildLevelData(List<GameObject> levelGameObjects)
        {
            var levelDataList = new List<LevelData>();

            for (int i = 0; i < levelGameObjects.Count; i++)
            {
                var levelObject = levelGameObjects[i];
                if (levelObject == null) continue;

                // Try to create from LevelPoint component first
                var levelData = LevelDataFactory.CreateFromGameObject(levelObject, i);

                if (levelData == null)
                {
                    // Fallback - create from transform position and object name
                    Debug.Log($"Creating level data from transform for {levelObject.name} at position {levelObject.transform.position}");
                    levelData = LevelDataFactory.CreateFromTransform(levelObject, i);
                }

                if (levelData != null)
                {
                    Debug.Log($"Level {i}: {levelData.levelName} at position {levelData.mapPosition}");
                    levelDataList.Add(levelData);
                }
            }

            return levelDataList;
        }

        public LevelData BuildSingleLevel(GameObject levelObject, int index)
        {
            if (levelObject == null) return null;

            // Try LevelPoint component first, fallback to transform-based creation
            return LevelDataFactory.CreateFromGameObject(levelObject, index) ??
                   LevelDataFactory.CreateFromTransform(levelObject, index);
        }
    }
}

using System.Collections.Generic;
using System.Linq;
using Core.Data;
using Core.Events;
using UnityEngine;
using UnityEngine.SceneManagement;
using VContainer;

namespace LevelSelection
{
    public class LevelSelectionManager : MonoBehaviour
    {
        [Header("Level Configuration")]
        public List<GameObject> levelGameObjects = new List<GameObject>();
        public Transform levelContainer;

        [Header("Components")]
        public LevelSelector levelSelector;
        public ItemSelectScreen itemSelectScreen;
        public NESCrossfade crossfade;

        [Header("Input Settings")]
        public KeyCode upKey = KeyCode.UpArrow;
        public KeyCode downKey = KeyCode.DownArrow;
        public KeyCode leftKey = KeyCode.LeftArrow;
        public KeyCode rightKey = KeyCode.RightArrow;
        public KeyCode confirmKey = KeyCode.Return;

        private List<LevelData> _levelData;
        private List<LevelPoint> _levelPoints;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private bool _isActive = false;
        private LevelSelectionDirector _director;

        [Inject]
        public void Construct(IEventBus eventBus, IGameDataService gameDataService)
        {
            _eventBus = eventBus;
            _gameDataService = gameDataService;

            SubscribeToEvents();
        }

        private void Awake()
        {
            _director = new LevelSelectionDirector();
            InitializeLevelSelection();
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        private void InitializeLevelSelection()
        {
            // Auto-discover level objects if container is specified
            if (levelContainer != null && levelGameObjects.Count == 0)
            {
                for (int i = 0; i < levelContainer.childCount; i++)
                {
                    var child = levelContainer.GetChild(i);
                    if (child.GetComponent<LevelPoint>() != null)
                    {
                        levelGameObjects.Add(child.gameObject);
                    }
                }
            }

            // Use director pattern to build level data
            _levelData = _director.BuildLevelData(levelGameObjects);
            _levelPoints = levelGameObjects.Select(go => go.GetComponent<LevelPoint>()).ToList();

            LoadLevelProgressFromGameData();

            var gameData = _gameDataService?.GetGameData();
            int selectedIndex = gameData?.selectedLevelIndex ?? 0;

            levelSelector?.Initialize(_levelData, _levelPoints, selectedIndex);
        }

        private void LoadLevelProgressFromGameData()
        {
            var gameData = _gameDataService?.GetGameData();
            if (gameData == null) return;

            // Update unlock status based on game data
            for (int i = 0; i < _levelData.Count; i++)
            {
                var level = _levelData[i];
                level.isUnlocked = gameData.unlockedLevels.Contains(level.levelName);

                if (gameData.levelCompleted.TryGetValue(level.levelName, out bool completed))
                {
                    level.isCompleted = completed;
                }

                if (gameData.levelBestTimes.TryGetValue(level.levelName, out float bestTime))
                {
                    level.bestTime = bestTime;
                }
            }
        }

        public void ActivateLevelSelection()
        {
            _isActive = true;
            gameObject.SetActive(true);
        }

        public void DeactivateLevelSelection()
        {
            _isActive = false;
            gameObject.SetActive(false);
        }

        private void Update()
        {
            if (!_isActive || levelSelector == null) return;

            HandleInput();
        }

        private void HandleInput()
        {
            Vector2 direction = Vector2.zero;

            if (Input.GetKeyDown(upKey))
                direction = Vector2.up;
            else if (Input.GetKeyDown(downKey))
                direction = Vector2.down;
            else if (Input.GetKeyDown(leftKey))
                direction = Vector2.left;
            else if (Input.GetKeyDown(rightKey))
                direction = Vector2.right;

            if (direction != Vector2.zero)
            {
                levelSelector.Navigate(direction);
                SaveSelectedLevel();
            }

            if (Input.GetKeyDown(confirmKey))
            {
                levelSelector.SelectCurrentLevel();
            }
        }

        private void SaveSelectedLevel()
        {
            var gameData = _gameDataService?.GetGameData();
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelSelector.CurrentIndex;
                _gameDataService?.SaveGameData();
            }
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            var selectedLevel = _levelData.FirstOrDefault(l => l.levelName == levelEvent.LevelName);
            if (selectedLevel == null) return;

            // Update current level in game data
            var gameData = _gameDataService?.GetGameData();
            if (gameData != null)
            {
                gameData.currentLevel = selectedLevel.levelName;
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                _gameDataService?.SaveGameData();
            }

            // Show item select screen
            _eventBus?.Publish(new ItemSelectScreenRequestedEvent
            {
                Timestamp = Time.time,
                LevelName = selectedLevel.levelName
            });

            itemSelectScreen?.ShowItemSelect(selectedLevel.levelName, selectedLevel.sceneName);
        }

        private void OnLevelLoadRequested(LevelLoadRequestedEvent loadEvent)
        {
            // Start crossfade and load scene
            crossfade?.FadeOutAndIn(
                onMiddle: () => {
                    // Load the scene
                    SceneManager.LoadScene(loadEvent.SceneName);
                },
                onComplete: () => {
                    // Publish level started event
                    _eventBus?.Publish(new LevelStartedEvent
                    {
                        Timestamp = Time.time,
                        LevelName = loadEvent.LevelName
                    });
                }
            );
        }

        private void OnLevelCompleted(LevelCompletedEvent completedEvent)
        {
            // Update level completion status and unlock next level
            var gameData = _gameDataService?.GetGameData();
            if (gameData == null) return;

            // Mark level as completed
            gameData.levelCompleted[completedEvent.LevelName] = true;

            // Update best time
            if (!gameData.levelBestTimes.ContainsKey(completedEvent.LevelName) || 
                completedEvent.CompletionTime < gameData.levelBestTimes[completedEvent.LevelName])
            {
                gameData.levelBestTimes[completedEvent.LevelName] = completedEvent.CompletionTime;
            }

            // Unlock next level
            int completedIndex = _levelData.FindIndex(l => l.levelName == completedEvent.LevelName);
            if (completedIndex >= 0 && completedIndex + 1 < _levelData.Count)
            {
                string nextLevelName = _levelData[completedIndex + 1].levelName;
                if (!gameData.unlockedLevels.Contains(nextLevelName))
                {
                    gameData.unlockedLevels.Add(nextLevelName);
                }
            }

            _gameDataService?.SaveGameData();

            // Refresh level data if we're still in level selection
            if (_isActive)
            {
                LoadLevelProgressFromGameData();
                levelSelector?.Initialize(_levelData, _levelPoints, levelSelector.CurrentIndex);
            }
        }

        public List<LevelData> GetLevelData() => _levelData;
        public bool IsActive => _isActive;
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using Core.Events;
using UnityEngine;
using VContainer;

namespace LevelSelection
{
    public class LevelSelector : MonoBehaviour
    {
        [Header("Selector Configuration")]
        public GameObject selectorObject;
        public float moveSpeed = 5f;
        public AudioClip navigationSound;
        public AudioClip selectionSound;
        public AudioClip lockedSound;

        [Header("Grid Navigation")]
        public int gridWidth = 4;
        public float snapThreshold = 0.1f;

        private List<LevelData> _availableLevels;
        private List<LevelPoint> _levelPoints;
        private int _currentIndex = 0;
        private bool _isMoving = false;
        private Vector3 _targetPosition;
        private IEventBus _eventBus;
        private AudioSource _audioSource;

        [Inject]
        public void Construct(IEventBus eventBus)
        {
            _eventBus = eventBus;
        }

        private void Awake()
        {
            _audioSource = GetComponent<AudioSource>();
            if (_audioSource == null)
            {
                _audioSource = gameObject.AddComponent<AudioSource>();
            }
        }

        public void Initialize(List<LevelData> levels, List<LevelPoint> levelPoints, int selectedIndex = 0)
        {
            _availableLevels = levels;
            _levelPoints = levelPoints;
            _currentIndex = Mathf.Clamp(selectedIndex, 0, levels.Count - 1);

            UpdateLevelStates();
            MoveToCurrentLevel(true); // Instant move on initialization
        }

        public void Navigate(Vector2 direction)
        {
            if (_isMoving || _availableLevels == null || _availableLevels.Count == 0)
                return;

            int newIndex = CalculateNewIndex(direction);

            if (newIndex != _currentIndex && newIndex >= 0 && newIndex < _availableLevels.Count)
            {
                int previousIndex = _currentIndex;
                _currentIndex = newIndex;

                PlaySound(navigationSound);
                UpdateSelection();
                MoveToCurrentLevel();

                _eventBus?.Publish(new LevelNavigationEvent
                {
                    Timestamp = Time.time,
                    PreviousIndex = previousIndex,
                    NewIndex = _currentIndex,
                    Direction = direction
                });
            }
        }

        private int CalculateNewIndex(Vector2 direction)
        {
            // Adventure Island III style navigation - mostly horizontal with some vertical
            int currentRow = _currentIndex / gridWidth;
            int currentCol = _currentIndex % gridWidth;

            if (Mathf.Abs(direction.x) > Mathf.Abs(direction.y))
            {
                // Horizontal movement
                if (direction.x > 0) // Right
                {
                    return Mathf.Min(_currentIndex + 1, _availableLevels.Count - 1);
                }
                else // Left
                {
                    return Mathf.Max(_currentIndex - 1, 0);
                }
            }
            else
            {
                // Vertical movement
                if (direction.y > 0) // Up
                {
                    int newIndex = _currentIndex - gridWidth;
                    return newIndex >= 0 ? newIndex : _currentIndex;
                }
                else // Down
                {
                    int newIndex = _currentIndex + gridWidth;
                    return newIndex < _availableLevels.Count ? newIndex : _currentIndex;
                }
            }
        }

        public void SelectCurrentLevel()
        {
            if (_availableLevels == null || _currentIndex >= _availableLevels.Count)
                return;

            var selectedLevel = _availableLevels[_currentIndex];

            if (!selectedLevel.isUnlocked)
            {
                PlaySound(lockedSound);
                return;
            }

            PlaySound(selectionSound);

            _eventBus?.Publish(new LevelSelectedEvent
            {
                Timestamp = Time.time,
                LevelName = selectedLevel.levelName,
                LevelIndex = _currentIndex
            });
        }

        private void UpdateSelection()
        {
            for (int i = 0; i < _levelPoints.Count; i++)
            {
                if (_levelPoints[i] != null)
                {
                    _levelPoints[i].SetSelected(i == _currentIndex);
                }
            }
        }

        private void UpdateLevelStates()
        {
            for (int i = 0; i < _levelPoints.Count && i < _availableLevels.Count; i++)
            {
                if (_levelPoints[i] != null)
                {
                    _levelPoints[i].SetUnlocked(_availableLevels[i].isUnlocked);
                    _levelPoints[i].SetSelected(i == _currentIndex);
                }
            }
        }

        private void MoveToCurrentLevel(bool instant = false)
        {
            if (_currentIndex >= _levelPoints.Count || _levelPoints[_currentIndex] == null)
                return;

            _targetPosition = _levelPoints[_currentIndex].transform.position;

            if (instant)
            {
                selectorObject.transform.position = _targetPosition;
                _isMoving = false;
            }
            else
            {
                _isMoving = true;
            }
        }

        private void Update()
        {
            if (_isMoving && selectorObject != null)
            {
                selectorObject.transform.position = Vector3.MoveTowards(
                    selectorObject.transform.position, 
                    _targetPosition, 
                    moveSpeed * Time.deltaTime
                );

                if (Vector3.Distance(selectorObject.transform.position, _targetPosition) < snapThreshold)
                {
                    selectorObject.transform.position = _targetPosition;
                    _isMoving = false;
                }
            }
        }

        private void PlaySound(AudioClip clip)
        {
            if (_audioSource && clip)
            {
                _audioSource.PlayOneShot(clip);
            }
        }

        public int CurrentIndex => _currentIndex;
        public LevelData CurrentLevel => _availableLevels?[_currentIndex];
        public bool IsMoving => _isMoving;
    }
}

using System.Collections;
using UnityEngine;
using UnityEngine.UI;

namespace LevelSelection
{
    public class NESCrossfade : MonoBehaviour
    {
        [Header("Crossfade Configuration")]
        public Image fadeImage;
        public float fadeDuration = 1f;
        public Color fadeColor = Color.black;
        public AnimationCurve fadeCurve = AnimationCurve.EaseInOut(0, 0, 1, 1);

        [Header("NES Style Effect")]
        public bool useNESEffect = true;
        public Color[] nesColors = { Color.black, new Color(0.2f, 0.2f, 0.3f), new Color(0.1f, 0.1f, 0.2f) };
        public float colorFlickerSpeed = 10f;

        private bool _isFading = false;
        private System.Action _onFadeComplete;

        private void Awake()
        {
            if (fadeImage == null)
            {
                // Create fade image if not assigned
                GameObject fadeGO = new GameObject("FadeImage");
                fadeGO.transform.SetParent(transform, false);

                Canvas canvas = GetComponent<Canvas>();
                if (canvas == null)
                {
                    canvas = gameObject.AddComponent<Canvas>();
                    canvas.sortingOrder = 1000;
                    canvas.renderMode = RenderMode.ScreenSpaceOverlay;
                }

                fadeImage = fadeGO.AddComponent<Image>();
                RectTransform rect = fadeImage.rectTransform;
                rect.anchorMin = Vector2.zero;
                rect.anchorMax = Vector2.one;
                rect.sizeDelta = Vector2.zero;
                rect.anchoredPosition = Vector2.zero;
            }

            // Start with transparent
            SetAlpha(0f);
        }

        public void FadeOut(System.Action onComplete = null)
        {
            if (_isFading) return;

            _onFadeComplete = onComplete;
            StartCoroutine(FadeCoroutine(0f, 1f));
        }

        public void FadeIn(System.Action onComplete = null)
        {
            if (_isFading) return;

            _onFadeComplete = onComplete;
            StartCoroutine(FadeCoroutine(1f, 0f));
        }

        public void FadeOutAndIn(System.Action onMiddle = null, System.Action onComplete = null)
        {
            if (_isFading) return;

            StartCoroutine(FadeOutAndInCoroutine(onMiddle, onComplete));
        }

        private IEnumerator FadeCoroutine(float from, float to)
        {
            _isFading = true;
            float elapsed = 0f;

            while (elapsed < fadeDuration)
            {
                elapsed += Time.deltaTime;
                float progress = elapsed / fadeDuration;
                float curveValue = fadeCurve.Evaluate(progress);
                float alpha = Mathf.Lerp(from, to, curveValue);

                if (useNESEffect)
                {
                    SetNESStyleAlpha(alpha);
                }
                else
                {
                    SetAlpha(alpha);
                }

                yield return null;
            }

            if (useNESEffect)
            {
                SetNESStyleAlpha(to);
            }
            else
            {
                SetAlpha(to);
            }

            _isFading = false;
            _onFadeComplete?.Invoke();
            _onFadeComplete = null;
        }

        private IEnumerator FadeOutAndInCoroutine(System.Action onMiddle, System.Action onComplete)
        {
            // Fade out
            yield return StartCoroutine(FadeCoroutine(0f, 1f));

            // Middle action (like loading scene)
            onMiddle?.Invoke();

            // Small delay to ensure scene is loaded
            yield return new WaitForSeconds(0.1f);

            // Fade in
            yield return StartCoroutine(FadeCoroutine(1f, 0f));

            onComplete?.Invoke();
        }

        private void SetAlpha(float alpha)
        {
            if (fadeImage != null)
            {
                Color color = fadeColor;
                color.a = alpha;
                fadeImage.color = color;
            }
        }

        private void SetNESStyleAlpha(float alpha)
        {
            if (fadeImage != null && nesColors.Length > 0)
            {
                // Use NES-style color flickering for more authentic feel
                int colorIndex = Mathf.FloorToInt(Time.time * colorFlickerSpeed) % nesColors.Length;
                Color color = nesColors[colorIndex];
                color.a = alpha;
                fadeImage.color = color;
            }
        }

        public bool IsFading => _isFading;
    }
}

using Health.Interfaces;
using UnityEngine;

namespace Player.Components
{
    /// <summary>
    /// When enabled, instantly kills any damageable object the player collides with.
    /// Should be enabled only when the player is invincible.
    /// </summary>
    [DisallowMultipleComponent]
    public class PlayerInvincibleDamageDealer : MonoBehaviour, IDamageDealer
    {
        [SerializeField] private int damageAmount = 9999; // "Kill" value
        [SerializeField] private LayerMask targetLayers = ~0; // All by default

        public int GetDamageAmount() => damageAmount;

        private void OnCollisionEnter2D(Collision2D collision)
        {
            if (((1 << collision.gameObject.layer) & targetLayers) == 0)
                return;
            var damageable = collision.gameObject.GetComponent<IDamageable>();
            if (damageable != null)
            {
                damageable.Damage(damageAmount, gameObject);
            }
        }
    }
}

