option.value:
using System.Linq;
using UnityEditor.SceneTemplate;
using UnityEngine;
using UnityEngine.SceneManagement;

public class LevelsTemplatePipeline : ISceneTemplatePipeline
{
    public virtual bool IsValidTemplateForInstantiation(SceneTemplateAsset sceneTemplateAsset)
    {
        return true;
    }

    public virtual void BeforeTemplateInstantiation(SceneTemplateAsset sceneTemplateAsset, bool isAdditive, string sceneName)
    {

    }

    public virtual void AfterTemplateInstantiation(SceneTemplateAsset sceneTemplateAsset, Scene scene, bool isAdditive, string sceneName)
    {
        var sceneFolder = System.IO.Path.Combine("Assets", "Scenes");
        var sceneFiles = System.IO.Directory.GetFiles(sceneFolder, "Level*.unity");
        int levelPrefixLength = "Level".Length;
        int maxLevel = sceneFiles
            .Select(System.IO.Path.GetFileNameWithoutExtension)
            .Where(fileName => fileName.StartsWith("Level"))
            .Select(fileName => int.TryParse(fileName[levelPrefixLength..], out int num) ? num : 0)
            .DefaultIfEmpty(0)
            .Max();
        string newLevelName = $"Level{maxLevel + 1}";
        scene.name = newLevelName;
        SceneManager.SetActiveScene(scene);
        Debug.Log($"[LevelsTemplatePipeline] Instantiated new level: {newLevelName}");
    }
}

using System.Linq;
using UnityEditor.SceneTemplate;
using UnityEngine;
using UnityEngine.SceneManagement;

public class LevelsTemplatePipeline : ISceneTemplatePipeline
{
    public virtual bool IsValidTemplateForInstantiation(SceneTemplateAsset sceneTemplateAsset)
    {
        return true;
    }

    public virtual void BeforeTemplateInstantiation(SceneTemplateAsset sceneTemplateAsset, bool isAdditive, string sceneName)
    {

    }

    public virtual void AfterTemplateInstantiation(SceneTemplateAsset sceneTemplateAsset, Scene scene, bool isAdditive, string sceneName)
    {
        var sceneFolder = System.IO.Path.Combine("Assets", "Scenes");
        var sceneFiles = System.IO.Directory.GetFiles(sceneFolder, "Level*.unity");
        int levelPrefixLength = "Level".Length;
        int maxLevel = sceneFiles
            .Select(System.IO.Path.GetFileNameWithoutExtension)
            .Where(fileName => fileName.StartsWith("Level"))
            .Select(fileName => int.TryParse(fileName[levelPrefixLength..], out int num) ? num : 0)
            .DefaultIfEmpty(0)
            .Max();
        string newLevelName = $"Level{maxLevel + 1}";
        scene.name = newLevelName;
        SceneManager.SetActiveScene(scene);
        Debug.Log($"[LevelsTemplatePipeline] Instantiated new level: {newLevelName}");

        // Show save file dialog with the new scene name as default
#if UNITY_EDITOR
        string savePath = UnityEditor.EditorUtility.SaveFilePanel(
            "Save New Scene",
            sceneFolder,
            newLevelName,
            "unity"
        );
        if (!string.IsNullOrEmpty(savePath))
        {
            UnityEditor.SceneManagement.EditorSceneManager.SaveScene(scene, savePath);
            Debug.Log($"[LevelsTemplatePipeline] Scene saved to: {savePath}");
        }
#endif
    }
}

using System;
using System.Collections.Generic;
using LevelSelection;
using UnityEngine;

namespace Core.Data
{
    [Serializable]
    public class GameData
    {
        [Header("Player Data")] 
        public const int MaxLives = 3;
        public int lives = 3;
        public int score;
        public int maxScore; // Track highest score ever achieved

        [Header("Level Progress")] 
        public string currentLevel = "Level_01";

        // Level Selection Data
        public List<string> unlockedLevels = new() { "Level_01" };
        public List<string> completedLevels = new(); // Added for EndLevelZone
        public int selectedLevelIndex;

        // Enhanced timing and scoring data
        public Dictionary<string, float> LevelBestTimes = new();
        public Dictionary<string, int> LevelBestScores = new(); // Best score per level
        public Dictionary<string, bool> LevelCompleted = new();

        public float bestTime = float.MaxValue; // Overall best time

        [Header("Settings")] 
        public float musicVolume = 1.0f;
        public float sfxVolume = 1.0f;

        [Header("Collectables")] 
        public int fruitCollected;

        // Cached level discovery data
        public List<LevelData> cachedLevelData = new();
        public bool levelDataCacheValid = false;

        // Constructor for easy initialization
        public GameData()
        {
            // Default values are set by field initializers
        }

        // Copy constructor
        public GameData(GameData other)
        {
            lives = other.lives;
            score = other.score;
            maxScore = other.maxScore;
            currentLevel = other.currentLevel;
            bestTime = other.bestTime;
            musicVolume = other.musicVolume;
            sfxVolume = other.sfxVolume;
            fruitCollected = other.fruitCollected;
            unlockedLevels = new List<string>(other.unlockedLevels);
            completedLevels = new List<string>(other.completedLevels);
            selectedLevelIndex = other.selectedLevelIndex;
            LevelBestTimes = new Dictionary<string, float>(other.LevelBestTimes);
            LevelBestScores = new Dictionary<string, int>(other.LevelBestScores);
            LevelCompleted = new Dictionary<string, bool>(other.LevelCompleted);
            cachedLevelData = new List<LevelData>(other.cachedLevelData);
            levelDataCacheValid = other.levelDataCacheValid;
        }

        // Reset to default values
        public void Reset()
        {
            lives = MaxLives;
            score = 0;
            maxScore = 0;
            currentLevel = "Level_01";
            bestTime = float.MaxValue;
            musicVolume = 1.0f;
            sfxVolume = 1.0f;
            fruitCollected = 0;

            // Reset level selection data
            unlockedLevels = new List<string> { "Level_01" };
            completedLevels = new List<string>();
            selectedLevelIndex = 0;
            LevelBestTimes = new Dictionary<string, float>();
            LevelBestScores = new Dictionary<string, int>();
            LevelCompleted = new Dictionary<string, bool>();
            cachedLevelData = new List<LevelData>();
            levelDataCacheValid = false;
        }

        /// <summary>
        /// Update best time for a specific level
        /// </summary>
        public void UpdateLevelBestTime(string levelName, float completionTime)
        {
            if (string.IsNullOrEmpty(levelName)) return;

            if (!LevelBestTimes.ContainsKey(levelName) || completionTime < LevelBestTimes[levelName])
            {
                LevelBestTimes[levelName] = completionTime;

                // Update overall best time if this is better
                if (completionTime < bestTime)
                {
                    bestTime = completionTime;
                }
            }
        }

        /// <summary>
        /// Update best score for a specific level
        /// </summary>
        public void UpdateLevelBestScore(string levelName, int levelScore)
        {
            if (string.IsNullOrEmpty(levelName)) return;

            if (!LevelBestScores.ContainsKey(levelName) || levelScore > LevelBestScores[levelName])
            {
                LevelBestScores[levelName] = levelScore;
            }
        }

        /// <summary>
        /// Update max score if current score is higher
        /// </summary>
        public void UpdateMaxScore(int currentScore)
        {
            if (currentScore > maxScore)
            {
                maxScore = currentScore;
            }
        }

        /// <summary>
        /// Get best time for a specific level
        /// </summary>
        public float GetLevelBestTime(string levelName)
        {
            return LevelBestTimes.TryGetValue(levelName, out float time) ? time : float.MaxValue;
        }

        /// <summary>
        /// Get best score for a specific level
        /// </summary>
        public int GetLevelBestScore(string levelName)
        {
            return LevelBestScores.TryGetValue(levelName, out int score) ? score : 0;
        }

        private static GameData CreateDefaultData() =>
            new()
            {
                lives = MaxLives,
                score = 0,
                currentLevel = "Level_01",
                bestTime = float.MaxValue,
                musicVolume = 1.0f,
                sfxVolume = 1.0f,
                fruitCollected = 0,
                unlockedLevels = new List<string> { "Level_01" },
                selectedLevelIndex = 0,
                LevelBestTimes = new Dictionary<string, float>(),
                LevelCompleted = new Dictionary<string, bool>()
            };
    }
}

using System;
using System.Collections.Generic;
using LevelSelection;
using UnityEngine;

namespace Core.Data
{
    [Serializable]
    public class GameData
    {
        [Header("Player Data")] 
        public const int MaxLives = 3;
        public int lives = 3;
        public int score;
        public int maxScore; // Track highest score ever achieved

        [Header("Level Progress")] 
        public string currentLevel = "Level_01";

        // Level Selection Data
        public List<string> unlockedLevels = new() { "Level_01" };
        public List<string> completedLevels = new(); // Added for EndLevelZone
        public int selectedLevelIndex;

        // Enhanced timing and scoring data
        public Dictionary<string, float> LevelBestTimes = new();
        public Dictionary<string, int> LevelBestScores = new(); // Best score per level
        public Dictionary<string, bool> LevelCompleted = new();

        public float bestTime = float.MaxValue; // Overall best time

        [Header("Settings")] 
        public float musicVolume = 1.0f;
        public float sfxVolume = 1.0f;

        [Header("Collectables")] 
        public int fruitCollected;

        // Cached level discovery data
        public List<LevelData> cachedLevelData = new();
        public bool levelDataCacheValid = false;

        // Constructor for easy initialization
        public GameData()
        {
            // Default values are set by field initializers
        }

        // Copy constructor
        public GameData(GameData other)
        {
            lives = other.lives;
            score = other.score;
            maxScore = other.maxScore;
            currentLevel = other.currentLevel;
            bestTime = other.bestTime;
            musicVolume = other.musicVolume;
            sfxVolume = other.sfxVolume;
            fruitCollected = other.fruitCollected;
            unlockedLevels = new List<string>(other.unlockedLevels);
            completedLevels = new List<string>(other.completedLevels);
            selectedLevelIndex = other.selectedLevelIndex;
            LevelBestTimes = new Dictionary<string, float>(other.LevelBestTimes);
            LevelBestScores = new Dictionary<string, int>(other.LevelBestScores);
            LevelCompleted = new Dictionary<string, bool>(other.LevelCompleted);
            cachedLevelData = new List<LevelData>(other.cachedLevelData);
            levelDataCacheValid = other.levelDataCacheValid;
        }

        // Reset to default values
        public void Reset()
        {
            lives = MaxLives;
            score = 0;
            maxScore = 0;
            currentLevel = "Level_01";
            bestTime = float.MaxValue;
            musicVolume = 1.0f;
            sfxVolume = 1.0f;
            fruitCollected = 0;

            // Reset level selection data
            unlockedLevels = new List<string> { "Level_01" };
            completedLevels = new List<string>();
            selectedLevelIndex = 0;
            LevelBestTimes = new Dictionary<string, float>();
            LevelBestScores = new Dictionary<string, int>();
            LevelCompleted = new Dictionary<string, bool>();
            cachedLevelData = new List<LevelData>();
            levelDataCacheValid = false;
        }

        /// <summary>
        /// Update best time for a specific level
        /// </summary>
        public void UpdateLevelBestTime(string levelName, float completionTime)
        {
            if (string.IsNullOrEmpty(levelName)) return;

            if (!LevelBestTimes.ContainsKey(levelName) || completionTime < LevelBestTimes[levelName])
            {
                LevelBestTimes[levelName] = completionTime;

                // Update overall best time if this is better
                if (completionTime < bestTime)
                {
                    bestTime = completionTime;
                }
            }
        }

        /// <summary>
        /// Update best score for a specific level
        /// </summary>
        public void UpdateLevelBestScore(string levelName, int levelScore)
        {
            if (string.IsNullOrEmpty(levelName)) return;

            if (!LevelBestScores.ContainsKey(levelName) || levelScore > LevelBestScores[levelName])
            {
                LevelBestScores[levelName] = levelScore;
            }
        }

        /// <summary>
        /// Update max score if current score is higher
        /// </summary>
        public void UpdateMaxScore(int currentScore)
        {
            if (currentScore > maxScore)
            {
                maxScore = currentScore;
            }
        }

        /// <summary>
        /// Get best time for a specific level
        /// </summary>
        public float GetLevelBestTime(string levelName)
        {
            return LevelBestTimes.TryGetValue(levelName, out float time) ? time : float.MaxValue;
        }

        /// <summary>
        /// Get best score for a specific level
        /// </summary>
        public int GetLevelBestScore(string levelName)
        {
            return LevelBestScores.TryGetValue(levelName, out int score) ? score : 0;
        }

        /// <summary>
        /// Check if a level is unlocked
        /// </summary>
        public bool IsLevelUnlocked(string levelName)
        {
            return !string.IsNullOrEmpty(levelName) && unlockedLevels.Contains(levelName);
        }

        /// <summary>
        /// Check if a level is completed
        /// </summary>
        public bool IsLevelCompleted(string levelName)
        {
            return !string.IsNullOrEmpty(levelName) && completedLevels.Contains(levelName);
        }

        private static GameData CreateDefaultData() =>
            new()
            {
                lives = MaxLives,
                score = 0,
                currentLevel = "Level_01",
                bestTime = float.MaxValue,
                musicVolume = 1.0f,
                sfxVolume = 1.0f,
                fruitCollected = 0,
                unlockedLevels = new List<string> { "Level_01" },
                selectedLevelIndex = 0,
                LevelBestTimes = new Dictionary<string, float>(),
                LevelCompleted = new Dictionary<string, bool>()
            };
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using LevelSelection;
using LevelSelection.Services;
using VContainer;

namespace Core.Data
{
    public class GameDataService : IGameDataService
    {
        private readonly IGameDataRepository _repository;

        [Inject]
        public GameDataService(IGameDataRepository repository)
        {
            _repository = repository;
            CurrentData = _repository.LoadData();
        }

        public GameData CurrentData { get; private set; }

        public event Action<GameData> OnDataChanged;

        public void UpdateLives(int lives)
        {
            CurrentData.lives = lives;
            NotifyDataChanged();
        }

        public void UpdateScore(int score)
        {
            CurrentData.score = score;
            NotifyDataChanged();
        }


        public void UpdateCurrentLevel(string levelName)
        {
            CurrentData.currentLevel = levelName;
            NotifyDataChanged();
        }

        public void UpdateBestTime(string levelName, float time)
        {
            var gameData = CurrentData;
            if (gameData == null) return;

            // Update overall best time
            if (time < gameData.bestTime)
            {
                gameData.bestTime = time;
            }

            // Update per-level best time
            if (!gameData.LevelBestTimes.ContainsKey(levelName) || time < gameData.LevelBestTimes[levelName])
            {
                gameData.LevelBestTimes[levelName] = time;
            }

            NotifyDataChanged();
        }

        public void ResetAllData()
        {
            _repository.DeleteData();
            CurrentData = _repository.LoadData();
            NotifyDataChanged();
        }

        public void SaveData()
        {
            _repository.SaveData(CurrentData);
        }

        public void AddFruitCollected()
        {
            CurrentData.fruitCollected++;
            NotifyDataChanged();
        }

        public void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime)
        {
            var gameData = CurrentData;
            if (gameData == null) return;

            // Update completed levels list
            if (isCompleted && !gameData.completedLevels.Contains(levelName))
            {
                gameData.completedLevels.Add(levelName);
            }

            // Update best time
            if (!gameData.LevelBestTimes.ContainsKey(levelName) || completionTime < gameData.LevelBestTimes[levelName])
            {
                gameData.LevelBestTimes[levelName] = completionTime;
            }

            NotifyDataChanged();
        }

        public async Task<List<LevelData>> GetLevelDataAsync(ILevelDiscoveryService discoveryService)
        {
            if (CurrentData.levelDataCacheValid && CurrentData.cachedLevelData.Any())
            {
                return ApplyGameStateToLevelData(CurrentData.cachedLevelData);
            }

            var discoveredLevels = await discoveryService.DiscoverLevelsFromSceneAsync();
            CacheLevelData(discoveredLevels);
            return ApplyGameStateToLevelData(discoveredLevels);
        }

        private void CacheLevelData(List<LevelData> levelData)
        {
            CurrentData.cachedLevelData = new List<LevelData>(levelData);
            CurrentData.levelDataCacheValid = true;
            SaveData();
        }

        private List<LevelData> ApplyGameStateToLevelData(List<LevelData> baseLevelData)
        {
            // No need to modify LevelData anymore since state is stored separately
            // Just return the base level data as-is
            return baseLevelData;
        }

        private void NotifyDataChanged()
        {
            OnDataChanged?.Invoke(CurrentData);
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using LevelSelection;
using LevelSelection.Services;
using VContainer;

namespace Core.Data
{
    public class GameDataService : IGameDataService
    {
        private readonly IGameDataRepository _repository;

        [Inject]
        public GameDataService(IGameDataRepository repository)
        {
            _repository = repository;
            CurrentData = _repository.LoadData();
        }

        public GameData CurrentData { get; private set; }

        public event Action<GameData> OnDataChanged;

        public void UpdateLives(int lives)
        {
            CurrentData.lives = lives;
            NotifyDataChanged();
        }

        public void UpdateScore(int score)
        {
            CurrentData.score = score;
            NotifyDataChanged();
        }


        public void UpdateCurrentLevel(string levelName)
        {
            CurrentData.currentLevel = levelName;
            NotifyDataChanged();
        }

        public void UpdateBestTime(string levelName, float time)
        {
            var gameData = CurrentData;
            if (gameData == null) return;

            // Update overall best time
            if (time < gameData.bestTime)
            {
                gameData.bestTime = time;
            }

            // Update per-level best time
            if (!gameData.LevelBestTimes.ContainsKey(levelName) || time < gameData.LevelBestTimes[levelName])
            {
                gameData.LevelBestTimes[levelName] = time;
            }

            NotifyDataChanged();
        }

        public void ResetAllData()
        {
            _repository.DeleteData();
            CurrentData = _repository.LoadData();
            NotifyDataChanged();
        }

        public void SaveData()
        {
            _repository.SaveData(CurrentData);
        }

        public void AddFruitCollected()
        {
            CurrentData.fruitCollected++;
            NotifyDataChanged();
        }

        public void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime)
        {
            var gameData = CurrentData;
            if (gameData == null) return;

            // Update completed levels list
            if (isCompleted && !gameData.completedLevels.Contains(levelName))
            {
                gameData.completedLevels.Add(levelName);
            }

            // Update best time
            if (!gameData.LevelBestTimes.ContainsKey(levelName) || completionTime < gameData.LevelBestTimes[levelName])
            {
                gameData.LevelBestTimes[levelName] = completionTime;
            }

            NotifyDataChanged();
        }

        public async Task<List<LevelData>> GetLevelDataAsync(ILevelDiscoveryService discoveryService)
        {
            if (CurrentData.levelDataCacheValid && CurrentData.cachedLevelData.Any())
            {
                return ApplyGameStateToLevelData(CurrentData.cachedLevelData);
            }

            var discoveredLevels = await discoveryService.DiscoverLevelsFromSceneAsync();
            CacheLevelData(discoveredLevels);
            return ApplyGameStateToLevelData(discoveredLevels);
        }

        private void CacheLevelData(List<LevelData> levelData)
        {
            CurrentData.cachedLevelData = new List<LevelData>(levelData);
            CurrentData.levelDataCacheValid = true;
            SaveData();
        }

        private List<LevelData> ApplyGameStateToLevelData(List<LevelData> baseLevelData)
        {
            // No need to modify LevelData anymore since state is stored separately
            // Just return the base level data as-is
            return baseLevelData;
        }

        private void NotifyDataChanged()
        {
            OnDataChanged?.Invoke(CurrentData);
        }
    }
}

using System;
using System.Collections.Generic;
using System.Threading.Tasks;
using LevelSelection;

namespace Core.Data
{
    public interface IGameDataService
    {
        GameData CurrentData { get; }
        void UpdateLives(int lives);
        void UpdateScore(int score);
        void AddFruitCollected();
        void UpdateBestTime(float time);
        void UpdateCurrentLevel(string levelName);
        void ResetAllData();
        void SaveData();
        event Action<GameData> OnDataChanged;

        // Level data operations that should go through GameDataService
        void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime);
        Task<List<LevelData>> DiscoverLevelsAsync();
    }
}

using System;
using System.Collections.Generic;
using System.Threading.Tasks;
using LevelSelection;
using LevelSelection.Services;

namespace Core.Data
{
    public interface IGameDataService
    {
        GameData CurrentData { get; }
        void UpdateLives(int lives);
        void UpdateScore(int score);
        void AddFruitCollected();
        void UpdateBestTime(string levelName, float time);
        void UpdateCurrentLevel(string levelName);
        void ResetAllData();
        void SaveData();
        event Action<GameData> OnDataChanged;
        
        // Level data operations that should go through GameDataService
        void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime);
        Task<List<LevelData>> GetLevelDataAsync(ILevelDiscoveryService discoveryService);
    }
}

using System.Collections.Generic;
using System.Threading.Tasks;
using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection;
using LevelSelection.Services;
using UnityEngine;
using VContainer;

namespace Core
{
    public class GameDataCoordinator : MonoBehaviour
    {

#if UNITY_EDITOR
        [Header("Debug")]
        [SerializeField] private bool resetGameData;
#endif

        private IAutoSaveService _autoSaveService;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private ILevelDiscoveryService _levelDiscoveryService;
        private bool _isInitialized;

        // Track previous values to detect changes
        private int _previousLives = -1;

        [Inject]
        public void Construct(
            IGameDataService gameDataService,
            IEventBus eventBus,
            IAutoSaveService autoSaveService,
            ILevelDiscoveryService levelDiscoveryService)
        {
            _gameDataService = gameDataService;
            _eventBus = eventBus;
            _autoSaveService = autoSaveService;
            _levelDiscoveryService = levelDiscoveryService;
            _isInitialized = true;

            Initialize();
        }

        private void Initialize()
        {
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Subscribe<LevelStartedEvent>(OnLevelStarted);
            _eventBus?.Subscribe<GameStateChangedEvent>(OnGameStateChanged);
            _eventBus?.Subscribe<PlayerDeathEvent>(OnPlayerDied);
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelNavigationEvent>(OnLevelNavigation);
        }

#if UNITY_EDITOR
        private void OnValidate()
        {
            if (resetGameData)
            {
                if (_gameDataService != null)
                {
                    _gameDataService.ResetAllData();
                    Debug.Log("Game data has been reset.");
                }
                else
                {
                    Debug.LogWarning("GameDataService not available. Cannot reset game data.");
                }
                resetGameData = false;
            }
        }
#endif

        private void SaveData()
        {
            _gameDataService?.SaveData();
        }

        private void Update()
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.Update();
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Unsubscribe<LevelStartedEvent>(OnLevelStarted);
            _eventBus?.Unsubscribe<GameStateChangedEvent>(OnGameStateChanged);
            _eventBus?.Unsubscribe<PlayerDeathEvent>(OnPlayerDied);
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelNavigationEvent>(OnLevelNavigation);

            if (_gameDataService != null)
                _gameDataService.OnDataChanged -= OnGameDataChanged;

            if (_autoSaveService != null)
            {
                _autoSaveService.OnSaveRequested -= SaveData;
                _autoSaveService.ForceSave();
            }
        }

        private void OnApplicationPause(bool pauseStatus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationPause(pauseStatus);
        }

        private void OnApplicationFocus(bool hasFocus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationFocus(hasFocus);
        }

        private void OnPlayerDied(PlayerDeathEvent deathEvent)
        {
            _autoSaveService?.RequestSave();
        }

        private void OnLevelCompleted(LevelCompletedEvent levelEvent)
        {
            _gameDataService?.UpdateBestTime(levelEvent.LevelName, levelEvent.CompletionTime);
            _autoSaveService?.RequestSave();
        }

        private void OnGameStateChanged(GameStateChangedEvent stateEvent)
        {
            if (stateEvent.NewState is GameState.Victory or GameState.GameOver)
            {
                _autoSaveService?.ForceSave();
            }
        }

        private void OnLevelStarted(LevelStartedEvent levelEvent)
        {
            _gameDataService?.UpdateCurrentLevel(levelEvent.LevelName);
        }

        private void OnGameDataChanged(GameData newData)
        {
            // Check if lives changed and publish the appropriate event
            if (_previousLives != newData.lives)
            {
                _eventBus?.Publish(new PlayerLivesChangedEvent
                {
                    PreviousLives = _previousLives,
                    CurrentLives = newData.lives,
                    MaxLives = GameData.MaxLives,
                    Timestamp = Time.time
                });

                _previousLives = newData.lives;
            }

            _autoSaveService?.RequestSave();
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                gameData.currentLevel = levelEvent.LevelName;
            }
        }

        private void OnLevelNavigation(LevelNavigationEvent navigationEvent)
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = navigationEvent.NewIndex;
            }
        }

        // Public API for other systems to request data operations
        public void UpdateLives(int lives)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateLives(lives);
        }

        public void UpdateCurrentLevel(string levelName)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateCurrentLevel(levelName);
        }

        public void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateLevelProgress(levelName, isCompleted, completionTime);
        }

        public async Task<List<LevelData>> DiscoverLevelsAsync()
        {
            if (!_isInitialized || _gameDataService == null || _levelDiscoveryService == null)
                return new List<LevelData>();
            return await _gameDataService.GetLevelDataAsync(_levelDiscoveryService);
        }

        public GameData GetCurrentData()
        {
            if (!_isInitialized) return null;
            return _gameDataService?.CurrentData;
        }

        public void ResetAllData()
        {
            if (!_isInitialized) return;
            _gameDataService?.ResetAllData();
        }

        // Wrapper methods for GameData operations 
        public bool IsLevelUnlocked(string levelName)
        {
            return GetCurrentData()?.IsLevelUnlocked(levelName) ?? false;
        }

        public bool IsLevelCompleted(string levelName)
        {
            return GetCurrentData()?.IsLevelCompleted(levelName) ?? false;
        }

        public float GetLevelBestTime(string levelName)
        {
            return GetCurrentData()?.GetLevelBestTime(levelName) ?? float.MaxValue;
        }

        public int GetLevelBestScore(string levelName)
        {
            return GetCurrentData()?.GetLevelBestScore(levelName) ?? 0;
        }

        // Additional wrapper methods to avoid GetCurrentData() calls
        public int GetCurrentLives()
        {
            return GetCurrentData()?.lives ?? GameData.MaxLives;
        }

        public List<string> GetUnlockedLevels()
        {
            return GetCurrentData()?.unlockedLevels ?? new List<string> { "Level_01" };
        }

        public List<string> GetCompletedLevels()
        {
            return GetCurrentData()?.completedLevels ?? new List<string>();
        }

        public int GetSelectedLevelIndex()
        {
            return GetCurrentData()?.selectedLevelIndex ?? 0;
        }
    }
}

using System.Collections.Generic;
using System.Threading.Tasks;
using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection;
using LevelSelection.Services;
using UnityEngine;
using VContainer;

namespace Core
{
    public class GameDataCoordinator : MonoBehaviour
    {

#if UNITY_EDITOR
        [Header("Debug")]
        [SerializeField] private bool resetGameData;
#endif

        private IAutoSaveService _autoSaveService;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private ILevelDiscoveryService _levelDiscoveryService;
        private bool _isInitialized;

        // Track previous values to detect changes
        private int _previousLives = -1;

        [Inject]
        public void Construct(
            IGameDataService gameDataService,
            IEventBus eventBus,
            IAutoSaveService autoSaveService,
            ILevelDiscoveryService levelDiscoveryService)
        {
            _gameDataService = gameDataService;
            _eventBus = eventBus;
            _autoSaveService = autoSaveService;
            _levelDiscoveryService = levelDiscoveryService;
            _isInitialized = true;

            Initialize();
        }

        private void Initialize()
        {
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Subscribe<LevelStartedEvent>(OnLevelStarted);
            _eventBus?.Subscribe<GameStateChangedEvent>(OnGameStateChanged);
            _eventBus?.Subscribe<PlayerDeathEvent>(OnPlayerDied);
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelNavigationEvent>(OnLevelNavigation);
        }

#if UNITY_EDITOR
        private void OnValidate()
        {
            if (resetGameData)
            {
                if (_gameDataService != null)
                {
                    _gameDataService.ResetAllData();
                    Debug.Log("Game data has been reset.");
                }
                else
                {
                    Debug.LogWarning("GameDataService not available. Cannot reset game data.");
                }
                resetGameData = false;
            }
        }
#endif

        private void SaveData()
        {
            _gameDataService?.SaveData();
        }

        private void Update()
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.Update();
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Unsubscribe<LevelStartedEvent>(OnLevelStarted);
            _eventBus?.Unsubscribe<GameStateChangedEvent>(OnGameStateChanged);
            _eventBus?.Unsubscribe<PlayerDeathEvent>(OnPlayerDied);
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelNavigationEvent>(OnLevelNavigation);

            if (_gameDataService != null)
                _gameDataService.OnDataChanged -= OnGameDataChanged;

            if (_autoSaveService != null)
            {
                _autoSaveService.OnSaveRequested -= SaveData;
                _autoSaveService.ForceSave();
            }
        }

        private void OnApplicationPause(bool pauseStatus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationPause(pauseStatus);
        }

        private void OnApplicationFocus(bool hasFocus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationFocus(hasFocus);
        }

        private void OnPlayerDied(PlayerDeathEvent deathEvent)
        {
            _autoSaveService?.RequestSave();
        }

        private void OnLevelCompleted(LevelCompletedEvent levelEvent)
        {
            _gameDataService?.UpdateBestTime(levelEvent.LevelName, levelEvent.CompletionTime);
            _autoSaveService?.RequestSave();
        }

        private void OnGameStateChanged(GameStateChangedEvent stateEvent)
        {
            if (stateEvent.NewState is GameState.Victory or GameState.GameOver)
            {
                _autoSaveService?.ForceSave();
            }
        }

        private void OnLevelStarted(LevelStartedEvent levelEvent)
        {
            _gameDataService?.UpdateCurrentLevel(levelEvent.LevelName);
        }

        private void OnGameDataChanged(GameData newData)
        {
            // Check if lives changed and publish the appropriate event
            if (_previousLives != newData.lives)
            {
                _eventBus?.Publish(new PlayerLivesChangedEvent
                {
                    PreviousLives = _previousLives,
                    CurrentLives = newData.lives,
                    MaxLives = GameData.MaxLives,
                    Timestamp = Time.time
                });

                _previousLives = newData.lives;
            }

            _autoSaveService?.RequestSave();
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                gameData.currentLevel = levelEvent.LevelName;
            }
        }

        private void OnLevelNavigation(LevelNavigationEvent navigationEvent)
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = navigationEvent.NewIndex;
            }
        }

        // Public API for other systems to request data operations
        public void UpdateLives(int lives)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateLives(lives);
        }

        public void UpdateCurrentLevel(string levelName)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateCurrentLevel(levelName);
        }

        public void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateLevelProgress(levelName, isCompleted, completionTime);
        }

        public async Task<List<LevelData>> DiscoverLevelsAsync()
        {
            if (!_isInitialized || _gameDataService == null || _levelDiscoveryService == null)
                return new List<LevelData>();
            return await _gameDataService.GetLevelDataAsync(_levelDiscoveryService);
        }

        public GameData GetCurrentData()
        {
            if (!_isInitialized) return null;
            return _gameDataService?.CurrentData;
        }

        public void ResetAllData()
        {
            if (!_isInitialized) return;
            _gameDataService?.ResetAllData();
        }

        // Wrapper methods for GameData operations 
        public bool IsLevelUnlocked(string levelName)
        {
            return GetCurrentData()?.IsLevelUnlocked(levelName) ?? false;
        }

        public bool IsLevelCompleted(string levelName)
        {
            return GetCurrentData()?.IsLevelCompleted(levelName) ?? false;
        }

        public float GetLevelBestTime(string levelName)
        {
            return GetCurrentData()?.GetLevelBestTime(levelName) ?? float.MaxValue;
        }

        public int GetLevelBestScore(string levelName)
        {
            return GetCurrentData()?.GetLevelBestScore(levelName) ?? 0;
        }

        // Additional wrapper methods to avoid GetCurrentData() calls
        public int GetCurrentLives()
        {
            return GetCurrentData()?.lives ?? GameData.MaxLives;
        }

        public List<string> GetUnlockedLevels()
        {
            return GetCurrentData()?.unlockedLevels ?? new List<string> { "Level_01" };
        }

        public List<string> GetCompletedLevels()
        {
            return GetCurrentData()?.completedLevels ?? new List<string>();
        }

        public int GetSelectedLevelIndex()
        {
            return GetCurrentData()?.selectedLevelIndex ?? 0;
        }

        public void UnlockLevel(string levelName)
        {
            var gameData = GetCurrentData();
            if (gameData != null && !string.IsNullOrEmpty(levelName) && !gameData.unlockedLevels.Contains(levelName))
            {
                gameData.unlockedLevels.Add(levelName);
                Debug.Log($"[GameDataCoordinator] Unlocked level: {levelName}");
            }
        }
    }
}

using System;
using System.Linq;
using System.Threading.Tasks;
using Core.Data;
using Core.Events;
using Player.Components;
using UnityEngine;
using UnityEngine.SceneManagement;
using VContainer;

namespace Core
{
    public class GameFlowManager : MonoBehaviour
    {
        [Header("Game Settings")]
        [SerializeField] private bool autoStartGame = true;
        [SerializeField] private float restartDelay = 2f;

        [Header("Victory Settings")]
        [SerializeField] private string victorySceneName = "YouWonScene";
        [SerializeField] private float victoryTransitionDelay = 3f;

        private string _currentLevelName = "Unknown";
        private float _levelStartTime;
        private IEventBus _eventBus;
        private GameDataCoordinator _gameDataCoordinator;

        public GameState CurrentState { get; private set; } = GameState.MainMenu;
        public bool IsPlaying => CurrentState == GameState.Playing;

        [Inject]
        public void Construct(IEventBus eventBus, GameDataCoordinator gameDataCoordinator)
        {
            _eventBus = eventBus;
            _gameDataCoordinator = gameDataCoordinator;
            SubscribeToEvents();
        }

        private void Start()
        {
            _currentLevelName = GetCurrentLevelName();

            if (autoStartGame)
            {
                StartGame();
            }
        }

        private void OnDestroy()
        {
            UnsubscribeFromEvents();
        }

        private void StartGame()
        {
            Time.timeScale = 1;
            ChangeState(GameState.Playing);
            _levelStartTime = Time.time;

            _eventBus?.Publish(new LevelStartedEvent
            {
                LevelName = _currentLevelName,
                Timestamp = Time.time
            });
        }

        public void PauseGame()
        {
            if (CurrentState == GameState.Playing)
                ChangeState(GameState.Paused);
        }

        public void ResumeGame()
        {
            if (CurrentState == GameState.Paused)
                ChangeState(GameState.Playing);
        }

        public void RestartLevel()
        {
            SceneManager.LoadScene(SceneManager.GetActiveScene().name);
        }

        public void HandlePlayerDeath(Vector3 deathPosition)
        {
            _eventBus?.Publish(new PlayerDeathEvent
            {
                DeathPosition = deathPosition,
                Timestamp = Time.time
            });
        }

        public void CompleteLevel(string currentLevelName)
        {
            float completionTime = Time.time - _levelStartTime;

            _eventBus?.Publish(new LevelCompletedEvent
            {
                LevelName = currentLevelName,
                CompletionTime = completionTime,
                Timestamp = Time.time
            });
        }

        public void RequestLevelLoad(string levelName, string sceneName)
        {
            _eventBus?.Publish(new LevelLoadRequestedEvent
            {
                Timestamp = Time.time,
                LevelName = levelName,
                SceneName = sceneName
            });
        }

        public void SelectLevel(string levelName, int levelIndex)
        {
            _eventBus?.Publish(new LevelSelectedEvent
            {
                Timestamp = Time.time,
                LevelName = levelName,
                LevelIndex = levelIndex
            });
        }

        public void NavigateLevel(int previousIndex, int newIndex, Vector2 direction)
        {
            _eventBus?.Publish(new LevelNavigationEvent
            {
                Timestamp = Time.time,
                PreviousIndex = previousIndex,
                NewIndex = newIndex,
                Direction = direction
            });
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<GameOverEvent>(OnGameOver);
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Subscribe<PlayerLivesChangedEvent>(OnPlayerLivesChanged);
        }

        private void UnsubscribeFromEvents()
        {
            _eventBus?.Unsubscribe<GameOverEvent>(OnGameOver);
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Unsubscribe<PlayerLivesChangedEvent>(OnPlayerLivesChanged);
        }

        private void OnGameOver(GameOverEvent gameOverEvent)
        {
            ChangeState(GameState.GameOver);
            RestartLevelAfterDelayAsync(restartDelay);
        }

        private async void OnLevelCompleted(LevelCompletedEvent levelEvent)
        {
            ChangeState(GameState.Victory);

            // Update level progress through GameDataCoordinator
            _gameDataCoordinator?.UpdateLevelProgress(levelEvent.LevelName, true, levelEvent.CompletionTime);

            // Check if level is already completed to avoid duplicate additions
            if (_gameDataCoordinator && !_gameDataCoordinator.IsLevelCompleted(levelEvent.LevelName))
            {
                // Unlock next level if it exists
                UnlockNextLevel(levelEvent.LevelName);
            }

            if (await IsGameCompletedAsync())
            {
                Debug.Log("[GameFlowManager] All levels completed! Transitioning to YouWonScene...");
                TransitionToVictorySceneAsync();
            }
            else
            {
                Debug.Log($"[GameFlowManager] Level {levelEvent.LevelName} completed. Returning to level selection...");
                ReturnToLevelSelectionAsync();
            }
        }

        private void UnlockNextLevel(string completedLevelName)
        {
            // Simple next level unlocking logic - you can make this more sophisticated
            if (completedLevelName.Contains("Level_"))
            {
                var levelNumber = ExtractLevelNumber(completedLevelName);
                var nextLevelName = $"Level_{levelNumber + 1:D2}";

                if (!_gameDataCoordinator.IsLevelUnlocked(nextLevelName))
                {
                    // We need to add a method to unlock levels in GameDataCoordinator
                    var unlockedLevels = _gameDataCoordinator.GetUnlockedLevels();
                    if (!unlockedLevels.Contains(nextLevelName))
                    {
                        // For now, we'll access the raw data since we need to modify the list
                        var gameData = _gameDataCoordinator?.GetCurrentData();
                        if (gameData != null && !gameData.unlockedLevels.Contains(nextLevelName))
                        {
                            gameData.unlockedLevels.Add(nextLevelName);
                            Debug.Log($"[GameFlowManager] Unlocked next level: {nextLevelName}");
                        }
                    }
                }
            }
        }

        private int ExtractLevelNumber(string levelName)
        {
            // Extract number from level name like "Level_01" -> 1
            var parts = levelName.Split('_');
            if (parts.Length > 1 && int.TryParse(parts[1], out int levelNum))
            {
                return levelNum;
            }
            return 0;
        }

        private async Task<bool> IsGameCompletedAsync()
        {
            try
            {
                // Check using GameData completed levels through GameDataCoordinator
                var gameData = _gameDataCoordinator?.GetCurrentData();
                if (gameData == null) return false;

                // Get all available levels through GameDataCoordinator
                var allLevels = await _gameDataCoordinator.DiscoverLevelsAsync();

                if (allLevels == null || allLevels.Count == 0)
                {
                    Debug.LogWarning("[GameFlowManager] No levels found in discovery service");
                    return false;
                }

                // Check if all levels are in the completed list
                var completedCount = 0;
                foreach (var level in allLevels)
                {
                    if (gameData.completedLevels.Contains(level.levelName))
                    {
                        completedCount++;
                    }
                }

                Debug.Log($"[GameFlowManager] Game completion check: {completedCount}/{allLevels.Count} levels completed");

                if (completedCount >= allLevels.Count)
                {
                    Debug.Log("[GameFlowManager] All levels completed!");
                    return true;
                }

                var remainingLevels = allLevels.Where(l => !gameData.completedLevels.Contains(l.levelName)).Select(l => l.levelName);
                Debug.Log($"[GameFlowManager] Remaining levels: {string.Join(", ", remainingLevels)}");
                return false;
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Error checking game completion: {e}");
                return false;
            }
        }

        private async void TransitionToVictorySceneAsync()
        {
            try
            {
                await Task.Delay((int)(victoryTransitionDelay * 1000));

                _eventBus?.Publish(new GameCompletedEvent
                {
                    Timestamp = Time.time,
                    FinalLevelName = _currentLevelName
                });

                SceneManager.LoadScene(victorySceneName);
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to transition to victory scene: {e}");
                SceneManager.LoadScene("Level Select");
            }
        }

        private async void ReturnToLevelSelectionAsync()
        {
            try
            {
                await Task.Delay((int)(2f * 1000));
                SceneManager.LoadScene("Level Select");
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to return to level selection: {e}");
            }
        }

        private void OnPlayerLivesChanged(PlayerLivesChangedEvent livesEvent)
        {
            bool lostLife = livesEvent.PreviousLives > livesEvent.CurrentLives;
            bool isGameOver = livesEvent.CurrentLives == 0;

            if (isGameOver)
            {
                ChangeState(GameState.GameOver);
                _gameDataCoordinator?.ResetAllData();
                _eventBus?.Publish(new GameOverEvent { Timestamp = Time.time });
            }

            if (lostLife)
            {
                _eventBus?.Publish(new PlayerDeathEvent
                {
                    DeathPosition = PlayerLocator.PlayerTransform.position,
                    Timestamp = Time.time
                });
            }

            if (isGameOver || lostLife)
            {
                Time.timeScale = 0.01f;
                RestartLevelAfterDelayAsync(restartDelay);
            }
        }

        private static async void RestartLevelAfterDelayAsync(float delay)
        {
            try
            {
                await Task.Delay((int)(delay * 1000));
                SceneManager.LoadScene(SceneManager.GetActiveScene().name);
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to restart level after delay: {e}");
            }
        }

        private void ChangeState(GameState newState)
        {
            if (CurrentState == newState) return;

            GameState oldState = CurrentState;
            CurrentState = newState;

            _eventBus?.Publish(new GameStateChangedEvent
            {
                PreviousState = oldState,
                NewState = newState,
                Timestamp = Time.time
            });
        }

        private string GetCurrentLevelName() => SceneManager.GetActiveScene().name;
    }
}

using System;
using System.Linq;
using System.Threading.Tasks;
using Core.Data;
using Core.Events;
using Player.Components;
using UnityEngine;
using UnityEngine.SceneManagement;
using VContainer;

namespace Core
{
    public class GameFlowManager : MonoBehaviour
    {
        [Header("Game Settings")]
        [SerializeField] private bool autoStartGame = true;
        [SerializeField] private float restartDelay = 2f;

        [Header("Victory Settings")]
        [SerializeField] private string victorySceneName = "YouWonScene";
        [SerializeField] private float victoryTransitionDelay = 3f;

        private string _currentLevelName = "Unknown";
        private float _levelStartTime;
        private IEventBus _eventBus;
        private GameDataCoordinator _gameDataCoordinator;

        public GameState CurrentState { get; private set; } = GameState.MainMenu;
        public bool IsPlaying => CurrentState == GameState.Playing;

        [Inject]
        public void Construct(IEventBus eventBus, GameDataCoordinator gameDataCoordinator)
        {
            _eventBus = eventBus;
            _gameDataCoordinator = gameDataCoordinator;
            SubscribeToEvents();
        }

        private void Start()
        {
            _currentLevelName = GetCurrentLevelName();

            if (autoStartGame)
            {
                StartGame();
            }
        }

        private void OnDestroy()
        {
            UnsubscribeFromEvents();
        }

        private void StartGame()
        {
            Time.timeScale = 1;
            ChangeState(GameState.Playing);
            _levelStartTime = Time.time;

            _eventBus?.Publish(new LevelStartedEvent
            {
                LevelName = _currentLevelName,
                Timestamp = Time.time
            });
        }

        public void PauseGame()
        {
            if (CurrentState == GameState.Playing)
                ChangeState(GameState.Paused);
        }

        public void ResumeGame()
        {
            if (CurrentState == GameState.Paused)
                ChangeState(GameState.Playing);
        }

        public void RestartLevel()
        {
            SceneManager.LoadScene(SceneManager.GetActiveScene().name);
        }

        public void HandlePlayerDeath(Vector3 deathPosition)
        {
            _eventBus?.Publish(new PlayerDeathEvent
            {
                DeathPosition = deathPosition,
                Timestamp = Time.time
            });
        }

        public void CompleteLevel(string currentLevelName)
        {
            float completionTime = Time.time - _levelStartTime;

            _eventBus?.Publish(new LevelCompletedEvent
            {
                LevelName = currentLevelName,
                CompletionTime = completionTime,
                Timestamp = Time.time
            });
        }

        public void RequestLevelLoad(string levelName, string sceneName)
        {
            _eventBus?.Publish(new LevelLoadRequestedEvent
            {
                Timestamp = Time.time,
                LevelName = levelName,
                SceneName = sceneName
            });
        }

        public void SelectLevel(string levelName, int levelIndex)
        {
            _eventBus?.Publish(new LevelSelectedEvent
            {
                Timestamp = Time.time,
                LevelName = levelName,
                LevelIndex = levelIndex
            });
        }

        public void NavigateLevel(int previousIndex, int newIndex, Vector2 direction)
        {
            _eventBus?.Publish(new LevelNavigationEvent
            {
                Timestamp = Time.time,
                PreviousIndex = previousIndex,
                NewIndex = newIndex,
                Direction = direction
            });
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<GameOverEvent>(OnGameOver);
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Subscribe<PlayerLivesChangedEvent>(OnPlayerLivesChanged);
        }

        private void UnsubscribeFromEvents()
        {
            _eventBus?.Unsubscribe<GameOverEvent>(OnGameOver);
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Unsubscribe<PlayerLivesChangedEvent>(OnPlayerLivesChanged);
        }

        private void OnGameOver(GameOverEvent gameOverEvent)
        {
            ChangeState(GameState.GameOver);
            RestartLevelAfterDelayAsync(restartDelay);
        }

        private async void OnLevelCompleted(LevelCompletedEvent levelEvent)
        {
            ChangeState(GameState.Victory);

            // Update level progress through GameDataCoordinator
            _gameDataCoordinator?.UpdateLevelProgress(levelEvent.LevelName, true, levelEvent.CompletionTime);

            // Check if level is already completed to avoid duplicate additions
            if (_gameDataCoordinator && !_gameDataCoordinator.IsLevelCompleted(levelEvent.LevelName))
            {
                // Unlock next level if it exists
                UnlockNextLevel(levelEvent.LevelName);
            }

            if (await IsGameCompletedAsync())
            {
                Debug.Log("[GameFlowManager] All levels completed! Transitioning to YouWonScene...");
                TransitionToVictorySceneAsync();
            }
            else
            {
                Debug.Log($"[GameFlowManager] Level {levelEvent.LevelName} completed. Returning to level selection...");
                ReturnToLevelSelectionAsync();
            }
        }

        private void UnlockNextLevel(string completedLevelName)
        {
            // Simple next level unlocking logic - you can make this more sophisticated
            if (completedLevelName.Contains("Level_"))
            {
                var levelNumber = ExtractLevelNumber(completedLevelName);
                var nextLevelName = $"Level_{levelNumber + 1:D2}";

                if (!_gameDataCoordinator.IsLevelUnlocked(nextLevelName))
                {
                    _gameDataCoordinator.UnlockLevel(nextLevelName);
                    Debug.Log($"[GameFlowManager] Unlocked next level: {nextLevelName}");
                }
            }
        }

        private int ExtractLevelNumber(string levelName)
        {
            // Extract number from level name like "Level_01" -> 1
            var parts = levelName.Split('_');
            if (parts.Length > 1 && int.TryParse(parts[1], out int levelNum))
            {
                return levelNum;
            }
            return 0;
        }

        private async Task<bool> IsGameCompletedAsync()
        {
            try
            {
                // Get all available levels through GameDataCoordinator
                var allLevels = await _gameDataCoordinator.DiscoverLevelsAsync();

                if (allLevels == null || allLevels.Count == 0)
                {
                    Debug.LogWarning("[GameFlowManager] No levels found in discovery service");
                    return false;
                }

                // Get completed levels using wrapper method
                var completedLevels = _gameDataCoordinator?.GetCompletedLevels();
                if (completedLevels == null) return false;

                // Check if all levels are in the completed list
                var completedCount = 0;
                foreach (var level in allLevels)
                {
                    if (completedLevels.Contains(level.levelName))
                    {
                        completedCount++;
                    }
                }

                Debug.Log($"[GameFlowManager] Game completion check: {completedCount}/{allLevels.Count} levels completed");

                if (completedCount >= allLevels.Count)
                {
                    Debug.Log("[GameFlowManager] All levels completed!");
                    return true;
                }

                var remainingLevels = allLevels.Where(l => !completedLevels.Contains(l.levelName)).Select(l => l.levelName);
                Debug.Log($"[GameFlowManager] Remaining levels: {string.Join(", ", remainingLevels)}");
                return false;
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Error checking game completion: {e}");
                return false;
            }
        }

        private async void TransitionToVictorySceneAsync()
        {
            try
            {
                await Task.Delay((int)(victoryTransitionDelay * 1000));

                _eventBus?.Publish(new GameCompletedEvent
                {
                    Timestamp = Time.time,
                    FinalLevelName = _currentLevelName
                });

                SceneManager.LoadScene(victorySceneName);
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to transition to victory scene: {e}");
                SceneManager.LoadScene("Level Select");
            }
        }

        private async void ReturnToLevelSelectionAsync()
        {
            try
            {
                await Task.Delay((int)(2f * 1000));
                SceneManager.LoadScene("Level Select");
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to return to level selection: {e}");
            }
        }

        private void OnPlayerLivesChanged(PlayerLivesChangedEvent livesEvent)
        {
            bool lostLife = livesEvent.PreviousLives > livesEvent.CurrentLives;
            bool isGameOver = livesEvent.CurrentLives == 0;

            if (isGameOver)
            {
                ChangeState(GameState.GameOver);
                _gameDataCoordinator?.ResetAllData();
                _eventBus?.Publish(new GameOverEvent { Timestamp = Time.time });
            }

            if (lostLife)
            {
                _eventBus?.Publish(new PlayerDeathEvent
                {
                    DeathPosition = PlayerLocator.PlayerTransform.position,
                    Timestamp = Time.time
                });
            }

            if (isGameOver || lostLife)
            {
                Time.timeScale = 0.01f;
                RestartLevelAfterDelayAsync(restartDelay);
            }
        }

        private static async void RestartLevelAfterDelayAsync(float delay)
        {
            try
            {
                await Task.Delay((int)(delay * 1000));
                SceneManager.LoadScene(SceneManager.GetActiveScene().name);
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to restart level after delay: {e}");
            }
        }

        private void ChangeState(GameState newState)
        {
            if (CurrentState == newState) return;

            GameState oldState = CurrentState;
            CurrentState = newState;

            _eventBus?.Publish(new GameStateChangedEvent
            {
                PreviousState = oldState,
                NewState = newState,
                Timestamp = Time.time
            });
        }

        private string GetCurrentLevelName() => SceneManager.GetActiveScene().name;
    }
}

using Enemies.Interfaces;
using Player.Components;
using UnityEngine;

namespace Enemies.Behaviors
{
    // Emits an event when the player is within a certain distance
    [RequireComponent(typeof(Rigidbody2D))]
    public class FrogProximityTrigger : MonoBehaviour, ITrigger
    {
        [SerializeField] private LayerMask groundLayer;
        [SerializeField] private float triggerDistance = 3f;
        [SerializeField] private float jumpCooldown = 2f;
        [SerializeField] private int checkEveryNFrames = 1;

        private int _frameCounter;
        private bool _grounded;
        private float _lastTriggerTime;
        private Transform _player;

        private void Start()
        {
            _player = PlayerLocator.PlayerTransform;
        }

        private void OnCollisionEnter2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = true;
        }

        private void OnCollisionExit2D(Collision2D collision)
        {
            if ((1 << collision.gameObject.layer & groundLayer) != 0)
                _grounded = false;
        }

        public bool IsTriggered { get; private set; }

        public void CheckTrigger()
        {
            if (++_frameCounter % checkEveryNFrames != 0 || !_player) return;

            Vector2 toPlayer = _player.position - transform.position;
            float sqrDist = toPlayer.sqrMagnitude;
            float sqrTrigger = triggerDistance * triggerDistance;

            if (sqrDist < sqrTrigger && Time.time - _lastTriggerTime > jumpCooldown && _grounded)
            {
                IsTriggered = true;
                _lastTriggerTime = Time.time;
            }
            else
            {
                IsTriggered = false;
            }
        }
    }
}

