using UnityEngine;

namespace Enemy
{
    public static class EnemyDirector
    {
        // Entry point for configuring an enemy in the scene
        public static void ConstructEnemy(EnemyType type, EnemyBase enemy)
        {
            // The director delegates the construction to the factory
            EnemyFactory.ConfigureEnemy(type, enemy);
        }
    }
}

using System;
using UnityEngine;

namespace Enemy
{
    public enum EnemyType
    {
        FireSnake,
        Spider,
        Bird,
        Snake,
        Frog
    }

    public static class EnemyFactory
    {
        // Configures an existing enemy instance using the builder pattern
        public static void ConfigureEnemy(EnemyType type, EnemyBase enemy)
        {
            if (enemy == null) throw new ArgumentNullException(nameof(enemy));
            var builder = new EnemyBuilder(enemy);
            switch (type)
            {
                case EnemyType.FireSnake:
                    builder.WithFireSnakeBehavior();
                    break;
                case EnemyType.Spider:
                    builder.WithSpiderBehavior();
                    break;
                case EnemyType.Bird:
                    builder.WithBirdBehavior();
                    break;
                case EnemyType.Snake:
                    builder.WithSnakeBehavior();
                    break;
                case EnemyType.Frog:
                    builder.WithFrogBehavior();
                    break;
                default:
                    break;
            }
        }
    }
}

using UnityEngine;
using Weapons.Models;

namespace Enemy
{
    public class FireSnakeEnemy : EnemyBase
    {
        [SerializeField] private FireballWeapon fireballWeapon;
        [SerializeField] private float attackCooldown = 2f;
        private float _nextAttackTime;

        protected override void Awake()
        {
            base.Awake();
            if (fireballWeapon != null)
                fireballWeapon.Equip();
        }

        public override void Attack()
        {
            if (fireballWeapon == null)
                return;
            if (Time.time < _nextAttackTime)
                return;
            fireballWeapon.Shoot();
            _nextAttackTime = Time.time + attackCooldown;
        }
    }
}

using UnityEngine;
using Enemy.Behaviors;

namespace Enemy
{
    // Frog: Jumps higher and farther, jumps when player is near
    public class FrogEnemy : EnemyBase, IMovable, IPlayerProximityReactable
    {
        [SerializeField] private float jumpForce = 12f;
        [SerializeField] private float jumpDistance = 6f;
        [SerializeField] private float jumpCooldown = 2f;
        private float _nextJumpTime;
        private Rigidbody2D _rb;
        private bool _playerNearby;

        protected override void Awake()
        {
            base.Awake();
            _rb = GetComponent<Rigidbody2D>();
        }

        public void Move()
        {
            if (_playerNearby && Time.time >= _nextJumpTime)
            {
                Vector2 jumpVector = new Vector2(jumpDistance, jumpForce);
                _rb.AddForce(jumpVector, ForceMode2D.Impulse);
                _nextJumpTime = Time.time + jumpCooldown;
                _playerNearby = false; // Only jump once per proximity event
            }
        }

        public void OnPlayerNearby()
        {
            _playerNearby = true;
        }
    }
}

using UnityEngine;
using Enemy.Behaviors;

namespace Enemy
{
    // Snake: Jumps forward
    public class SnakeEnemy : EnemyBase, IMovable
    {
        [SerializeField] private float jumpForce = 7f;
        [SerializeField] private float jumpInterval = 2f;
        private float _nextJumpTime;
        private Rigidbody2D _rb;

        protected override void Awake()
        {
            base.Awake();
            _rb = GetComponent<Rigidbody2D>();
        }

        public void Move()
        {
            if (Time.time >= _nextJumpTime)
            {
                _rb.AddForce(Vector2.right * jumpForce, ForceMode2D.Impulse);
                _nextJumpTime = Time.time + jumpInterval;
            }
        }
    }
}

using UnityEngine;
using Enemy.Behaviors;

namespace Enemy
{
    // Spider: Can move up/down or stay static in air
    public class SpiderEnemy : EnemyBase, IMovable
    {
        [SerializeField] private bool canMove = true;
        [SerializeField] private float moveSpeed = 2f;
        [SerializeField] private float moveDistance = 3f;
        private Vector3 _startPos;
        private bool _movingUp = true;

        protected override void Awake()
        {
            base.Awake();
            _startPos = transform.position;
        }

        public void Move()
        {
            if (!canMove) return;
            float delta = moveSpeed * Time.deltaTime * (_movingUp ? 1 : -1);
            transform.position += new Vector3(0, delta, 0);
            if (Mathf.Abs(transform.position.y - _startPos.y) > moveDistance)
                _movingUp = !_movingUp;
        }
    }
}

using UnityEngine;
using Health.Interfaces;

namespace Health.Damage.Conditions
{
    [DisallowMultipleComponent]
    public class AlwaysAllowDamage : MonoBehaviour, IDamageCondition
    {
        public bool CanBeDamagedBy(GameObject damager) => true;
    }
}

using Health.Interfaces;
using UnityEngine;

namespace Health.Damage
{
    [DisallowMultipleComponent]
    public class DamageOnCollision : MonoBehaviour
    {
        private IDamageDealer _dealer;
        private DamageConditionsComponent _damageConditions;

        [SerializeField] private LayerMask targetLayers = ~0; // All layers by default

        private void Awake()
        {
            _dealer = GetComponent<IDamageDealer>();
            _damageConditions = GetComponent<DamageConditionsComponent>();
        }

        private void OnCollisionEnter2D(Collision2D collision)
        {
            GameObject target = collision.gameObject;
            if (((1 << target.layer) & targetLayers) == 0)
                return;
            IDamageable damageable = target.GetComponent<IDamageable>();
            if (damageable == null) return;

            if (_damageConditions && !_damageConditions.CanBeDamagedBy(target))
                return;

            int amount = _dealer?.GetDamageAmount() ?? 1;
            damageable.Damage(amount, gameObject);
        }
    }
}

using Health.Interfaces;
using System.Linq;
using UnityEngine;

namespace Health.Damage
{
    [DisallowMultipleComponent]
    public class BidirectionalDamageOnCollision : MonoBehaviour
    {
        private IDamageDealer _dealer;
        private DamageConditionsComponent _damageConditions;
        [SerializeField] private LayerMask targetLayers = ~0;

        private void Awake()
        {
            _dealer = GetComponent<IDamageDealer>();
            _damageConditions = GetComponent<DamageConditionsComponent>();
        }

        private void OnCollisionEnter2D(Collision2D collision)
        {
            GameObject other = collision.gameObject;
            GameObject self = gameObject;

            // 1. This object damages the other
            TryDealDamage(self, other, _dealer, _damageConditions, targetLayers);

            // 2. The other object damages this
            var otherDealer = other.GetComponent<IDamageDealer>();
            var otherConditions = other.GetComponent<DamageConditionsComponent>();
            TryDealDamage(other, self, otherDealer, otherConditions, targetLayers);
        }

        private static void TryDealDamage(
            GameObject dealerObj,
            GameObject targetObj,
            IDamageDealer dealer,
            DamageConditionsComponent conditions,
            LayerMask targetLayers)
        {
            if (dealer == null) return;
            if (((1 << targetObj.layer) & targetLayers) == 0) return;
            var damageable = targetObj.GetComponent<IDamageable>();
            if (damageable == null) return;
            if (conditions && !conditions.CanBeDamagedBy(targetObj)) return;
            int amount = dealer.GetDamageAmount();
            if (amount > 0)
                damageable.Damage(amount, dealerObj);
        }
    }
}

