                $"[LevelNavigationService] Requesting level selection through GameFlowManager for: {selectedLevel.levelName}");

            _gameFlowManager?.SelectLevel(selectedLevel.levelName, CurrentIndex);
        }

        public void SetGridWidth(int gridWidth)
        {
            _gridWidth = gridWidth;
            Debug.Log($"[LevelNavigationService] Grid width set to {_gridWidth}");
        }

        public void SetCurrentIndex(int index)
        {
            if (_levelData == null || index < 0 || index >= _levelData.Count) return;

            UpdateSelection(index);
        }

        private int CalculateNewIndex(Vector2 direction)
        {
            if (Mathf.Abs(direction.x) > Mathf.Abs(direction.y))
            {
                // Horizontal movement
                return direction.x > 0
                    ? Mathf.Min(CurrentIndex + 1, _levelData.Count - 1)
                    : Mathf.Max(CurrentIndex - 1, 0);
            }

            // Vertical movement - now uses the actual grid width from config
            int newIndex = direction.y > 0
                ? CurrentIndex - _gridWidth
                : CurrentIndex + _gridWidth;

            return Mathf.Clamp(newIndex, 0, _levelData.Count - 1);
        }

        private void UpdateSelection(int newIndex)
        {
            if (newIndex == CurrentIndex)
            {
                return;
            }

            int previousIndex = CurrentIndex;
            CurrentIndex = newIndex;

            // Only handle FLOW - let GameDataCoordinator handle the DATA when it receives this event
            _gameFlowManager?.NavigateLevel(previousIndex, CurrentIndex, Vector2.zero);
        }
    }
}

using System.Collections.Generic;
using UnityEngine;

namespace LevelSelection.Services
{
    /// <summary>
    ///     Handles selector visual state and movement (Single Responsibility)
    /// </summary>
    public class SelectorService : ISelectorService
    {
        private const float DefaultMoveSpeed = 5f;
        private const float DefaultSnapThreshold = 0.1f;

        private Vector3[] _levelPositions;
        private GameObject _selectorObject;
        private Vector3 _targetPosition;

        public bool IsMoving { get; private set; }

        public void Initialize(GameObject selectorObject)
        {
            _selectorObject = selectorObject;
        }

        public void SetLevelPositions(Vector3[] levelPositions)
        {
            _levelPositions = levelPositions;
        }

        public void MoveToPosition(Vector3 targetPosition)
        {
            if (_selectorObject == null) return;

            float distance = Vector3.Distance(_selectorObject.transform.position, targetPosition);
            if (distance > 0.01f)
            {
                _targetPosition = targetPosition;
                IsMoving = true;
            }
        }

        public void MoveToLevel(int levelIndex)
        {
            if (_levelPositions == null || levelIndex < 0 || levelIndex >= _levelPositions.Length) return;

            Vector3 targetPosition = _levelPositions[levelIndex];
            MoveToPosition(targetPosition);
        }

        public void SetVisible(bool visible)
        {
            if (_selectorObject != null)
            {
                _selectorObject.SetActive(visible);
            }
        }

        public void Update()
        {
            if (!IsMoving || _selectorObject == null) return;

            _selectorObject.transform.position = Vector3.MoveTowards(
                _selectorObject.transform.position,
                _targetPosition,
                DefaultMoveSpeed * Time.deltaTime
            );

            if (Vector3.Distance(_selectorObject.transform.position, _targetPosition) < DefaultSnapThreshold)
            {
                _selectorObject.transform.position = _targetPosition;
                IsMoving = false;
            }
        }
    }
}

using System.Collections.Generic;
using UnityEngine;

namespace LevelSelection.Services
{
    /// <summary>
    ///     Handles selector visual state and movement (Single Responsibility)
    /// </summary>
    public class SelectorService : ISelectorService
    {
        private const float DefaultMoveSpeed = 5f;
        private const float DefaultSnapThreshold = 0.1f;

        private GameObject _selectorObject;
        private Vector3 _targetPosition;

        public bool IsMoving { get; private set; }

        public void Initialize(GameObject selectorObject)
        {
            _selectorObject = selectorObject;
        }

        public void MoveToPosition(Vector3 targetPosition)
        {
            if (_selectorObject == null) return;

            float distance = Vector3.Distance(_selectorObject.transform.position, targetPosition);
            if (distance > 0.01f)
            {
                _targetPosition = targetPosition;
                IsMoving = true;
            }
        }

        public void MoveToCurrentLevel(ILevelNavigationService navigationService)
        {
            if (navigationService?.CurrentLevel != null)
            {
                MoveToPosition(navigationService.CurrentLevel.mapPosition);
            }
        }

        public void SetVisible(bool visible)
        {
            if (_selectorObject != null)
            {
                _selectorObject.SetActive(visible);
            }
        }

        public void Update()
        {
            if (!IsMoving || _selectorObject == null) return;

            _selectorObject.transform.position = Vector3.MoveTowards(
                _selectorObject.transform.position,
                _targetPosition,
                DefaultMoveSpeed * Time.deltaTime
            );

            if (Vector3.Distance(_selectorObject.transform.position, _targetPosition) < DefaultSnapThreshold)
            {
                _selectorObject.transform.position = _targetPosition;
                IsMoving = false;
            }
        }
    }
}

using UnityEngine;

namespace Player.Components
{
    /// <summary>
    /// Provides ledge assist functionality for one-way platforms.
    /// Helps players land on platforms when they jump near the edge.
    /// </summary>
    public class LedgeAssistController : MonoBehaviour
    {
        [Header("Ledge Assist Settings")]
        [SerializeField] private float assistRange = 0.5f;
        [SerializeField] private float assistStrength = 2f;
        [SerializeField] private float assistDuration = 0.2f;
        [SerializeField] private LayerMask platformLayers = -1;
        [SerializeField] private bool debugMode = false;

        [Header("Detection")]
        [SerializeField] private Vector2 detectionBoxSize = new Vector2(1.2f, 0.5f);
        [SerializeField] private Vector2 detectionOffset = new Vector2(0f, 0.3f);

        private Rigidbody2D _rigidbody;
        private bool _isAssisting;
        private float _assistTimer;
        private Vector3 _targetPlatformPosition;
        private bool _wasGrounded;

        // Events for integration with other systems
        public System.Action OnLedgeAssistTriggered;
        public System.Action OnLedgeAssistCompleted;

        private void Awake()
        {
            _rigidbody = GetComponent<Rigidbody2D>();
            if (_rigidbody == null)
            {
                Debug.LogError("[LedgeAssistController] Rigidbody2D component required!");
            }
        }

        private void Update()
        {
            HandleLedgeAssist();
        }

        private void HandleLedgeAssist()
        {
            if (_isAssisting)
            {
                UpdateAssist();
                return;
            }

            // Check if we should trigger ledge assist
            if (ShouldTriggerLedgeAssist())
            {
                TriggerLedgeAssist();
            }
        }

        private bool ShouldTriggerLedgeAssist()
        {
            // Only assist when player is moving upward (jumping)
            if (_rigidbody.velocity.y <= 0.1f) return false;

            // Don't assist if already grounded
            if (IsGrounded()) return false;

            // Check if there's a platform edge nearby
            return DetectNearbyPlatformEdge(out _targetPlatformPosition);
        }

        private bool DetectNearbyPlatformEdge(out Vector3 platformPosition)
        {
            platformPosition = Vector3.zero;

            // Cast a box above the player to detect platforms
            Vector2 castPosition = (Vector2)transform.position + detectionOffset;
            RaycastHit2D[] hits = Physics2D.BoxCastAll(
                castPosition,
                detectionBoxSize,
                0f,
                Vector2.up,
                0.1f,
                platformLayers
            );

            foreach (var hit in hits)
            {
                // Check if this is a one-way platform
                PlatformEffector2D platformEffector = hit.collider.GetComponent<PlatformEffector2D>();
                if (platformEffector != null && platformEffector.useOneWay)
                {
                    // Check if player is approaching the platform from the side
                    Vector2 platformCenter = hit.collider.bounds.center;
                    Vector2 playerPosition = transform.position;

                    // Find the closest edge point on the platform
                    Vector2 closestPoint = hit.collider.ClosestPoint(playerPosition);

                    // Check if we're within assist range horizontally
                    float horizontalDistance = Mathf.Abs(playerPosition.x - closestPoint.x);
                    float verticalDistance = closestPoint.y - playerPosition.y;

                    if (horizontalDistance <= assistRange && verticalDistance > 0 && verticalDistance <= assistRange * 2)
                    {
                        platformPosition = new Vector3(closestPoint.x, hit.collider.bounds.max.y + 0.1f, transform.position.z);
                        return true;
                    }
                }
            }

            return false;
        }

        private void TriggerLedgeAssist()
        {
            _isAssisting = true;
            _assistTimer = assistDuration;

            // Apply gentle pull toward the platform
            Vector2 direction = (_targetPlatformPosition - transform.position).normalized;
            direction.y = Mathf.Max(0.2f, direction.y); // Ensure some upward momentum

            OnLedgeAssistTriggered?.Invoke();

            if (debugMode)
            {
                Debug.Log($"[LedgeAssistController] Ledge assist triggered toward {_targetPlatformPosition}");
            }
        }

        private void UpdateAssist()
        {
            _assistTimer -= Time.deltaTime;

            if (_assistTimer <= 0 || IsGrounded())
            {
                _isAssisting = false;
                OnLedgeAssistCompleted?.Invoke();

                if (debugMode)
                {
                    Debug.Log("[LedgeAssistController] Ledge assist completed");
                }
                return;
            }

            // Apply gentle force toward the platform
            Vector2 direction = (_targetPlatformPosition - transform.position).normalized;
            Vector2 assistForce = direction * assistStrength * (_assistTimer / assistDuration);

            // Only apply horizontal assist if we're still moving upward
            if (_rigidbody.velocity.y > 0)
            {
                assistForce.y *= 0.3f; // Reduce vertical component
                _rigidbody.AddForce(assistForce, ForceMode2D.Force);
            }
        }

        private bool IsGrounded()
        {
            // Simple ground check - you can replace this with your existing ground detection
            Vector2 boxCenter = (Vector2)transform.position + Vector2.down * 0.1f;
            return Physics2D.OverlapBox(boxCenter, new Vector2(0.8f, 0.1f), 0f, platformLayers) != null;
        }

        // Public methods for external control
        public void SetAssistEnabled(bool enabled)
        {
            this.enabled = enabled;
        }

        public bool IsCurrentlyAssisting => _isAssisting;

        private void OnDrawGizmosSelected()
        {
            if (!debugMode) return;

            // Draw detection box
            Gizmos.color = Color.yellow;
            Vector3 boxCenter = transform.position + (Vector3)detectionOffset;
            Gizmos.DrawWireCube(boxCenter, detectionBoxSize);

            // Draw assist range
            Gizmos.color = Color.green;
            Gizmos.DrawWireSphere(transform.position, assistRange);

            // Draw target platform position when assisting
            if (_isAssisting)
            {
                Gizmos.color = Color.red;
                Gizmos.DrawWireSphere(_targetPlatformPosition, 0.2f);
                Gizmos.DrawLine(transform.position, _targetPlatformPosition);
            }
        }
    }
}

using Core;
using Core.Events;
using Health.Core;
using Health.Interfaces;
using Health.Views;
using Player.Interfaces;
using UnityEngine;
using VContainer;

namespace Player.Components
{
    public class PlayerHealthController : HealthComponent, IBypassableDamageable
    {
        [SerializeField] private BarsHealthView healthView;
        private IEventBus _eventBus;
        private GameFlowManager _gameFlowManager;
        private IHealthView _healthView;
        private IInvincibility _invincibility;
        private IPlayerLivesService _livesService;
        private IShield _shield;

        #region VContainer Injection

        [Inject]
        public void Construct(IEventBus eventBus, IPlayerLivesService livesService, GameFlowManager gameFlowManager)
        {
            _eventBus = eventBus;
            _livesService = livesService;
            _gameFlowManager = gameFlowManager;
        }

        #endregion

        #region Unity Lifecycle

        private new void Awake()
        {
            base.Awake();
            _healthView = healthView;
            _shield = GetComponent<IShield>();
            _invincibility = GetComponent<IInvincibility>();
        }

        protected void Start()
        {
            _healthView.UpdateDisplay(CurrentHp, MaxHp);
            OnHealthChanged += HandleHealthChanged;
            OnDeath += HandleHealthEmpty;
        }

        protected void OnDestroy()
        {
            OnHealthChanged -= HandleHealthChanged;
            OnDeath -= HandleHealthEmpty;
        }

        #endregion

        #region Event Handlers

        private void HandleHealthChanged(int hp, int maxHp)
        {
            _healthView.UpdateDisplay(hp, maxHp);
            _eventBus?.Publish(new PlayerHealthChangedEvent
            {
                CurrentHp = hp,
                MaxHp = maxHp,
                Damage = maxHp - hp,
                Timestamp = Time.time
            });
        }

        private void HandleHealthEmpty()
        {
            if (_livesService == null)
            {
                Debug.LogError("[PlayerHealthController] _livesService is null. Ensure it is properly injected.");
                return;
            }

            if (!_gameFlowManager)
            {
                Debug.LogError("[PlayerHealthController] _gameFlowManager is null. Ensure it is properly injected.");
                return;
            }

            if (_livesService.TryUseLife())
            {
                Debug.Log(
                    $"[PlayerHealthController] Used a life, waiting for scene reload. Lives remaining: {_livesService.CurrentLives}");

                return;
            }

            Debug.Log("[PlayerHealthController] No lives left, calling HandlePlayerDeath.");
            _gameFlowManager.HandlePlayerDeath(transform.position);
        }

        #endregion

        #region Damage Handling

        public override void Damage(int amount, GameObject source = null)
        {
            if (_invincibility is { IsInvincible: true })
                return;

            if (_shield is { IsActive: true })
            {
                Debug.Log("[PlayerHealthController] Shield active, breaking shield.");
                _shield.BreakShield(amount);
                return;
            }

            base.Damage(amount, source);
        }

        /// <summary>
        ///     Damage that bypasses transformation shield (used by continuous damage, etc.)
        /// </summary>
        public void DamageBypass(int amount)
        {
            base.Damage(amount, gameObject);
        }

        #endregion

    }
}

using Core;
using Health.Core;
using Health.Interfaces;
using Health.Views;
using Player.Interfaces;
using UnityEngine;
using VContainer;

namespace Player.Components
{
    public class PlayerHealthController : HealthComponent, IBypassableDamageable
    {
        [SerializeField] private BarsHealthView healthView;
        private GameFlowManager _gameFlowManager;
        private IHealthView _healthView;
        private IInvincibility _invincibility;
        private IPlayerLivesService _livesService;
        private IShield _shield;

        #region VContainer Injection

        [Inject]
        public void Construct(IPlayerLivesService livesService, GameFlowManager gameFlowManager)
        {
            _livesService = livesService;
            _gameFlowManager = gameFlowManager;
        }

        #endregion

        #region Unity Lifecycle

        private new void Awake()
        {
            base.Awake();
            _healthView = healthView;
            _shield = GetComponent<IShield>();
            _invincibility = GetComponent<IInvincibility>();
        }

        protected void Start()
        {
            _healthView.UpdateDisplay(CurrentHp, MaxHp);
            OnHealthChanged += HandleHealthChanged;
            OnDeath += HandleHealthEmpty;
        }

        protected void OnDestroy()
        {
            OnHealthChanged -= HandleHealthChanged;
            OnDeath -= HandleHealthEmpty;
        }

        #endregion

        #region Event Handlers

        private void HandleHealthChanged(int hp, int maxHp)
        {
            _healthView.UpdateDisplay(hp, maxHp);
            // Health changes are now handled purely as UI updates
            // No need to publish events for every health change
        }

        private void HandleHealthEmpty()
        {
            if (_livesService == null)
            {
                Debug.LogError("[PlayerHealthController] _livesService is null. Ensure it is properly injected.");
                return;
            }

            if (!_gameFlowManager)
            {
                Debug.LogError("[PlayerHealthController] _gameFlowManager is null. Ensure it is properly injected.");
                return;
            }

            if (_livesService.TryUseLife())
            {
                return;
            }

            _gameFlowManager.HandlePlayerDeath(transform.position);
        }

        #endregion

        #region Damage Handling

        public override void Damage(int amount, GameObject source = null)
        {
            if (_invincibility is { IsInvincible: true })
                return;

            if (_shield is { IsActive: true })
            {
                Debug.Log("[PlayerHealthController] Shield active, breaking shield.");
                _shield.BreakShield(amount);
                return;
            }

            base.Damage(amount, source);
        }

        /// <summary>
        ///     Damage that bypasses transformation shield (used by continuous damage, etc.)
        /// </summary>
        public void DamageBypass(int amount)
        {
            base.Damage(amount, gameObject);
        }

        #endregion

    }
}

using Health.Interfaces;
using UnityEngine;

namespace Player.Components
{
    /// <summary>
    /// When enabled, instantly kills any damageable object the player collides with.
    /// Should be enabled only when the player is invincible.
    /// </summary>
    [DisallowMultipleComponent]
    public class PlayerInvincibleDamageDealer : MonoBehaviour, IDamageDealer
    {
        [SerializeField] private int damageAmount = 9999; // "Kill" value
        [SerializeField] private LayerMask targetLayers = ~0; // All by default

        public int GetDamageAmount() => damageAmount;

        private void OnCollisionEnter2D(Collision2D collision)
        {
            if (((1 << collision.gameObject.layer) & targetLayers) == 0)
                return;
            var damageable = collision.gameObject.GetComponent<IDamageable>();
            if (damageable != null)
            {
                damageable.Damage(damageAmount, gameObject);
            }
        }
    }
}

using System;
using Core;
using Core.Data;
using Player.Interfaces;
using UnityEngine;

namespace Player.Services
{
    public class PlayerLivesService : IPlayerLivesService
    {
        private readonly GameDataCoordinator _gameDataCoordinator;

        public PlayerLivesService(GameDataCoordinator gameDataCoordinator)
        {
            _gameDataCoordinator = gameDataCoordinator;
            MaxLives = GameData.MaxLives;

            Debug.Log("[PlayerLivesService] Initialized with max lives: " + MaxLives);
        }

        public int CurrentLives => _gameDataCoordinator.GetCurrentData()?.lives ?? MaxLives;
        public int MaxLives { get; }
        public bool HasLivesRemaining => CurrentLives > 0;

        public event Action<int> OnLivesChanged;
        public event Action<Vector3> OnOneUpAwarded;

        public bool TryUseLife()
        {
            if (CurrentLives <= 0) return false;

            int newLives = CurrentLives - 1;
            _gameDataCoordinator.UpdateLives(newLives);
            OnLivesChanged?.Invoke(newLives);

            return newLives > 0;
        }

        public void ResetLives()
        {
            _gameDataCoordinator.UpdateLives(MaxLives);
            OnLivesChanged?.Invoke(MaxLives);
        }

        public void AddLife(Vector3 collectPosition)
        {
            int newLives = CurrentLives + 1;
            _gameDataCoordinator.UpdateLives(newLives);
            OnLivesChanged?.Invoke(newLives);
            OnOneUpAwarded?.Invoke(collectPosition);
        }
    }
}

using System;
using Core;
using Core.Data;
using Player.Interfaces;
using UnityEngine;

namespace Player.Services
{
    public class PlayerLivesService : IPlayerLivesService
    {
        private readonly GameDataCoordinator _gameDataCoordinator;

        public PlayerLivesService(GameDataCoordinator gameDataCoordinator)
        {
            _gameDataCoordinator = gameDataCoordinator;
            MaxLives = GameData.MaxLives;

            Debug.Log("[PlayerLivesService] Initialized with max lives: " + MaxLives);
        }

        public int CurrentLives => _gameDataCoordinator.GetCurrentLives();

        public int MaxLives => GameData.MaxLives;
        public bool HasLivesRemaining => CurrentLives > 0;

        public event Action<int> OnLivesChanged;
        public event Action<Vector3> OnOneUpAwarded;

        public bool TryUseLife()
        {
            if (CurrentLives <= 0) return false;

            int newLives = CurrentLives - 1;
            _gameDataCoordinator.UpdateLives(newLives);
            OnLivesChanged?.Invoke(newLives);

            return newLives > 0;
        }

        public void ResetLives()
        {
            _gameDataCoordinator.UpdateLives(MaxLives);
            OnLivesChanged?.Invoke(MaxLives);
        }

        public void AddLife(Vector3 collectPosition)
        {
            int newLives = CurrentLives + 1;
            _gameDataCoordinator.UpdateLives(newLives);
            OnLivesChanged?.Invoke(newLives);
            OnOneUpAwarded?.Invoke(collectPosition);
        }
    }
}

