using System.Collections.Generic;
using System.Threading.Tasks;
using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection;
using LevelSelection.Services;
using UnityEngine;
using VContainer;

namespace Core
{
    public class GameDataCoordinator : MonoBehaviour
    {

#if UNITY_EDITOR
        [Header("Debug")]
        [SerializeField] private bool resetGameData;
#endif

        private IAutoSaveService _autoSaveService;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private ILevelDiscoveryService _levelDiscoveryService;
        private bool _isInitialized;

        // Track previous values to detect changes
        private int _previousLives = -1;

        [Inject]
        public void Construct(
            IGameDataService gameDataService,
            IEventBus eventBus,
            IAutoSaveService autoSaveService,
            ILevelDiscoveryService levelDiscoveryService)
        {
            _gameDataService = gameDataService;
            _eventBus = eventBus;
            _autoSaveService = autoSaveService;
            _levelDiscoveryService = levelDiscoveryService;
            _isInitialized = true;

            Initialize();
        }

        private void Initialize()
        {
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Subscribe<LevelStartedEvent>(OnLevelStarted);
            _eventBus?.Subscribe<GameStateChangedEvent>(OnGameStateChanged);
            _eventBus?.Subscribe<PlayerDeathEvent>(OnPlayerDied);
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelNavigationEvent>(OnLevelNavigation);
        }

#if UNITY_EDITOR
        private void OnValidate()
        {
            if (resetGameData)
            {
                if (_gameDataService != null)
                {
                    _gameDataService.ResetAllData();
                    Debug.Log("Game data has been reset.");
                }
                else
                {
                    Debug.LogWarning("GameDataService not available. Cannot reset game data.");
                }
                resetGameData = false;
            }
        }
#endif

        private void SaveData()
        {
            _gameDataService?.SaveData();
        }

        private void Update()
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.Update();
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Unsubscribe<LevelStartedEvent>(OnLevelStarted);
            _eventBus?.Unsubscribe<GameStateChangedEvent>(OnGameStateChanged);
            _eventBus?.Unsubscribe<PlayerDeathEvent>(OnPlayerDied);
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelNavigationEvent>(OnLevelNavigation);

            _autoSaveService?.ForceSave();
        }

        private void OnApplicationPause(bool pauseStatus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationPause(pauseStatus);
        }

        private void OnApplicationFocus(bool hasFocus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationFocus(hasFocus);
        }

        private void OnPlayerDied(PlayerDeathEvent deathEvent)
        {
            _autoSaveService?.RequestSave();
        }

        private void OnLevelCompleted(LevelCompletedEvent levelEvent)
        {
            _gameDataService?.UpdateBestTime(levelEvent.LevelName, levelEvent.CompletionTime);
            _autoSaveService?.RequestSave();
        }

        private void OnGameStateChanged(GameStateChangedEvent stateEvent)
        {
            if (stateEvent.NewState is GameState.Victory or GameState.GameOver)
            {
                _autoSaveService?.ForceSave();
            }
        }

        private void OnLevelStarted(LevelStartedEvent levelEvent)
        {
            _gameDataService?.UpdateCurrentLevel(levelEvent.LevelName);
        }

        private void OnGameDataChanged(GameData newData)
        {
            // Check if lives changed and publish the appropriate event
            if (_previousLives != newData.lives)
            {
                _eventBus?.Publish(new PlayerLivesChangedEvent
                {
                    PreviousLives = _previousLives,
                    CurrentLives = newData.lives,
                    MaxLives = GameData.MaxLives,
                    Timestamp = Time.time
                });

                _previousLives = newData.lives;
            }

            _autoSaveService?.RequestSave();
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                gameData.currentLevel = levelEvent.LevelName;
            }
        }

        private void OnLevelNavigation(LevelNavigationEvent navigationEvent)
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = navigationEvent.NewIndex;
            }
        }

        // Public API for other systems to request data operations
        public void UpdateLives(int lives)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateLives(lives);
        }

        public void UpdateCurrentLevel(string levelName)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateCurrentLevel(levelName);
        }

        public void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateLevelProgress(levelName, isCompleted, completionTime);
        }

        public void UpdateScore(int score)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateScore(score);
        }

        public void AddFruitCollected()
        {
            if (!_isInitialized) return;
            _gameDataService?.AddFruitCollected();
        }

        public async Task<List<LevelData>> DiscoverLevelsAsync()
        {
            if (!_isInitialized || _gameDataService == null || _levelDiscoveryService == null)
                return new List<LevelData>();
            return await _gameDataService.GetLevelDataAsync(_levelDiscoveryService);
        }

        public GameData GetCurrentData()
        {
            return !_isInitialized ? null : _gameDataService?.CurrentData;
        }

        public void ResetAllData()
        {
            if (!_isInitialized) return;
            _gameDataService?.ResetAllData();
        }

        // Wrapper methods for GameData operations 
        public bool IsLevelUnlocked(string levelName)
        {
            return GetCurrentData()?.IsLevelUnlocked(levelName) ?? false;
        }

        public bool IsLevelCompleted(string levelName)
        {
            return GetCurrentData()?.IsLevelCompleted(levelName) ?? false;
        }

        public float GetLevelBestTime(string levelName)
        {
            return GetCurrentData()?.GetLevelBestTime(levelName) ?? float.MaxValue;
        }

        public int GetLevelBestScore(string levelName)
        {
            return GetCurrentData()?.GetLevelBestScore(levelName) ?? 0;
        }

        // Additional wrapper methods to avoid GetCurrentData() calls
        public int GetCurrentLives()
        {
            return GetCurrentData()?.lives ?? GameData.MaxLives;
        }

        public List<string> GetUnlockedLevels()
        {
            return GetCurrentData()?.unlockedLevels ?? new List<string> { "Level_01" };
        }

        public List<string> GetCompletedLevels()
        {
            return GetCurrentData()?.completedLevels ?? new List<string>();
        }

        public int GetSelectedLevelIndex()
        {
            return GetCurrentData()?.selectedLevelIndex ?? 0;
        }

        public void UnlockLevel(string levelName)
        {
            if (!_isInitialized) return;
            _gameDataService?.UnlockLevel(levelName);
        }

        public int GetCurrentScore()
        {
            return GetCurrentData()?.score ?? 0;
        }

        public int GetFruitCollectedCount()
        {
            return GetCurrentData()?.fruitCollected ?? 0;
        }
    }
}

using System.Collections.Generic;
using System.Threading.Tasks;
using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection;
using LevelSelection.Services;
using UnityEngine;
using VContainer;

namespace Core
{
    public class GameDataCoordinator : MonoBehaviour
    {

#if UNITY_EDITOR
        [Header("Debug")]
        [SerializeField] private bool resetGameData;
#endif

        private IAutoSaveService _autoSaveService;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private ILevelDiscoveryService _levelDiscoveryService;
        private bool _isInitialized;

        // Track previous values to detect changes
        private int _previousLives = -1;

        [Inject]
        public void Construct(
            IGameDataService gameDataService,
            IEventBus eventBus,
            IAutoSaveService autoSaveService,
            ILevelDiscoveryService levelDiscoveryService)
        {
            _gameDataService = gameDataService;
            _eventBus = eventBus;
            _autoSaveService = autoSaveService;
            _levelDiscoveryService = levelDiscoveryService;
            _isInitialized = true;

            Initialize();
        }

        private void Initialize()
        {
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Subscribe<LevelStartedEvent>(OnLevelStarted);
            _eventBus?.Subscribe<GameStateChangedEvent>(OnGameStateChanged);
            _eventBus?.Subscribe<PlayerDeathEvent>(OnPlayerDied);
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelNavigationEvent>(OnLevelNavigation);
        }

#if UNITY_EDITOR
        private void OnValidate()
        {
            if (resetGameData)
            {
                if (_gameDataService != null)
                {
                    _gameDataService.ResetAllData();
                    Debug.Log("Game data has been reset.");
                }
                else
                {
                    Debug.LogWarning("GameDataService not available. Cannot reset game data.");
                }
                resetGameData = false;
            }
        }
#endif

        private void SaveData()
        {
            _gameDataService?.SaveData();
        }

        private void Update()
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.Update();
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Unsubscribe<LevelStartedEvent>(OnLevelStarted);
            _eventBus?.Unsubscribe<GameStateChangedEvent>(OnGameStateChanged);
            _eventBus?.Unsubscribe<PlayerDeathEvent>(OnPlayerDied);
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelNavigationEvent>(OnLevelNavigation);

            _autoSaveService?.ForceSave();
        }

        private void OnApplicationPause(bool pauseStatus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationPause(pauseStatus);
        }

        private void OnApplicationFocus(bool hasFocus)
        {
            if (!_isInitialized || _autoSaveService == null) return;

            _autoSaveService.OnApplicationFocus(hasFocus);
        }

        private void OnPlayerDied(PlayerDeathEvent deathEvent)
        {
            _autoSaveService?.RequestSave();
        }

        private void OnLevelCompleted(LevelCompletedEvent levelEvent)
        {
            _gameDataService?.UpdateBestTime(levelEvent.LevelName, levelEvent.CompletionTime);
            _autoSaveService?.RequestSave();
        }

        private void OnGameStateChanged(GameStateChangedEvent stateEvent)
        {
            if (stateEvent.NewState is GameState.Victory or GameState.GameOver)
            {
                _autoSaveService?.ForceSave();
            }
        }

        private void OnLevelStarted(LevelStartedEvent levelEvent)
        {
            _gameDataService?.UpdateCurrentLevel(levelEvent.LevelName);
        }

        private void OnGameDataChanged(GameData newData)
        {
            // Check if lives changed and publish the appropriate event
            if (_previousLives != newData.lives)
            {
                _eventBus?.Publish(new PlayerLivesChangedEvent
                {
                    PreviousLives = _previousLives,
                    CurrentLives = newData.lives,
                    MaxLives = GameData.MaxLives,
                    Timestamp = Time.time
                });

                _previousLives = newData.lives;
            }

            _autoSaveService?.RequestSave();
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                gameData.currentLevel = levelEvent.LevelName;
            }
        }

        private void OnLevelNavigation(LevelNavigationEvent navigationEvent)
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = navigationEvent.NewIndex;
            }
        }

        // Public API for other systems to request data operations
        public void UpdateLives(int lives)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateLives(lives);
        }

        public void UpdateCurrentLevel(string levelName)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateCurrentLevel(levelName);
        }

        public void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateLevelProgress(levelName, isCompleted, completionTime);
        }

        public void UpdateScore(int score)
        {
            if (!_isInitialized) return;
            _gameDataService?.UpdateScore(score);
        }

        public void AddFruitCollected()
        {
            if (!_isInitialized) return;
            _gameDataService?.AddFruitCollected();
        }

        public async Task<List<LevelData>> DiscoverLevelsAsync()
        {
            if (!_isInitialized || _gameDataService == null || _levelDiscoveryService == null)
                return new List<LevelData>();
            return await _gameDataService.GetLevelDataAsync(_levelDiscoveryService);
        }

        public GameData GetCurrentData()
        {
            return !_isInitialized ? null : _gameDataService?.CurrentData;
        }

        public void ResetAllData()
        {
            if (!_isInitialized) return;
            _gameDataService?.ResetAllData();
        }

        public void ResetProgressData()
        {
            if (!_isInitialized) return;
            _gameDataService?.ResetProgressData();
        }

        // Wrapper methods for GameData operations 
        public bool IsLevelUnlocked(string levelName)
        {
            return GetCurrentData()?.IsLevelUnlocked(levelName) ?? false;
        }

        public bool IsLevelCompleted(string levelName)
        {
            return GetCurrentData()?.IsLevelCompleted(levelName) ?? false;
        }

        public float GetLevelBestTime(string levelName)
        {
            return GetCurrentData()?.GetLevelBestTime(levelName) ?? float.MaxValue;
        }

        public int GetLevelBestScore(string levelName)
        {
            return GetCurrentData()?.GetLevelBestScore(levelName) ?? 0;
        }

        // Additional wrapper methods to avoid GetCurrentData() calls
        public int GetCurrentLives()
        {
            return GetCurrentData()?.lives ?? GameData.MaxLives;
        }

        public List<string> GetUnlockedLevels()
        {
            return GetCurrentData()?.unlockedLevels ?? new List<string> { "Level_01" };
        }

        public List<string> GetCompletedLevels()
        {
            return GetCurrentData()?.completedLevels ?? new List<string>();
        }

        public int GetSelectedLevelIndex()
        {
            return GetCurrentData()?.selectedLevelIndex ?? 0;
        }

        public void UnlockLevel(string levelName)
        {
            if (!_isInitialized) return;
            _gameDataService?.UnlockLevel(levelName);
        }

        public int GetCurrentScore()
        {
            return GetCurrentData()?.score ?? 0;
        }

        public int GetFruitCollectedCount()
        {
            return GetCurrentData()?.fruitCollected ?? 0;
        }
    }
}

using System;
using System.Linq;
using System.Threading.Tasks;
using Core.Data;
using Core.Events;
using EasyTransition;
using LevelSelection.Services;
using Player.Components;
using UnityEngine;
using UnityEngine.SceneManagement;
using VContainer;

namespace Core
{
    public class GameFlowManager : MonoBehaviour
    {
        [Header("Game Settings")]
        [SerializeField] private float restartDelay = 2f;

        [Header("Victory Settings")]
        [SerializeField] private string victorySceneName = "YouWonScene";
        [SerializeField] private float victoryTransitionDelay = 3f;

        private string _currentLevelName = "Unknown";
        private float _levelStartTime;
        private IEventBus _eventBus;
        private GameDataCoordinator _gameDataCoordinator;
        private ISceneLoadService _sceneLoadService;

        private GameState CurrentState { get; set; } = GameState.MainMenu;

        [Inject]
        public void Construct(IEventBus eventBus, GameDataCoordinator gameDataCoordinator, ISceneLoadService sceneLoadService)
        {
            _eventBus = eventBus;
            _gameDataCoordinator = gameDataCoordinator;
            _sceneLoadService = sceneLoadService;
            SubscribeToEvents();
        }

        private void Start()
        {
            _currentLevelName = GetCurrentLevelName();

            // Only auto-start gameplay in actual level scenes
            // Other scenes (Level Select, Start Menu, etc.) will manage their own states
            if (ShouldAutoStartGameplay())
            {
                StartGameplay();
            }
        }

        private static bool ShouldAutoStartGameplay()
        {
            // Only auto-start gameplay in actual level scenes
            string sceneName = SceneManager.GetActiveScene().name;
            return !sceneName.Equals("Level Select", StringComparison.OrdinalIgnoreCase) && 
                   !sceneName.Contains("Start") &&
                   !sceneName.Equals("YouWonScene", StringComparison.OrdinalIgnoreCase);
        }

        private void OnDestroy()
        {
            UnsubscribeFromEvents();
        }

        private void StartGameplay()
        {
            Time.timeScale = 1;
            ChangeState(GameState.Playing);
            _levelStartTime = Time.time;

            _eventBus?.Publish(new LevelStartedEvent
            {
                LevelName = _currentLevelName,
                Timestamp = Time.time
            });
        }

        public void StartLevel(string levelName)
        {
            _currentLevelName = levelName;
            Time.timeScale = 1;
            ChangeState(GameState.Playing);
            _levelStartTime = Time.time;

            _eventBus?.Publish(new LevelStartedEvent
            {
                LevelName = levelName,
                Timestamp = Time.time
            });
        }

        public void PauseGame()
        {
            if (CurrentState == GameState.Playing)
                ChangeState(GameState.Paused);
        }

        public void ResumeGame()
        {
            if (CurrentState == GameState.Paused)
                ChangeState(GameState.Playing);
        }

        public void RestartLevel()
        {
            string currentSceneName = SceneManager.GetActiveScene().name;
            _sceneLoadService?.LoadLevel(currentSceneName);
        }

        public void HandlePlayerDeath(Vector3 deathPosition)
        {
            _eventBus?.Publish(new PlayerDeathEvent
            {
                DeathPosition = deathPosition,
                Timestamp = Time.time
            });
        }

        public void CompleteLevel(string currentLevelName)
        {
            float completionTime = Time.time - _levelStartTime;

            _eventBus?.Publish(new LevelCompletedEvent
            {
                LevelName = currentLevelName,
                CompletionTime = completionTime,
                Timestamp = Time.time
            });
        }

        public void RequestLevelLoad(string levelName, string sceneName)
        {
            _eventBus?.Publish(new LevelLoadRequestedEvent
            {
                Timestamp = Time.time,
                LevelName = levelName,
                SceneName = sceneName
            });
        }

        public void SelectLevel(string levelName, int levelIndex)
        {
            _eventBus?.Publish(new LevelSelectedEvent
            {
                Timestamp = Time.time,
                LevelName = levelName,
                LevelIndex = levelIndex
            });
        }

        public void NavigateLevel(int previousIndex, int newIndex, Vector2 direction)
        {
            _eventBus?.Publish(new LevelNavigationEvent
            {
                Timestamp = Time.time,
                PreviousIndex = previousIndex,
                NewIndex = newIndex,
                Direction = direction
            });
        }

        public void NavigateToLevelSelection()
        {
            try
            {
                ChangeState(GameState.LevelSelection);
                _sceneLoadService?.LoadLevel("Level Select");
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to navigate to level selection: {e}");
            }
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<GameOverEvent>(OnGameOver);
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Subscribe<PlayerLivesChangedEvent>(OnPlayerLivesChanged);
        }

        private void UnsubscribeFromEvents()
        {
            _eventBus?.Unsubscribe<GameOverEvent>(OnGameOver);
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
            _eventBus?.Unsubscribe<PlayerLivesChangedEvent>(OnPlayerLivesChanged);
        }

        private void OnGameOver(GameOverEvent gameOverEvent)
        {
            ChangeState(GameState.GameOver);
            RestartLevelAfterDelayAsync(restartDelay);
        }

        private async void OnLevelCompleted(LevelCompletedEvent levelEvent)
        {
            ChangeState(GameState.Victory);

            // First, check if the level was already completed
            bool wasAlreadyCompleted = _gameDataCoordinator.IsLevelCompleted(levelEvent.LevelName);

            // Now, update the progress (marks as complete, updates best time, etc.)
            _gameDataCoordinator.UpdateLevelProgress(levelEvent.LevelName, true, levelEvent.CompletionTime);

            // If it's the first time completing this level, unlock the next one
            if (!wasAlreadyCompleted)
            {
                await UnlockNextLevelByIndex(levelEvent.LevelName);
            }

            if (await IsGameCompletedAsync())
            {
                Debug.Log("[GameFlowManager] All levels completed! Transitioning to YouWonScene...");
                TransitionToVictorySceneAsync();
            }
            else
            {
                Debug.Log($"[GameFlowManager] Level {levelEvent.LevelName} completed. Returning to level selection...");
                ReturnToLevelSelectionAsync();
            }
        }

        private async Task UnlockNextLevelByIndex(string completedLevelName)
        {
            Debug.Log($"[GameFlowManager] UnlockNextLevelByIndex called for: {completedLevelName}");

            var allLevels = await _gameDataCoordinator.DiscoverLevelsAsync();
            if (allLevels == null || allLevels.Count == 0) 
            {
                Debug.LogWarning($"[GameFlowManager] No levels discovered for unlocking next level");
                return;
            }

            Debug.Log($"[GameFlowManager] Found {allLevels.Count} levels total");

            var completedLevel = allLevels.FirstOrDefault(l => l.levelName == completedLevelName);
            if (completedLevel == null) 
            {
                Debug.LogWarning($"[GameFlowManager] Could not find completed level: {completedLevelName}");
                return;
            }

            Debug.Log($"[GameFlowManager] Completed level index: {completedLevel.levelIndex}");
            int nextLevelIndex = completedLevel.levelIndex + 1;

            if (nextLevelIndex < allLevels.Count)
            {
                var nextLevel = allLevels.FirstOrDefault(l => l.levelIndex == nextLevelIndex);
                if (nextLevel != null)
                {
                    bool isAlreadyUnlocked = _gameDataCoordinator.IsLevelUnlocked(nextLevel.levelName);
                    Debug.Log($"[GameFlowManager] Next level '{nextLevel.levelName}' (index {nextLevelIndex}) - Already unlocked: {isAlreadyUnlocked}");

                    if (!isAlreadyUnlocked)
                    {
                        _gameDataCoordinator.UnlockLevel(nextLevel.levelName);
                        Debug.Log($"[GameFlowManager] Unlocked next level: {nextLevel.levelName}");
                    }
                    else
                    {
                        Debug.Log($"[GameFlowManager] Next level {nextLevel.levelName} was already unlocked");
                    }
                }
                else
                {
                    Debug.LogWarning($"[GameFlowManager] Could not find level with index {nextLevelIndex}");
                }
            }
            else
            {
                Debug.Log($"[GameFlowManager] No more levels to unlock (completed level was the last one)");
            }
        }

        private async Task<bool> IsGameCompletedAsync()
        {
            try
            {
                // Get all available levels through GameDataCoordinator
                var allLevels = await _gameDataCoordinator.DiscoverLevelsAsync();

                if (allLevels == null || allLevels.Count == 0)
                {
                    Debug.LogWarning("[GameFlowManager] No levels found in discovery service");
                    return false;
                }

                // Get completed levels using wrapper method
                var completedLevels = _gameDataCoordinator?.GetCompletedLevels();
                if (completedLevels == null) return false;

                // Check if all levels are in the completed list
                var completedCount = 0;
                foreach (var level in allLevels)
                {
                    if (completedLevels.Contains(level.levelName))
                    {
                        completedCount++;
                    }
                }

                Debug.Log($"[GameFlowManager] Game completion check: {completedCount}/{allLevels.Count} levels completed");

                if (completedCount >= allLevels.Count)
                {
                    Debug.Log("[GameFlowManager] All levels completed!");
                    return true;
                }

                var remainingLevels = allLevels.Where(l => !completedLevels.Contains(l.levelName)).Select(l => l.levelName);
                Debug.Log($"[GameFlowManager] Remaining levels: {string.Join(", ", remainingLevels)}");
                return false;
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Error checking game completion: {e}");
                return false;
            }
        }

        private async void TransitionToVictorySceneAsync()
        {
            try
            {
                await Task.Delay((int)(victoryTransitionDelay * 1000));

                _eventBus?.Publish(new GameCompletedEvent
                {
                    Timestamp = Time.time,
                    FinalLevelName = _currentLevelName
                });

                _sceneLoadService?.LoadLevel(victorySceneName);
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to transition to victory scene: {e}");
                _sceneLoadService?.LoadLevel("Level Select");
            }
        }

        private async void ReturnToLevelSelectionAsync()
        {
            try
            {
                await Task.Delay((int)(2f * 1000));
                ChangeState(GameState.LevelSelection);
                _sceneLoadService?.LoadLevel("Level Select");
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to return to level selection: {e}");
            }
        }

        private void OnPlayerLivesChanged(PlayerLivesChangedEvent livesEvent)
        {
            bool lostLife = livesEvent.PreviousLives > livesEvent.CurrentLives;
            bool isGameOver = livesEvent.CurrentLives == 0;

            if (isGameOver)
            {
                ChangeState(GameState.GameOver);
                _gameDataCoordinator?.ResetAllData();
                _eventBus?.Publish(new GameOverEvent { Timestamp = Time.time });
            }

            if (lostLife)
            {
                _eventBus?.Publish(new PlayerDeathEvent
                {
                    DeathPosition = PlayerLocator.PlayerTransform.position,
                    Timestamp = Time.time
                });
            }

            if (isGameOver || lostLife)
            {
                Time.timeScale = 0.01f;
                RestartLevelAfterDelayAsync(restartDelay);
            }
        }

        private async void RestartLevelAfterDelayAsync(float delay)
        {
            try
            {
                await Task.Delay((int)(delay * 1000));
                string currentSceneName = SceneManager.GetActiveScene().name;
                _sceneLoadService?.LoadLevel(currentSceneName);
            }
            catch (Exception e)
            {
                Debug.LogError($"[GameFlowManager] Failed to restart level after delay: {e}");
            }
        }

        private void ChangeState(GameState newState)
        {
            if (CurrentState == newState) return;

            GameState oldState = CurrentState;
            CurrentState = newState;

            _eventBus?.Publish(new GameStateChangedEvent
            {
                PreviousState = oldState,
                NewState = newState,
                Timestamp = Time.time
            });
        }

        private string GetCurrentLevelName() => SceneManager.GetActiveScene().name;
    }
}

