using System.Collections.Generic;
using UnityEngine;

namespace LevelSelection
{
    /// <summary>
    /// Director pattern implementation for building level data structures
    /// </summary>
    public class LevelSelectionDirector
    {
        public List<LevelData> BuildLevelData(List<GameObject> levelGameObjects)
        {
            var levelDataList = new List<LevelData>();

            for (int i = 0; i < levelGameObjects.Count; i++)
            {
                var levelObject = levelGameObjects[i];
                if (levelObject == null) continue;

                var levelData = LevelDataFactory.CreateFromGameObject(levelObject, i);
                if (levelData != null)
                {
                    levelDataList.Add(levelData);
                }
                else
                {
                    // Fallback - create basic level data from GameObject name
                    var fallbackData = LevelDataFactory.CreateDefault(
                        levelObject.name,
                        levelObject.name,
                        levelObject.transform.position,
                        i
                    );
                    levelDataList.Add(fallbackData);
                }
            }

            return levelDataList;
        }

        public LevelData BuildSingleLevel(GameObject levelObject, int index)
        {
            if (levelObject == null) return null;

            return LevelDataFactory.CreateFromGameObject(levelObject, index) ??
                   LevelDataFactory.CreateDefault(levelObject.name, levelObject.name, levelObject.transform.position, index);
        }
    }
}

using System.Collections.Generic;
using UnityEngine;

namespace LevelSelection
{
    /// <summary>
    /// Director pattern implementation for building level data structures
    /// </summary>
    public class LevelSelectionDirector
    {
        public List<LevelData> BuildLevelData(List<GameObject> levelGameObjects)
        {
            var levelDataList = new List<LevelData>();

            for (int i = 0; i < levelGameObjects.Count; i++)
            {
                var levelObject = levelGameObjects[i];
                if (levelObject == null) continue;

                // Try to create from LevelPoint component first
                var levelData = LevelDataFactory.CreateFromGameObject(levelObject, i);

                if (levelData == null)
                {
                    // Fallback - create from transform position and object name
                    Debug.Log($"Creating level data from transform for {levelObject.name} at position {levelObject.transform.position}");
                    levelData = LevelDataFactory.CreateFromTransform(levelObject, i);
                }

                if (levelData != null)
                {
                    Debug.Log($"Level {i}: {levelData.levelName} at position {levelData.mapPosition}");
                    levelDataList.Add(levelData);
                }
            }

            return levelDataList;
        }

        public LevelData BuildSingleLevel(GameObject levelObject, int index)
        {
            if (levelObject == null) return null;

            // Try LevelPoint component first, fallback to transform-based creation
            return LevelDataFactory.CreateFromGameObject(levelObject, index) ??
                   LevelDataFactory.CreateFromTransform(levelObject, index);
        }
    }
}

using System.Collections.Generic;
using System.Linq;
using Core.Data;
using Core.Events;
using UnityEngine;
using UnityEngine.InputSystem;
using UnityEngine.SceneManagement;
using VContainer;

namespace LevelSelection
{
    public class LevelSelectionManager : MonoBehaviour
    {
        [Header("Level Configuration")]
        [SerializeField] private bool autoDiscoverLevels = true;
        [SerializeField] private bool autoActivateOnStart = true; 
        [SerializeField] private string levelTagFilter = ""; 
        public List<GameObject> levelGameObjects = new();
        public Transform levelContainer;

        [Header("Components")] public LevelSelector levelSelector;

        public ItemSelectScreen itemSelectScreen;
        public NESCrossfade crossfade;

        [Header("Input Actions")]
        [SerializeField] private InputActionReference navigateAction;
        [SerializeField] private InputActionReference submitAction;

        private List<LevelData> _levelData;
        private List<LevelPoint> _levelPoints;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private bool _isActive = false;
        private LevelSelectionDirector _director;

        public bool IsActive
        {
            get => _isActive;
            private set => _isActive = value;
        }

        private void Awake()
        {
            _director = new LevelSelectionDirector();
            InitializeLevelSelection();
        }

        private void OnEnable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.Enable();
                navigateAction.action.performed += OnNavigate;
            }

            if (submitAction != null)
            {
                submitAction.action.Enable();
                submitAction.action.performed += OnConfirm;
            }
        }

        private void OnDisable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.performed -= OnNavigate;
                navigateAction.action.Disable();
            }

            if (submitAction != null)
            {
                submitAction.action.performed -= OnConfirm;
                submitAction.action.Disable();
            }
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        [Inject]
        public void Construct(IEventBus eventBus, IGameDataService gameDataService)
        {
            _eventBus = eventBus;
            _gameDataService = gameDataService;

            SubscribeToEvents();
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        private void InitializeLevelSelection()
        {
            // Auto-discover level objects
            if (autoDiscoverLevels)
            {
                AutoDiscoverLevelObjects();
            }
            // Manual discovery from container (fallback)
            else if (levelContainer != null && levelGameObjects.Count == 0)
            {
                for (int i = 0; i < levelContainer.childCount; i++)
                {
                    Transform child = levelContainer.GetChild(i);
                    if (child.GetComponent<LevelPoint>() != null)
                    {
                        levelGameObjects.Add(child.gameObject);
                    }
                }
            }

            Debug.Log($"[LevelSelectionManager] Found {levelGameObjects.Count} level objects");

            // Use director pattern to build level data
            _levelData = _director.BuildLevelData(levelGameObjects);
            _levelPoints = levelGameObjects.Select(go => go.GetComponent<LevelPoint>()).ToList();

            LoadLevelProgressFromGameData();

            GameData gameData = _gameDataService?.CurrentData;
            int selectedIndex = gameData?.selectedLevelIndex ?? 0;

            levelSelector?.Initialize(_levelData, _levelPoints, selectedIndex);

            // Auto-activate level selection on start if enabled
            if (autoActivateOnStart)
            {
                ActivateLevelSelection();
            }
        }

        private void AutoDiscoverLevelObjects()
        {
            levelGameObjects.Clear();

            // Find all GameObjects with LevelPoint components in the scene
            LevelPoint[] allLevelPoints = FindObjectsByType<LevelPoint>(FindObjectsSortMode.None);

            Debug.Log($"[LevelSelectionManager] Auto-discovered {allLevelPoints.Length} LevelPoint components");

            foreach (LevelPoint levelPoint in allLevelPoints)
            {
                // Optional tag filtering
                if (!string.IsNullOrEmpty(levelTagFilter) && !levelPoint.CompareTag(levelTagFilter))
                {
                    continue;
                }

                levelGameObjects.Add(levelPoint.gameObject);
            }

            // Sort by name or position for consistent ordering
            levelGameObjects.Sort((a, b) => string.Compare(a.name, b.name, System.StringComparison.Ordinal));

            Debug.Log($"[LevelSelectionManager] After filtering and sorting: {levelGameObjects.Count} level objects");
        }

        private void LoadLevelProgressFromGameData()
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData == null) return;

            // Update unlock status based on game data
            for (int i = 0; i < _levelData.Count; i++)
            {
                LevelData level = _levelData[i];
                level.isUnlocked = gameData.unlockedLevels.Contains(level.levelName);

                if (gameData.levelCompleted.TryGetValue(level.levelName, out bool completed))
                {
                    level.isCompleted = completed;
                }

                if (gameData.levelBestTimes.TryGetValue(level.levelName, out float bestTime))
                {
                    level.bestTime = bestTime;
                }
            }
        }

        private void OnNavigate(InputAction.CallbackContext context)
        {
            if (!IsActive || levelSelector == null)
            {
                Debug.Log($"[LevelSelectionManager] Navigate blocked - IsActive: {IsActive}, levelSelector: {levelSelector != null}");
                return;
            }

            Vector2 direction = context.ReadValue<Vector2>();
            Debug.Log($"[LevelSelectionManager] Navigate input received: {direction}");

            levelSelector.Navigate(direction);
            SaveSelectedLevel();
        }

        private void OnConfirm(InputAction.CallbackContext context)
        {
            if (!IsActive || levelSelector == null) return;

            levelSelector.SelectCurrentLevel();
        }

        private void SaveSelectedLevel()
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelSelector.CurrentIndex;
                _gameDataService?.SaveData();
            }
        }

        public void ActivateLevelSelection()
        {
            IsActive = true;
            gameObject.SetActive(true);
            Debug.Log($"[LevelSelectionManager] Level selection activated. Level count: {_levelData?.Count ?? 0}");
        }

        public void DeactivateLevelSelection()
        {
            IsActive = false;
            gameObject.SetActive(false);
        }

        // Debug method to check system status
        [ContextMenu("Debug Level Selection Status")]
        public void DebugStatus()
        {
            Debug.Log($"=== Level Selection Debug Status ===");
            Debug.Log($"IsActive: {IsActive}");
            Debug.Log($"Navigate Action Assigned: {navigateAction != null}");
            Debug.Log($"Submit Action Assigned: {submitAction != null}");
            Debug.Log($"Level Selector Assigned: {levelSelector != null}");
            Debug.Log($"Level Data Count: {_levelData?.Count ?? 0}");
            Debug.Log($"Level GameObjects Count: {levelGameObjects.Count}");

            if (levelSelector != null)
            {
                Debug.Log($"Selector Object Assigned: {levelSelector.selectorObject != null}");
                Debug.Log($"Current Index: {levelSelector.CurrentIndex}");
                Debug.Log($"Is Moving: {levelSelector.IsMoving}");
            }
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            LevelData selectedLevel = _levelData.FirstOrDefault(l => l.levelName == levelEvent.LevelName);
            if (selectedLevel == null) return;

            // Update current level in game data
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.currentLevel = selectedLevel.levelName;
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                _gameDataService?.SaveData();
            }

            // Show item select screen
            _eventBus?.Publish(new ItemSelectScreenRequestedEvent
            {
                Timestamp = Time.time,
                LevelName = selectedLevel.levelName
            });

            itemSelectScreen?.ShowItemSelect(selectedLevel.levelName, selectedLevel.sceneName);
        }

        private void OnLevelLoadRequested(LevelLoadRequestedEvent loadEvent)
        {
            // Start crossfade and load scene
            crossfade?.FadeOutAndIn(
                () =>
                {
                    // Load the scene
                    SceneManager.LoadScene(loadEvent.SceneName);
                },
                () =>
                {
                    // Publish level started event
                    _eventBus?.Publish(new LevelStartedEvent
                    {
                        Timestamp = Time.time,
                        LevelName = loadEvent.LevelName
                    });
                }
            );
        }

        private void OnLevelCompleted(LevelCompletedEvent completedEvent)
        {
            // Update level completion status and unlock next level
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData == null) return;

            // Mark level as completed
            gameData.levelCompleted[completedEvent.LevelName] = true;

            // Update best time
            if (!gameData.levelBestTimes.ContainsKey(completedEvent.LevelName) ||
                completedEvent.CompletionTime < gameData.levelBestTimes[completedEvent.LevelName])
            {
                gameData.levelBestTimes[completedEvent.LevelName] = completedEvent.CompletionTime;
            }

            // Unlock next level
            int completedIndex = _levelData.FindIndex(l => l.levelName == completedEvent.LevelName);
            if (completedIndex >= 0 && completedIndex + 1 < _levelData.Count)
            {
                string nextLevelName = _levelData[completedIndex + 1].levelName;
                if (!gameData.unlockedLevels.Contains(nextLevelName))
                {
                    gameData.unlockedLevels.Add(nextLevelName);
                }
            }

            _gameDataService?.SaveData();

            // Refresh level data if we're still in level selection
            if (IsActive)
            {
                LoadLevelProgressFromGameData();
                levelSelector?.Initialize(_levelData, _levelPoints, levelSelector.CurrentIndex);
            }
        }

        public List<LevelData> GetLevelData() => _levelData;
    }
}

using System.Collections.Generic;
using System.Linq;
using Core.Data;
using Core.Events;
using UnityEngine;
using UnityEngine.InputSystem;
using UnityEngine.SceneManagement;
using VContainer;

namespace LevelSelection
{
    public class LevelSelectionManager : MonoBehaviour
    {
        [Header("Level Configuration")]
        [SerializeField] private bool autoActivateOnStart = true;

        [Header("Components - Auto-discovered if not assigned")]
        public LevelSelector levelSelector;
        public ItemSelectScreen itemSelectScreen;
        public NESCrossfade crossfade;

        [Header("Input Actions - Auto-assigned from UI action map")]
        [SerializeField] private InputActionReference navigateAction;
        [SerializeField] private InputActionReference submitAction;

        // All private - no manual setup required
        private List<GameObject> _levelGameObjects = new();
        private List<LevelData> _levelData;
        private List<LevelPoint> _levelPoints;
        private IEventBus _eventBus;
        private IGameDataService _gameDataService;
        private bool _isActive = false;
        private LevelSelectionDirector _director;

        public bool IsActive
        {
            get => _isActive;
            private set => _isActive = value;
        }

        private void Awake()
        {
            _director = new LevelSelectionDirector();
            AutoDiscoverComponents();
            InitializeLevelSelection();
        }

        private void OnEnable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.Enable();
                navigateAction.action.performed += OnNavigate;
            }

            if (submitAction != null)
            {
                submitAction.action.Enable();
                submitAction.action.performed += OnConfirm;
            }
        }

        private void OnDisable()
        {
            if (navigateAction != null)
            {
                navigateAction.action.performed -= OnNavigate;
                navigateAction.action.Disable();
            }

            if (submitAction != null)
            {
                submitAction.action.performed -= OnConfirm;
                submitAction.action.Disable();
            }
        }

        private void OnDestroy()
        {
            _eventBus?.Unsubscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Unsubscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Unsubscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        [Inject]
        public void Construct(IEventBus eventBus, IGameDataService gameDataService)
        {
            _eventBus = eventBus;
            _gameDataService = gameDataService;

            SubscribeToEvents();
        }

        private void SubscribeToEvents()
        {
            _eventBus?.Subscribe<LevelSelectedEvent>(OnLevelSelected);
            _eventBus?.Subscribe<LevelLoadRequestedEvent>(OnLevelLoadRequested);
            _eventBus?.Subscribe<LevelCompletedEvent>(OnLevelCompleted);
        }

        private void InitializeLevelSelection()
        {
            // Auto-discover level objects
            AutoDiscoverLevelObjects();

            Debug.Log($"[LevelSelectionManager] Found {_levelGameObjects.Count} level objects");

            // Use director pattern to build level data
            _levelData = _director.BuildLevelData(_levelGameObjects);
            _levelPoints = _levelGameObjects.Select(go => go.GetComponent<LevelPoint>()).ToList();

            LoadLevelProgressFromGameData();

            GameData gameData = _gameDataService?.CurrentData;
            int selectedIndex = gameData?.selectedLevelIndex ?? 0;

            levelSelector?.Initialize(_levelData, _levelPoints, selectedIndex);

            // Auto-activate level selection on start if enabled
            if (autoActivateOnStart)
            {
                ActivateLevelSelection();
            }
        }

        private void AutoDiscoverComponents()
        {
            // Automatically discover and assign components in the scene
            if (levelSelector == null)
            {
                levelSelector = FindObjectOfType<LevelSelector>();
            }

            if (itemSelectScreen == null)
            {
                itemSelectScreen = FindObjectOfType<ItemSelectScreen>();
            }

            if (crossfade == null)
            {
                crossfade = FindObjectOfType<NESCrossfade>();
            }

            // Log any missing components
            if (levelSelector == null)
            {
                Debug.LogError("[LevelSelectionManager] LevelSelector not found in the scene.");
            }

            if (itemSelectScreen == null)
            {
                Debug.LogError("[LevelSelectionManager] ItemSelectScreen not found in the scene.");
            }

            if (crossfade == null)
            {
                Debug.LogError("[LevelSelectionManager] NESCrossfade not found in the scene.");
            }
        }

        private void AutoDiscoverLevelObjects()
        {
            _levelGameObjects.Clear();

            // Find all GameObjects with LevelPoint components in the scene
            LevelPoint[] allLevelPoints = FindObjectsByType<LevelPoint>(FindObjectsSortMode.None);

            Debug.Log($"[LevelSelectionManager] Auto-discovered {allLevelPoints.Length} LevelPoint components");

            foreach (LevelPoint levelPoint in allLevelPoints)
            {
                _levelGameObjects.Add(levelPoint.gameObject);
            }

            // Sort by name or position for consistent ordering
            _levelGameObjects.Sort((a, b) => string.Compare(a.name, b.name, System.StringComparison.Ordinal));

            Debug.Log($"[LevelSelectionManager] After sorting: {_levelGameObjects.Count} level objects");
        }

        private void LoadLevelProgressFromGameData()
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData == null) return;

            // Update unlock status based on game data
            for (int i = 0; i < _levelData.Count; i++)
            {
                LevelData level = _levelData[i];
                level.isUnlocked = gameData.unlockedLevels.Contains(level.levelName);

                if (gameData.levelCompleted.TryGetValue(level.levelName, out bool completed))
                {
                    level.isCompleted = completed;
                }

                if (gameData.levelBestTimes.TryGetValue(level.levelName, out float bestTime))
                {
                    level.bestTime = bestTime;
                }
            }
        }

        private void OnNavigate(InputAction.CallbackContext context)
        {
            if (!IsActive || levelSelector == null)
            {
                Debug.Log($"[LevelSelectionManager] Navigate blocked - IsActive: {IsActive}, levelSelector: {levelSelector != null}");
                return;
            }

            Vector2 direction = context.ReadValue<Vector2>();
            Debug.Log($"[LevelSelectionManager] Navigate input received: {direction}");

            levelSelector.Navigate(direction);
            SaveSelectedLevel();
        }

        private void OnConfirm(InputAction.CallbackContext context)
        {
            if (!IsActive || levelSelector == null) return;

            levelSelector.SelectCurrentLevel();
        }

        private void SaveSelectedLevel()
        {
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.selectedLevelIndex = levelSelector.CurrentIndex;
                _gameDataService?.SaveData();
            }
        }

        public void ActivateLevelSelection()
        {
            IsActive = true;
            gameObject.SetActive(true);
            Debug.Log($"[LevelSelectionManager] Level selection activated. Level count: {_levelData?.Count ?? 0}");
        }

        public void DeactivateLevelSelection()
        {
            IsActive = false;
            gameObject.SetActive(false);
        }

        // Debug method to check system status
        [ContextMenu("Debug Level Selection Status")]
        public void DebugStatus()
        {
            Debug.Log($"=== Level Selection Debug Status ===");
            Debug.Log($"IsActive: {IsActive}");
            Debug.Log($"Navigate Action Assigned: {navigateAction != null}");
            Debug.Log($"Submit Action Assigned: {submitAction != null}");
            Debug.Log($"Level Selector Assigned: {levelSelector != null}");
            Debug.Log($"Level Data Count: {_levelData?.Count ?? 0}");
            Debug.Log($"Level GameObjects Count: {_levelGameObjects.Count}");

            if (levelSelector != null)
            {
                Debug.Log($"Selector Object Assigned: {levelSelector.selectorObject != null}");
                Debug.Log($"Current Index: {levelSelector.CurrentIndex}");
                Debug.Log($"Is Moving: {levelSelector.IsMoving}");
            }
        }

        private void OnLevelSelected(LevelSelectedEvent levelEvent)
        {
            LevelData selectedLevel = _levelData.FirstOrDefault(l => l.levelName == levelEvent.LevelName);
            if (selectedLevel == null) return;

            // Update current level in game data
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData != null)
            {
                gameData.currentLevel = selectedLevel.levelName;
                gameData.selectedLevelIndex = levelEvent.LevelIndex;
                _gameDataService?.SaveData();
            }

            // Show item select screen
            _eventBus?.Publish(new ItemSelectScreenRequestedEvent
            {
                Timestamp = Time.time,
                LevelName = selectedLevel.levelName
            });

            itemSelectScreen?.ShowItemSelect(selectedLevel.levelName, selectedLevel.sceneName);
        }

        private void OnLevelLoadRequested(LevelLoadRequestedEvent loadEvent)
        {
            // Start crossfade and load scene
            crossfade?.FadeOutAndIn(
                () =>
                {
                    // Load the scene
                    SceneManager.LoadScene(loadEvent.SceneName);
                },
                () =>
                {
                    // Publish level started event
                    _eventBus?.Publish(new LevelStartedEvent
                    {
                        Timestamp = Time.time,
                        LevelName = loadEvent.LevelName
                    });
                }
            );
        }

        private void OnLevelCompleted(LevelCompletedEvent completedEvent)
        {
            // Update level completion status and unlock next level
            GameData gameData = _gameDataService?.CurrentData;
            if (gameData == null) return;

            // Mark level as completed
            gameData.levelCompleted[completedEvent.LevelName] = true;

            // Update best time
            if (!gameData.levelBestTimes.ContainsKey(completedEvent.LevelName) ||
                completedEvent.CompletionTime < gameData.levelBestTimes[completedEvent.LevelName])
            {
                gameData.levelBestTimes[completedEvent.LevelName] = completedEvent.CompletionTime;
            }

            // Unlock next level
            int completedIndex = _levelData.FindIndex(l => l.levelName == completedEvent.LevelName);
            if (completedIndex >= 0 && completedIndex + 1 < _levelData.Count)
            {
                string nextLevelName = _levelData[completedIndex + 1].levelName;
                if (!gameData.unlockedLevels.Contains(nextLevelName))
                {
                    gameData.unlockedLevels.Add(nextLevelName);
                }
            }

            _gameDataService?.SaveData();

            // Refresh level data if we're still in level selection
            if (IsActive)
            {
                LoadLevelProgressFromGameData();
                levelSelector?.Initialize(_levelData, _levelPoints, levelSelector.CurrentIndex);
            }
        }

        public List<LevelData> GetLevelData() => _levelData;
    }
}

using System.Collections.Generic;
using Core.Events;
using UnityEngine;
using VContainer;

namespace LevelSelection
{
    public class LevelSelector : MonoBehaviour
    {
        [Header("Selector Configuration")] public GameObject selectorObject;

        public float moveSpeed = 5f;
        public AudioClip navigationSound;
        public AudioClip selectionSound;
        public AudioClip lockedSound;

        [Header("Grid Navigation")] public int gridWidth = 4;

        public float snapThreshold = 0.1f;
        private AudioSource _audioSource;

        private List<LevelData> _availableLevels;
        private IEventBus _eventBus;
        private List<LevelPoint> _levelPoints;
        private Vector3 _targetPosition;

        public int CurrentIndex { get; private set; }

        public LevelData CurrentLevel => _availableLevels?[CurrentIndex];
        public bool IsMoving { get; private set; }

        private void Awake()
        {
            _audioSource = GetComponent<AudioSource>();
            if (_audioSource == null)
            {
                _audioSource = gameObject.AddComponent<AudioSource>();
            }
        }

        private void Update()
        {
            if (IsMoving && selectorObject != null)
            {
                selectorObject.transform.position = Vector3.MoveTowards(
                    selectorObject.transform.position,
                    _targetPosition,
                    moveSpeed * Time.deltaTime
                );

                if (Vector3.Distance(selectorObject.transform.position, _targetPosition) < snapThreshold)
                {
                    selectorObject.transform.position = _targetPosition;
                    IsMoving = false;
                }
            }
        }

        [Inject]
        public void Construct(IEventBus eventBus)
        {
            _eventBus = eventBus;
        }

        public void Initialize(List<LevelData> levels, List<LevelPoint> levelPoints, int selectedIndex = 0)
        {
            _availableLevels = levels;
            _levelPoints = levelPoints;
            CurrentIndex = Mathf.Clamp(selectedIndex, 0, levels.Count - 1);

            UpdateLevelStates();
            MoveToCurrentLevel(true); // Instant move on initialization
        }

        public void Navigate(Vector2 direction)
        {
            if (IsMoving || _availableLevels == null || _availableLevels.Count == 0)
                return;

            int newIndex = CalculateNewIndex(direction);

            if (newIndex != CurrentIndex && newIndex >= 0 && newIndex < _availableLevels.Count)
            {
                int previousIndex = CurrentIndex;
                CurrentIndex = newIndex;

                PlaySound(navigationSound);
                UpdateSelection();
                MoveToCurrentLevel();

                _eventBus?.Publish(new LevelNavigationEvent
                {
                    Timestamp = Time.time,
                    PreviousIndex = previousIndex,
                    NewIndex = CurrentIndex,
                    Direction = direction
                });
            }
        }

        private int CalculateNewIndex(Vector2 direction)
        {
            // Adventure Island III style navigation - mostly horizontal with some vertical
            int currentRow = CurrentIndex / gridWidth;
            int currentCol = CurrentIndex % gridWidth;

            if (Mathf.Abs(direction.x) > Mathf.Abs(direction.y))
            {
                // Horizontal movement
                if (direction.x > 0) // Right
                {
                    return Mathf.Min(CurrentIndex + 1, _availableLevels.Count - 1);
                }

                // Left
                return Mathf.Max(CurrentIndex - 1, 0);
            }

            // Vertical movement
            if (direction.y > 0) // Up
            {
                int newIndex = CurrentIndex - gridWidth;
                return newIndex >= 0 ? newIndex : CurrentIndex;
            }
            else // Down
            {
                int newIndex = CurrentIndex + gridWidth;
                return newIndex < _availableLevels.Count ? newIndex : CurrentIndex;
            }
        }

        public void SelectCurrentLevel()
        {
            if (_availableLevels == null || CurrentIndex >= _availableLevels.Count)
                return;

            LevelData selectedLevel = _availableLevels[CurrentIndex];

            if (!selectedLevel.isUnlocked)
            {
                PlaySound(lockedSound);
                return;
            }

            PlaySound(selectionSound);

            _eventBus?.Publish(new LevelSelectedEvent
            {
                Timestamp = Time.time,
                LevelName = selectedLevel.levelName,
                LevelIndex = CurrentIndex
            });
        }

        private void UpdateSelection()
        {
            for (int i = 0; i < _levelPoints.Count; i++)
            {
                if (_levelPoints[i] != null)
                {
                    _levelPoints[i].SetSelected(i == CurrentIndex);
                }
            }
        }

        private void UpdateLevelStates()
        {
            for (int i = 0; i < _levelPoints.Count && i < _availableLevels.Count; i++)
            {
                if (_levelPoints[i] != null)
                {
                    _levelPoints[i].SetUnlocked(_availableLevels[i].isUnlocked);
                    _levelPoints[i].SetSelected(i == CurrentIndex);
                }
            }
        }

        private void MoveToCurrentLevel(bool instant = false)
        {
            if (CurrentIndex >= _levelPoints.Count || _levelPoints[CurrentIndex] == null)
                return;

            _targetPosition = _levelPoints[CurrentIndex].transform.position;

            if (instant)
            {
                selectorObject.transform.position = _targetPosition;
                IsMoving = false;
            }
            else
            {
                IsMoving = true;
            }
        }

        private void PlaySound(AudioClip clip)
        {
            if (_audioSource && clip)
            {
                _audioSource.PlayOneShot(clip);
            }
        }
    }
}

using System.Collections.Generic;
using Core.Events;
using UnityEngine;
using VContainer;

namespace LevelSelection
{
    public class LevelSelector : MonoBehaviour
    {
        [Header("Selector Configuration")] public GameObject selectorObject;

