using Collectables.Counter;
using UnityEngine;
using VContainer;

namespace Collectables.Score
{
    public class ScoreController : MonoBehaviour
    {
        [SerializeField] private PaddedTextView scoreTextView;
        [SerializeField] private PaddedTextView fruitCountHealthView;
        private ICounterView _fruitCountHealthView;

        private IScoreService _scoreService;
        private ICounterView _scoreTextView;
        private void Awake()
        {
            _scoreTextView = scoreTextView;
            _fruitCountHealthView = fruitCountHealthView;
        }
        private void Start()
        {
            if (scoreTextView)
            {
                _scoreTextView.UpdateCountDisplay(_scoreService.CurrentScore);
            }

            if (fruitCountHealthView)
            {
                _fruitCountHealthView.UpdateCountDisplay(_scoreService.FruitCollectedCount);
            }
        }

        private void OnEnable()
        {
            ScoreCollectable.OnScoreCollected += HandleScoreCollected;
        }

        private void OnDisable()
        {
            ScoreCollectable.OnScoreCollected -= HandleScoreCollected;
        }

        #region VContainer Injection

        [Inject]
        public void Construct(IScoreService scoreService)
        {
            _scoreService = scoreService;
        }

        #endregion

        private void HandleScoreCollected(int scoreAmount, Vector3 position)
        {
            _scoreService.AddScore(scoreAmount);
            _scoreService.AddFruitCollected(position);
            if (scoreTextView)
            {
                _scoreTextView.UpdateCountDisplay(_scoreService.CurrentScore);
            }

            if (fruitCountHealthView)
            {
                _fruitCountHealthView.UpdateCountDisplay(_scoreService.FruitCollectedCount);
            }
        }
    }
}

using Collectables.Counter;
using Core.Events;
using UnityEngine;
using VContainer;

namespace Collectables.Score
{
    public class ScoreController : MonoBehaviour
    {
        [SerializeField] private PaddedTextView scoreTextView;
        [SerializeField] private PaddedTextView fruitCountHealthView;
        private ICounterView _fruitCountHealthView;

        private IScoreService _scoreService;
        private IEventBus _eventBus;
        private ICounterView _scoreTextView;

        private void Awake()
        {
            _scoreTextView = scoreTextView;
            _fruitCountHealthView = fruitCountHealthView;
        }

        private void Start()
        {
            if (scoreTextView)
            {
                _scoreTextView.UpdateCountDisplay(_scoreService.CurrentScore);
            }

            if (fruitCountHealthView)
            {
                _fruitCountHealthView.UpdateCountDisplay(_scoreService.FruitCollectedCount);
            }
        }

        private void OnEnable()
        {
            ScoreCollectable.OnScoreCollected += HandleScoreCollected;
            _eventBus?.Subscribe<ScoreChangedEvent>(OnScoreChanged);
        }

        private void OnDisable()
        {
            ScoreCollectable.OnScoreCollected -= HandleScoreCollected;
            _eventBus?.Unsubscribe<ScoreChangedEvent>(OnScoreChanged);
        }

        private void OnScoreChanged(ScoreChangedEvent scoreEvent)
        {
            _scoreTextView?.UpdateCountDisplay(scoreEvent.NewScore);
        }

        #region VContainer Injection

        [Inject]
        public void Construct(IScoreService scoreService, IEventBus eventBus)
        {
            _scoreService = scoreService;
            _eventBus = eventBus;
        }

        #endregion

        private void HandleScoreCollected(int scoreAmount)
        {
            _scoreService.AddScore(scoreAmount);
        }
    }
}

using Core;
using Player.Interfaces;
using UnityEngine;
using VContainer;

namespace Collectables.Score
{
    public class ScoreService : IScoreService
    {
        private const int OneUpThreshold = 30;
        private GameDataCoordinator _gameDataCoordinator;
        private IPlayerLivesService _livesService;

        public int CurrentScore => _gameDataCoordinator?.GetCurrentScore() ?? 0;

        public void AddScore(int amount)
        {
            int newScore = CurrentScore + amount;
            _gameDataCoordinator?.UpdateScore(newScore);
        }

        public void ResetScore()
        {
            _gameDataCoordinator?.UpdateScore(0);
        }

        public void AddFruitCollected(Vector3 collectPosition)
        {
            _gameDataCoordinator?.AddFruitCollected();
            int fruitCount = FruitCollectedCount;
            if (fruitCount > 0 && fruitCount % OneUpThreshold == 0)
            {
                _livesService?.AddLife(collectPosition);
                Debug.Log("One-up awarded! Player gained an extra life.");
            }
        }

        public int FruitCollectedCount => _gameDataCoordinator?.GetFruitCollectedCount() ?? 0;

        [Inject]
        public void Construct(GameDataCoordinator gameDataCoordinator, IPlayerLivesService livesService)
        {
            _gameDataCoordinator = gameDataCoordinator;
            _livesService = livesService;
        }
    }
}

using Core;
using Core.Events;
using Player.Interfaces;
using UnityEngine;
using VContainer;

namespace Collectables.Score
{
    public class ScoreService : IScoreService
    {
        private const int OneUpThreshold = 30;
        private GameDataCoordinator _gameDataCoordinator;
        private IPlayerLivesService _livesService;
        private IEventBus _eventBus;

        public int CurrentScore => _gameDataCoordinator?.GetCurrentScore() ?? 0;

        public void AddScore(int amount)
        {
            int previousScore = CurrentScore;
            int newScore = previousScore + amount;
            _gameDataCoordinator?.UpdateScore(newScore);

            // Publish score changed event for UI updates
            _eventBus?.Publish(new ScoreChangedEvent
            {
                Timestamp = Time.time,
                NewScore = newScore,
                ScoreChange = amount
            });
        }

        public void ResetScore()
        {
            _gameDataCoordinator?.UpdateScore(0);
        }

        public void AddFruitCollected(Vector3 collectPosition)
        {
            _gameDataCoordinator?.AddFruitCollected();
            int fruitCount = FruitCollectedCount;
            if (fruitCount > 0 && fruitCount % OneUpThreshold == 0)
            {
                _livesService?.AddLife(collectPosition);
                Debug.Log("One-up awarded! Player gained an extra life.");
            }
        }

        public int FruitCollectedCount => _gameDataCoordinator?.GetFruitCollectedCount() ?? 0;

        [Inject]
        public void Construct(GameDataCoordinator gameDataCoordinator, IPlayerLivesService livesService, IEventBus eventBus)
        {
            _gameDataCoordinator = gameDataCoordinator;
            _livesService = livesService;
            _eventBus = eventBus;
        }
    }
}

using Collectables.Score;
using Core.Data;
using Core.Events;
using Core.Services;
using LevelSelection;
using LevelSelection.Services;
using Player.Components;
using Player.Interfaces;
using Player.Services;
using Player.UI;
using Pooling;
using UnityEngine;
using VContainer;
using VContainer.Unity;
using Weapons.Models;
using Weapons.Services;

namespace Core.DI
{
    public class GameLifetimeScope : LifetimeScope
    {
        protected override void Awake()
        {
            Debug.Log("[GameLifetimeScope] Awake called.");
            AddToAutoInject<FireballWeapon>();
            base.Awake();
        }

        private void AddToAutoInject<T>() where T : Component
        {

            var components = FindObjectsByType<T>(FindObjectsInactive.Include, FindObjectsSortMode.None);

            foreach (T comp in components)
            {
                autoInjectGameObjects.Add(comp.gameObject);
            }
        }

        protected override void Configure(IContainerBuilder builder)
        {
            Debug.Log("[GameLifetimeScope] Configuring DI container...");

            // Register Core Services
            builder.Register<IEventBus, EventBus>(Lifetime.Singleton);

            // Register EventBus as IEventPublisher as well (since EventBus implements IEventPublisher)
            builder.Register<IEventPublisher>(resolver => resolver.Resolve<IEventBus>(), Lifetime.Singleton);

            builder.Register<IGameDataRepository, JsonGameDataRepository>(Lifetime.Singleton);
            builder.Register<IGameDataService, GameDataService>(Lifetime.Singleton);
            builder.Register<IAutoSaveService, AutoSaveService>(Lifetime.Singleton);
            builder.Register<IScoreService, ScoreService>(Lifetime.Singleton);
            builder.Register<ILevelDiscoveryService, LevelDiscoveryService>(Lifetime.Singleton);
            builder.RegisterComponentInHierarchy<SceneLoadService>().As<ISceneLoadService>();


            // Register Player Services
            builder.Register<IPlayerLivesService>(resolver
                => new PlayerLivesService(
                    resolver.Resolve<GameDataCoordinator>()
                ), Lifetime.Singleton);


            // Game Management
            builder.RegisterComponentInHierarchy<GameFlowManager>();
            builder.RegisterComponentInHierarchy<GameDataCoordinator>();
            builder.RegisterComponentInHierarchy<EndLevelZone>();
            builder.RegisterComponentInHierarchy<HealthBonusService>();
            // Pooling System
            builder.RegisterComponentInHierarchy<PoolManager>().As<IPoolService>();

            // Weapons
            builder.RegisterComponentInHierarchy<AxeWeapon>();
            builder.RegisterComponentInHierarchy<WeaponManagerService>();

            // Health
            builder.RegisterComponentInHierarchy<PlayerHealthController>();
            builder.RegisterComponentInHierarchy<PlayerLivesUIController>();
            builder.RegisterComponentInHierarchy<PlayerAnimationController>();

            // Score System
            builder.RegisterComponentInHierarchy<ScoreController>();
            builder.RegisterComponentInHierarchy<PopupTextService>();


            Debug.Log("[GameLifetimeScope] DI container configured successfully.");
        }
    }
}

