namespace Core.DI
{
    public class GameLifetimeScope : LifetimeScope
    {
        protected override void Awake()
        {
            Debug.Log("[GameLifetimeScope] Awake called.");
            AddToAutoInject<FireballWeapon>();
            base.Awake();
        }

        private void AddToAutoInject<T>() where T : Component
        {

            var components = FindObjectsByType<T>(FindObjectsInactive.Include, FindObjectsSortMode.None);

            foreach (T comp in components)
            {
                autoInjectGameObjects.Add(comp.gameObject);
            }
        }

        protected override void Configure(IContainerBuilder builder)
        {
            Debug.Log("[GameLifetimeScope] Configuring DI container...");

            // Register Core Services
            builder.Register<IEventBus, EventBus>(Lifetime.Singleton);

            // Register EventBus as IEventPublisher as well (since EventBus implements IEventPublisher)
            builder.Register<IEventPublisher>(resolver => resolver.Resolve<IEventBus>(), Lifetime.Singleton);

            builder.Register<IGameDataRepository, JsonGameDataRepository>(Lifetime.Singleton);
            builder.Register<IGameDataService, GameDataService>(Lifetime.Singleton);
            builder.Register<IAutoSaveService, AutoSaveService>(Lifetime.Singleton);
            builder.Register<IScoreService, ScoreService>(Lifetime.Singleton);
            builder.Register<ILevelDiscoveryService, LevelDiscoveryService>(Lifetime.Singleton);
            builder.RegisterComponentInHierarchy<SceneLoadService>().As<ISceneLoadService>();


            // Register Player Services
            builder.Register<IPlayerLivesService>(resolver
                => new PlayerLivesService(
                    resolver.Resolve<GameDataCoordinator>()
                ), Lifetime.Singleton);


            // Game Management
            builder.RegisterComponentInHierarchy<GameFlowManager>();
            builder.RegisterComponentInHierarchy<GameDataCoordinator>();
            builder.RegisterComponentInHierarchy<EndLevelZone>();
            builder.RegisterComponentInHierarchy<HealthBonusService>();
            // Pooling System
            builder.RegisterComponentInHierarchy<PoolManager>().As<IPoolService>();

            // Weapons
            builder.RegisterComponentInHierarchy<AxeWeapon>();
            builder.RegisterComponentInHierarchy<WeaponManagerService>();

            // Health
            builder.RegisterComponentInHierarchy<PlayerHealthController>();
            builder.RegisterComponentInHierarchy<PlayerLivesUIController>();
            builder.RegisterComponentInHierarchy<PlayerAnimationController>();
            builder.RegisterComponentInHierarchy<PeriodicBypassDamageDealer>();

            // Score System
            builder.RegisterComponentInHierarchy<ScoreController>();
            builder.RegisterComponentInHierarchy<PopupTextService>();


            Debug.Log("[GameLifetimeScope] DI container configured successfully.");
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using LevelSelection;
using LevelSelection.Services;
using UnityEngine;
using VContainer;

namespace Core.Data
{
    public class GameDataService : IGameDataService
    {
        private readonly IGameDataRepository _repository;

        [Inject]
        public GameDataService(IGameDataRepository repository)
        {
            _repository = repository;
            CurrentData = _repository.LoadData();
        }

        public GameData CurrentData { get; private set; }

        public event Action<GameData> OnDataChanged;

        public void UpdateLives(int lives)
        {
            CurrentData.lives = lives;
            NotifyDataChanged();
        }

        public void UpdateScore(int score)
        {
            CurrentData.score = score;
            NotifyDataChanged();
        }


        public void UpdateCurrentLevel(string levelName)
        {
            CurrentData.currentLevel = levelName;
            NotifyDataChanged();
        }

        public void UpdateBestTime(string levelName, float time)
        {
            var gameData = CurrentData;
            if (gameData == null) return;

            // Update overall best time
            if (time < gameData.bestTime)
            {
                gameData.bestTime = time;
            }

            // Update per-level best time
            if (!gameData.LevelBestTimes.ContainsKey(levelName) || time < gameData.LevelBestTimes[levelName])
            {
                gameData.LevelBestTimes[levelName] = time;
            }

            NotifyDataChanged();
        }

        public void ResetAllData()
        {
            _repository.DeleteData();
            CurrentData = _repository.LoadData();
            NotifyDataChanged();
        }

        public void SaveData()
        {
            _repository.SaveData(CurrentData);
        }

        public void AddFruitCollected()
        {
            CurrentData.fruitCollected++;
            NotifyDataChanged();
        }

        public void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime)
        {
            var gameData = CurrentData;
            if (gameData == null) return;

            // Update completed levels list
            if (isCompleted && !gameData.completedLevels.Contains(levelName))
            {
                gameData.completedLevels.Add(levelName);
            }

            // Update best time
            if (!gameData.LevelBestTimes.ContainsKey(levelName) || completionTime < gameData.LevelBestTimes[levelName])
            {
                gameData.LevelBestTimes[levelName] = completionTime;
            }

            NotifyDataChanged();
        }

        public void UnlockLevel(string levelName)
        {
            if (CurrentData != null && !string.IsNullOrEmpty(levelName) && !CurrentData.unlockedLevels.Contains(levelName))
            {
                CurrentData.unlockedLevels.Add(levelName);
                Debug.Log($"[GameDataService] Level '{levelName}' unlocked and added to unlocked levels list");
                NotifyDataChanged();
                SaveData(); 
            }
        }

        public async Task<List<LevelData>> GetLevelDataAsync(ILevelDiscoveryService discoveryService)
        {
            if (CurrentData.levelDataCacheValid && CurrentData.cachedLevelData.Any())
            {
                // Always re-apply default unlock status in case settings changed
                ApplyDefaultUnlockStatus(CurrentData.cachedLevelData);
                return CurrentData.cachedLevelData;
            }

            var discoveredLevels = await discoveryService.DiscoverLevelsFromSceneAsync();
            CacheLevelData(discoveredLevels);
            return discoveredLevels;
        }

        private void CacheLevelData(List<LevelData> levelData)
        {
            CurrentData.cachedLevelData = new List<LevelData>(levelData.OrderBy(l => l.levelIndex));
            CurrentData.levelDataCacheValid = true;

            // Apply default unlock status from level data
            ApplyDefaultUnlockStatus(levelData);

            SaveData();
        }

        /// <summary>
        /// Apply default unlock status from discovered level data
        /// </summary>
        private void ApplyDefaultUnlockStatus(List<LevelData> levelData)
        {
            foreach (LevelData level in levelData.Where(level =>
                         level.UnlockedByDefault && !CurrentData.unlockedLevels.Contains(level.levelName)))
            {
                CurrentData.unlockedLevels.Add(level.levelName);
                Debug.Log($"[GameDataService] Auto-unlocked level: {level.levelName} (UnlockedByDefault = true)");
            }
        }

        private void NotifyDataChanged()
        {
            OnDataChanged?.Invoke(CurrentData);
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using LevelSelection;
using LevelSelection.Services;
using UnityEngine;
using VContainer;

namespace Core.Data
{
    public class GameDataService : IGameDataService
    {
        private readonly IGameDataRepository _repository;

        [Inject]
        public GameDataService(IGameDataRepository repository)
        {
            _repository = repository;
            CurrentData = _repository.LoadData();
        }

        public GameData CurrentData { get; private set; }

        public event Action<GameData> OnDataChanged;

        public void UpdateLives(int lives)
        {
            CurrentData.lives = lives;
            NotifyDataChanged();
        }

        public void UpdateScore(int score)
        {
            CurrentData.score = score;
            NotifyDataChanged();
        }


        public void UpdateCurrentLevel(string levelName)
        {
            CurrentData.currentLevel = levelName;
            NotifyDataChanged();
        }

        public void UpdateBestTime(string levelName, float time)
        {
            var gameData = CurrentData;
            if (gameData == null) return;

            // Update overall best time
            if (time < gameData.bestTime)
            {
                gameData.bestTime = time;
            }

            // Update per-level best time
            if (!gameData.LevelBestTimes.ContainsKey(levelName) || time < gameData.LevelBestTimes[levelName])
            {
                gameData.LevelBestTimes[levelName] = time;
            }

            NotifyDataChanged();
        }

        public void ResetAllData()
        {
            _repository.DeleteData();
            CurrentData = _repository.LoadData();
            NotifyDataChanged();
        }

        public void ResetProgressData()
        {
            // Reset only player progress data, preserve score and level progress
            CurrentData.lives = GameData.MaxLives;
            CurrentData.currentLevel = "";
            CurrentData.selectedLevelIndex = 0;
            CurrentData.fruitCollected = 0;
            // Note: Score, maxScore, unlockedLevels, completedLevels, and best times are preserved
            NotifyDataChanged();
        }

        public void SaveData()
        {
            _repository.SaveData(CurrentData);
        }

        public void AddFruitCollected()
        {
            CurrentData.fruitCollected++;
            NotifyDataChanged();
        }

        public void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime)
        {
            var gameData = CurrentData;
            if (gameData == null) return;

            // Update completed levels list
            if (isCompleted && !gameData.completedLevels.Contains(levelName))
            {
                gameData.completedLevels.Add(levelName);
            }

            // Update best time
            if (!gameData.LevelBestTimes.ContainsKey(levelName) || completionTime < gameData.LevelBestTimes[levelName])
            {
                gameData.LevelBestTimes[levelName] = completionTime;
            }

            NotifyDataChanged();
        }

        public void UnlockLevel(string levelName)
        {
            if (CurrentData != null && !string.IsNullOrEmpty(levelName) && !CurrentData.unlockedLevels.Contains(levelName))
            {
                CurrentData.unlockedLevels.Add(levelName);
                Debug.Log($"[GameDataService] Level '{levelName}' unlocked and added to unlocked levels list");
                NotifyDataChanged();
                SaveData(); 
            }
        }

        public async Task<List<LevelData>> GetLevelDataAsync(ILevelDiscoveryService discoveryService)
        {
            if (CurrentData.levelDataCacheValid && CurrentData.cachedLevelData.Any())
            {
                // Always re-apply default unlock status in case settings changed
                ApplyDefaultUnlockStatus(CurrentData.cachedLevelData);
                return CurrentData.cachedLevelData;
            }

            var discoveredLevels = await discoveryService.DiscoverLevelsFromSceneAsync();
            CacheLevelData(discoveredLevels);
            return discoveredLevels;
        }

        private void CacheLevelData(List<LevelData> levelData)
        {
            CurrentData.cachedLevelData = new List<LevelData>(levelData.OrderBy(l => l.levelIndex));
            CurrentData.levelDataCacheValid = true;

            // Apply default unlock status from level data
            ApplyDefaultUnlockStatus(levelData);

            SaveData();
        }

        /// <summary>
        /// Apply default unlock status from discovered level data
        /// </summary>
        private void ApplyDefaultUnlockStatus(List<LevelData> levelData)
        {
            foreach (LevelData level in levelData.Where(level =>
                         level.UnlockedByDefault && !CurrentData.unlockedLevels.Contains(level.levelName)))
            {
                CurrentData.unlockedLevels.Add(level.levelName);
                Debug.Log($"[GameDataService] Auto-unlocked level: {level.levelName} (UnlockedByDefault = true)");
            }
        }

        private void NotifyDataChanged()
        {
            OnDataChanged?.Invoke(CurrentData);
        }
    }
}

using System;
using System.Collections.Generic;
using System.Threading.Tasks;
using LevelSelection;
using LevelSelection.Services;

namespace Core.Data
{
    public interface IGameDataService
    {
        GameData CurrentData { get; }
        void UpdateLives(int lives);
        void UpdateScore(int score);
        void AddFruitCollected();
        void UpdateBestTime(string levelName, float time);
        void UpdateCurrentLevel(string levelName);
        void UnlockLevel(string levelName);
        void ResetAllData();
        void SaveData();
        event Action<GameData> OnDataChanged;

        // Level data operations that should go through GameDataService
        void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime);
        Task<List<LevelData>> GetLevelDataAsync(ILevelDiscoveryService discoveryService);
    }
}

using System;
using System.Collections.Generic;
using System.Threading.Tasks;
using LevelSelection;
using LevelSelection.Services;

namespace Core.Data
{
    public interface IGameDataService
    {
        GameData CurrentData { get; }
        void UpdateLives(int lives);
        void UpdateScore(int score);
        void AddFruitCollected();
        void UpdateBestTime(string levelName, float time);
        void UpdateCurrentLevel(string levelName);
        void UnlockLevel(string levelName);
        void ResetAllData();
        void SaveData();
        void ResetProgressData(); // New method for selective reset
        event Action<GameData> OnDataChanged;
        
        // Level data operations that should go through GameDataService
        void UpdateLevelProgress(string levelName, bool isCompleted, float completionTime);
        Task<List<LevelData>> GetLevelDataAsync(ILevelDiscoveryService discoveryService);
    }
}

using UnityEngine;

namespace Core.Events
{
    public enum GameState
    {
        MainMenu,
        LevelSelection, 
        Loading,
        Playing,
        Paused,
        GameOver,
        Victory,
        Restarting
    }

    public struct GameStateChangedEvent
    {
        public GameState PreviousState;
        public GameState NewState;
        public float Timestamp;
    }

    public struct PlayerLivesChangedEvent
    {
        public int PreviousLives;
        public int CurrentLives;
        public int MaxLives;
        public float Timestamp;
    }

    // Base event structure for consistency
    public interface IGameEvent
    {
        float Timestamp { get; }
    }

    public struct PlayerDeathEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public Vector3 DeathPosition;
    }

    public struct LevelStartedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
    }

    public struct LevelCompletedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
        public float CompletionTime;
        public int RemainingHp;
    }

    public struct GameOverEvent : IGameEvent
    {
        public float Timestamp { get; set; }
    }

    public struct GameCompletedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string FinalLevelName;
    }

    // Level Selection Events
    public struct LevelSelectedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
        public int LevelIndex;
    }

    public struct LevelNavigationEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public int PreviousIndex;
        public int NewIndex;
        public Vector2 Direction;
    }

    public struct ItemSelectScreenRequestedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
    }

    public struct LevelLoadRequestedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string LevelName;
        public string SceneName;
    }

    public struct LevelUnlockedEvent : IGameEvent
    {
        public float Timestamp { get; set; }
        public string CompletedLevelName;
        public string UnlockedLevelName;
    }
}

using UnityEngine;

