using Health.Interfaces;
using UnityEngine;

namespace Enemy
{
    public class EnemyDeath : MonoBehaviour
    {
        private IHealthEvents _simpleHealthController;
        private void Start()
        {
            _simpleHealthController = GetComponent<IHealthEvents>();
            _simpleHealthController.OnLivesEmpty += HandleDeath;
            _simpleHealthController.OnLivesEmpty -= HandleDeath;
            _simpleHealthController.OnLivesEmpty -= HandleDeath;
            _simpleHealthController.OnLivesEmpty -= HandleDeath;
            _simpleHealthController.OnLivesEmpty -= HandleDeath;

using Health.Interfaces;
using UnityEngine;

namespace Enemy
{
    public class EnemyDeath : MonoBehaviour
    {
        private IHealthEvents _healthEvents;
        private void Start()
        {
            _healthEvents = GetComponent<IHealthEvents>();
            if (_healthEvents != null)
                _healthEvents.OnDeath += HandleDeath;
        }
        private void OnDisable()
        {
            if (_healthEvents != null)
                _healthEvents.OnDeath -= HandleDeath;
        }
        private void HandleDeath()
        {
            gameObject.SetActive(false);
        }
    }
}

using System;
using Health.Interfaces;
using UnityEngine;

namespace Health.Core
{
    [DisallowMultipleComponent]
    public class HealthComponent : MonoBehaviour, IHealth
    {
        [SerializeField] private int maxHp = 3;
        private bool _isDead;

        protected void Awake()
        {
            CurrentHp = maxHp;
        }
        public int MaxHp => maxHp;
        public int CurrentHp { get; private set; }
        public event Action<int, int> OnHealthChanged;
        public event Action OnDeath;

        public void Damage(int amount, GameObject source = null)
        {
            if (_isDead) return;
            CurrentHp = Mathf.Max(0, CurrentHp - amount);
            OnHealthChanged?.Invoke(CurrentHp, MaxHp);
            if (CurrentHp == 0)
            {
                _isDead = true;
                OnDeath?.Invoke();
            }
        }

        public void Heal(int amount)
        {
            if (_isDead) return;
            CurrentHp = Mathf.Min(MaxHp, CurrentHp + amount);
            OnHealthChanged?.Invoke(CurrentHp, MaxHp);
        }
    }
}

using System;
using Health.Interfaces;
using UnityEngine;

namespace Health.Core
{
    [DisallowMultipleComponent]
    public class HealthComponent : MonoBehaviour, IHealth
    {
        [SerializeField] private int maxHp = 3;
        private bool _isDead;

        protected void Awake()
        {
            CurrentHp = maxHp;
        }
        public int MaxHp => maxHp;
        public int CurrentHp { get; private set; }
        public event Action<int, int> OnHealthChanged;
        public event Action OnDeath;

        public virtual void Damage(int amount, GameObject source = null)
        {
            if (_isDead) return;
            CurrentHp = Mathf.Max(0, CurrentHp - amount);
            OnHealthChanged?.Invoke(CurrentHp, MaxHp);
            if (CurrentHp == 0)
            {
                _isDead = true;
                OnDeath?.Invoke();
            }
        }

        public void Heal(int amount)
        {
            if (_isDead) return;
            CurrentHp = Mathf.Min(MaxHp, CurrentHp + amount);
            OnHealthChanged?.Invoke(CurrentHp, MaxHp);
        }
    }
}

