using System.Collections.Generic;
using Enemies.Interfaces;
using UnityEngine;

namespace Enemies.Core
{
    // Handles all attack behaviors for an enemy
    public class EnemyAttackController : MonoBehaviour
    {
        private List<TriggerGroup> _triggerGroups;

        private void Awake()
        {
            _triggerGroups = new List<TriggerGroup>();

            // Dynamically assign triggers and commands
            var triggers = GetComponents<ITrigger>();
            foreach (var trigger in triggers)
            {
                var commands = new List<IAttackCommand>(GetComponents<IAttackCommand>());
                var triggerGroup = new TriggerGroup(new[] { trigger }, TriggerCondition.Any, () => ExecuteCommands(commands));
                _triggerGroups.Add(triggerGroup);
            }
        }

        private void Update()
        {
            foreach (TriggerGroup triggerGroup in _triggerGroups)
            {
                triggerGroup.Update();
            }
        }

        private void ExecuteCommands(IEnumerable<IAttackCommand> commands)
        {
            foreach (var command in commands)
            {
                command.Execute();
            }
        }
    }
}

