namespace Enemies.Core
{
    // Generic controller for handling triggers and commands
    public class GenericCommandController<TCommand> : MonoBehaviour where TCommand : ICommand
    {
        private List<TriggerGroup> _triggerGroups;

        private void Awake()
        {
            _triggerGroups = new List<TriggerGroup>();

            // Dynamically assign triggers and commands
            var triggers = GetComponents<ITrigger>();
            foreach (var trigger in triggers)
            {
                var commands = new List<TCommand>(GetComponents<TCommand>());
                var triggerGroup = new TriggerGroup(new[] { trigger }, TriggerCondition.Any, () => ExecuteCommands(commands));
                _triggerGroups.Add(triggerGroup);
            }
        }

        private void Update()
        {
            foreach (TriggerGroup triggerGroup in _triggerGroups)
            {
                triggerGroup.Update();
            }
        }

        private void ExecuteCommands(IEnumerable<TCommand> commands)
        {
            foreach (var command in commands)
            {
                command.Execute();
            }
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using Enemies.Interfaces;

namespace Enemies.Core
{
    public enum TriggerCondition
    {
        Any,
        All
    }

    public class TriggerGroup
    {
        private readonly List<ITrigger> _triggers;
        private readonly TriggerCondition _condition;
        private readonly Action _onEvaluate;

        public TriggerGroup(IEnumerable<ITrigger> triggers, TriggerCondition condition, Action onEvaluate)
        {
            _triggers = triggers.ToList();
            _condition = condition;
            _onEvaluate = onEvaluate;
        }


        public void Evaluate()
        {
            bool isActivated = _condition switch
            {
                TriggerCondition.Any => _triggers.Any(t => t.IsTriggered),
                TriggerCondition.All => _triggers.All(t => t.IsTriggered),
                _ => false
            };

            if (isActivated)
            {
                _onEvaluate?.Invoke();
            }
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using Enemies.Interfaces;

namespace Enemies.Core
{
    public enum TriggerCondition
    {
        Any,
        All
    }

    public class TriggerGroup
    {
        private readonly List<ITrigger> _triggers;
        private readonly TriggerCondition _condition;
        private readonly Action _onEvaluate;

        public TriggerGroup(IEnumerable<ITrigger> triggers, TriggerCondition condition, Action onEvaluate)
        {
            _triggers = triggers.ToList();
            _condition = condition;
            _onEvaluate = onEvaluate;
        }


        public void Update()
        {
            foreach (var trigger in _triggers)
            {
                trigger.CheckTrigger();
            }
            Evaluate();
        }

        public void Evaluate()
        {
            bool isActivated = _condition switch
            {
                TriggerCondition.Any => _triggers.Any(t => t.IsTriggered),
                TriggerCondition.All => _triggers.All(t => t.IsTriggered),
                _ => false
            };

            if (isActivated)
            {
                _onEvaluate?.Invoke();
            }
        }
    }
}

using Enemies.Interfaces;
using UnityEngine;
using System.Linq;

namespace Enemies.Core
{
    // Manages periodic checks for all triggers in the scene
    public class TriggerManager : MonoBehaviour
    {
        private TriggerGroup[] _triggerGroups;

        private void Awake()
        {
            _triggerGroups = FindObjectsOfType<MonoBehaviour>().OfType<TriggerGroup>().ToArray();
        }

        private void Update()
        {
            foreach (var group in _triggerGroups)
            {
                group.Update();
            }
        }
    }
}

