using UnityEngine;
using Enemy.Behaviors;

namespace Enemy
{
    // Bird: Moves left, goes up and down
    public class BirdEnemy : EnemyBase, IMovable
    {
        [SerializeField] private float moveSpeed = 2f;
        [SerializeField] private float verticalAmplitude = 1f;
        [SerializeField] private float verticalFrequency = 1f;
        private Vector3 _startPos;
        private float _time;

        protected override void Awake()
        {
            base.Awake();
            _startPos = transform.position;
        }

        public void Move()
        {
            _time += Time.deltaTime;
            float yOffset = Mathf.Sin(_time * verticalFrequency) * verticalAmplitude;
            transform.position = _startPos + new Vector3(-moveSpeed * _time, yOffset, 0);
        }
    }
}

